                  "description": "Array containing the errors (optional)",
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "field": {
                        "type": "string",
                        "description": "Property",
                        "example": "amount"
                      },
                      "message": {
                        "type": "string",
                        "description": "What happened",
                        "example": "O valor enviado Ã© maior do que o valor total da transaÃ§Ã£o original."
