{
  "openapi": "3.0.0",
  "servers": [
    {
      "description": "Light Config Server",
      "url": "https://config.neworknt.com/config-server"
    }
  ],
  "info": {
    "description": "Light Config Server API",
    "version": "1.0.0",
    "title": "Light Config Server API",
    "contact": {
      "email": "santosh.aherkar@gmail.com"
    }
  },
  "tags": [
    {
      "name": "config-server",
      "description": "Operations available in config server"
    }
  ],
  "paths": {
    "/configs/property/ref": {
      "get": {
        "tags": [
          "config-server"
        ],
        "summary": "Get reference data to populate the dropdown for property",
        "operationId": "getPropertyRef",
        "description": "Get property reference data with query parameters",
        "parameters": [
          {
            "in": "query",
            "name": "name",
            "description": "The name of the reference data",
            "required": true,
            "schema": {
              "type": "string",
              "enum": [
                "host",
                "cap",
                "project",
                "projver",
                "scope",
                "key"
              ]
            }
          },
          {
            "in": "query",
            "name": "host",
            "description": "The selected host as parameter for cap and scope",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "cap",
            "description": "The selected cap as parameter for project, projver and scope",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "project",
            "description": "The selected project as parameter for projver and scope",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "projver",
            "description": "The selected project version as parameter for scope",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "List of key/value map",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/Reference"
                  }
                }
              }
            }
          },
          "400": {
            "description": "Bad Request"
          }
        }
      }
    },
    "/configs/property/search": {
      "post": {
        "tags": [
          "config-server"
        ],
        "summary": "Search property key and value pairs with cap and optional scope.",
        "operationId": "postPropertySearch",
        "description": "Search property key and value pairs based on cap and scope.",
        "requestBody": {
          "description": "Search Criteria",
          "content": {
            "application/json": {
