          {
            "in": "query",
            "name": "host",
            "description": "The host of property",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "cap",
            "description": "The cap of property",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "project",
            "description": "The project of property",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "projver",
            "description": "The project version of property",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "scope",
            "description": "The scope of the property",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "query",
            "name": "key",
            "description": "The key of the property",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "204": {
            "description": "Successful Delete Configs Property"
          },
          "400": {
            "description": "Bad request"
          }
        },
        "security": [
          {
            "portal_auth": [
              "portal.w"
            ]
          }
        ]
      }
    },
