        "/api/rnb/pvt/import/calculatorConfiguration": {
            "post": {
                "tags": [
                    "Promotions and Taxes"
                ],
                "summary": "Create Multiple SKU Promotion",
                "description": "Creates a Multiple SKU Promotion. This scenario allows to create a single promotion for multiples SKUs with the Percentage Effect.",
                "parameters": [
                    {
                        "name": "Content-Type",
                        "in": "header",
                        "description": "Type of the content being sent.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "example": "text/csv"
                        }
                    },
                    {
                        "name": "Accept",
                        "in": "header",
                        "description": "HTTP Client Negotiation Accept Header. Indicates the types of responses the client can understand.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    },
                    {
                        "name": "X-VTEX-calculator-name",
                        "in": "header",
                        "description": "Promotion Name.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "example": "Test"
                        }
                    },
                    {
                        "name": "X-VTEX-cumulative",
                        "in": "header",
                        "description": "If the Promotion is cumulative with other promotions.",
                        "required": false,
                        "style": "simple",
                        "schema": {
                            "type": "boolean",
                            "example": false
                        }
                    },
                    {
                        "name": "X-VTEX-cluster-operator",
                        "in": "header",
                        "description": "This header allows implementing the Promotion in multiples client clusters. You can set the value as `all` - the Promotion will be valid to all the clusters - or `any` - the Promotion will be valid to any of the clusters.",
                        "required": false,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "example": "any"
                        }
                    },
                    {
                        "name": "X-VTEX-cluster-expression",
                        "in": "header",
                        "description": "Cluster that will be included in the Promotion. To add multiple clusters, create a header for each one of them.",
                        "required": false,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "example": "cluster_name=true"
                        }
                    },
                    {
                        "name": "X-VTEX-start-date",
                        "in": "header",
                        "description": "Promotion start date.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "example": "2020-08-18T16:00:00+3:00"
                        }
                    },
                    {
                        "name": "X-VTEX-end-date",
                        "in": "header",
                        "description": "Promotion end date.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "example": "2020-08-18T16:30:00+3:00"
                        }
                    },
                    {
                        "name": "X-VTEX-accumulate-with-manual-prices",
                        "in": "header",
                        "description": "Condition that will accumulate the Promotion with manual prices or not.",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "boolean",
                            "example": false
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    }
                },
                "requestBody": {
                    "content": {
                        "text/csv": {
                            "schema": {
                                "type": "string",
                                "format": "binary",
                                "description": "The file must contain the SKU ID and the percentage of the discount. The first line must have the headers `sku` and `effect`. The next lines must be the SKU ID and the percentage, without any spaces. For decimal values, use `.` as in `10.5`. This should be sent as a raw text on the request body.",
                                "example": "sku,effect\r\n1,10.5"
                            }
                        }
                    }
                }
            }
        },
