{
  "openapi": "3.0.1",
  "info": {
    "title": "IRN API v1",
    "description": "Allows users to extract, create, update and configure IRN data.",
    "version": "1"
  },
  "servers": [
    {
      "url": "https://api-sandbox.factset.com/research/irn"
    }
  ],
  "paths": {
    "/v1/events": {
      "get": {
        "tags": [
          "Events"
        ],
        "summary": "Get all the record events in the specified date range filtered on the given types",
        "operationId": "GetAllEvents",
        "parameters": [
          {
            "name": "startDate",
            "in": "query",
            "description": "StartDate",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "endDate",
            "in": "query",
            "description": "EndDate",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "types",
            "in": "query",
            "description": "Set of event types to filter on",
            "schema": {
              "type": "array",
              "items": {
                "type": "string"
              }
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success - Returns events list",
            "content": {
              "text/plain": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/EventSnippetDto"
                  }
                }
              },
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/EventSnippetDto"
                  }
                }
              },
              "text/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/EventSnippetDto"
                  }
                }
              }
            }
          },
          "400": {
            "description": "Bad Request - Returns error message"
          }
        }
      }
    },
    "/v1/identifiers": {
      "get": {
        "tags": [
          "Identifiers"
        ],
        "summary": "Get all the identifier details for given identifiers",
        "operationId": "GetIdentifiers",
        "parameters": [
          {
            "name": "identifiers",
            "in": "query",
            "description": "Identifiers",
            "schema": {
              "type": "string"
