        "operationId": "getOrganizationApiRequests",
        "parameters": [
          {
            "name": "organizationId",
            "in": "path",
            "description": "",
            "required": true,
            "style": "simple",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "t0",
            "in": "query",
            "description": "The beginning of the timespan for the data. The maximum lookback period is 31 days from today.",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "t1",
            "in": "query",
            "description": "The end of the timespan for the data. t1 can be a maximum of 31 days after t0.",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "timespan",
            "in": "query",
            "description": "The timespan for which the information will be fetched. If specifying timespan, do not specify parameters t0 and t1. The value must be in seconds and be less than or equal to 31 days. The default is 31 days.",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "number",
              "format": "double"
            }
          },
          {
            "name": "perPage",
            "in": "query",
            "description": "The number of entries per page returned. Acceptable range is 3 - 1000. Default is 50.",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "integer",
              "format": "int32"
            }
          },
          {
            "name": "startingAfter",
            "in": "query",
            "description": "A token used by the server to indicate the start of the page. Often this is a timestamp or an ID but it is not limited to those. This parameter should not be defined by client applications. The link for the first, last, prev, or next page in the HTTP Link header should define it.",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "endingBefore",
            "in": "query",
            "description": "A token used by the server to indicate the end of the page. Often this is a timestamp or an ID but it is not limited to those. This parameter should not be defined by client applications. The link for the first, last, prev, or next page in the HTTP Link header should define it.",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "adminId",
            "in": "query",
            "description": "Filter the results by the ID of the admin who made the API requests",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "path",
            "in": "query",
            "description": "Filter the results by the path of the API requests",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "method",
            "in": "query",
            "description": "Filter the results by the method of the API requests (must be 'GET', 'PUT', 'POST' or 'DELETE')",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "responseCode",
            "in": "query",
            "description": "Filter the results by the response code of the API requests",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "integer",
              "format": "int32"
            }
          },
          {
            "name": "sourceIp",
            "in": "query",
            "description": "Filter the results by the IP address of the originating API request",
            "style": "form",
            "explode": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful operation",
            "headers": {},
            "content": {
              "text/plain": {
                "schema": {
                  "type": "object",
                  "description": "Successful operation",
                  "example": "[\r\n  {\r\n    \"adminId\": \"212406\",\r\n    \"method\": \"GET\",\r\n    \"host\": \"api.meraki.com\",\r\n    \"path\": \"/api/v0/organizations/33349/apiRequests\",\r\n    \"queryString\": \"timespan=604800\",\r\n    \"userAgent\": \"PostmanRuntime/7.6.0\",\r\n    \"ts\": \"2019-02-20T17:31:23Z\",\r\n    \"responseCode\": 200,\r\n    \"sourceIp\": \"123.123.123.1\"\r\n  }\r\n]"
                },
                "example": "[\r\n  {\r\n    \"adminId\": \"212406\",\r\n    \"method\": \"GET\",\r\n    \"host\": \"api.meraki.com\",\r\n    \"path\": \"/api/v0/organizations/33349/apiRequests\",\r\n    \"queryString\": \"timespan=604800\",\r\n    \"userAgent\": \"PostmanRuntime/7.6.0\",\r\n    \"ts\": \"2019-02-20T17:31:23Z\",\r\n    \"responseCode\": 200,\r\n    \"sourceIp\": \"123.123.123.1\"\r\n  }\r\n]"
