        "title": "Played Games",
        "required": [
          "playedGameId",
          "gameDefinitionId",
          "gameDefinitionName",
          "gamingGroupId",
          "gamingGroupName",
          "datePlayed",
          "dateLastUpdated",
          "playerGameResults"
        ],
        "type": "object",
        "properties": {
          "playedGameId": {
            "type": "number",
            "description": "NemeStats unique identifier for the Played Game."
          },
          "gameDefinitionId": {
            "type": "number",
            "description": "NemeStats unique identifier for the Game Definition of the Played Game."
          },
          "gameDefinitionName": {
            "type": "string",
            "description": "Name of the Game Definition."
          },
          "gamingGroupId": {
            "type": "number",
            "description": "NemeStats unique identifier of the Gaming Group against which this Played Game was recorded."
          },
          "gamingGroupName": {
            "type": "string",
            "description": "Name of the Gaming Group."
          },
          "datePlayed": {
            "type": "string",
            "description": "The date that the Played Game was recorded in yyyy-MM-dd format (e.g. 2015-05-013)."
          },
          "dateLastUpdated": {
            "type": "string",
            "description": "The date that the Played Game was last updated in yyyy-MM-dd format (e.g. 2015-05-013). This will usually match the datePlayed unless the Played Game was updated at a later date."
          },
          "playerGameResults": {
            "type": "object",
            "description": "Represents all of the data related to a particular Player's performance in a particular Played Game.\n+ playerId (number, required) - NemeStats unique identifier for the Player.\n+ playerName (string, required) - The name of the Player.\n+ playerActive (bool, required) - Boolean value indicating whether the player is currently Active.\n+ gameRank (number, required) - The corresponding integer rank of the Player in this Played Game. A rank of 1 means the Player got first place, 2 means second place, and so on.\n+ pointsScored (number, optional) - Decimal number of points the Player scored in the Played Game.\n+ nemeStatsPointsAwarded (number, required) - The number of NemeStats Points awarded to the Player for their respective rank in the Played Game.\n+ gameDurationBonusNemePoints  (number, required) - The number of bonus NemeStats Points awarded due to the BoardGameGeek average Play Time of this Game Definition.\n+ gameWeightBonusNemePoints (number, required) - The number of bonus NemeStats Points awarded due to the BoardGameGeek Weight of this Game Definition.\n+ totalNemeStatsPointsAwarded (number, required) - The total number of NemeStats Points awarded including all bonus points."
          },
          "boardGameGeekObjectId": {
            "type": "number",
            "description": "Board Game Geek unique identifier for the Game Definition."
          },
          "notes": {
            "type": "string",
            "description": "User-entered notes about the game."
          },
          "applicationLinkages": {
            "type": "object",
            "description": "Represents a unique identifier for an external application that also stores a representation of this Played Game. For example, if a Played Game is also in BoardGameGeek, the client could set an applicationLinkage with the applicationName = \"BGG\" and the entityId = \"1399193\".\n+ applicationName (string, required) - Unique identifier for an external application which also holds a representation of this play. Use \"BGG\" for BoardGameGeek and \"BGStats\" for Board Game Stats. Other applications can use different application names, so long as they are unique.\n+ entityId (string, required) - Unique identifier within the external application. For example, BoardGameGeek has an \"id\" value for each play which could be passed in this field."
          }
        }
