            "in": "query",
            "name": "_paginationOffset",
            "description": "Non-negative number of entries to skip. Default is 0",
            "schema": {
              "type": "integer"
            },
            "examples": {
              "startIndex": {
                "value": 0,
                "description": "Non-negative number of entries to skip"
              }
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Successful operation",
            "content": {
              "application/json": {
