                        "stored_at": {
                          "type": "string",
                          "format": "date-time",
                          "description": "Timestamp when the report has been written into database.",
                          "example": "2020-01-23T16:15:59.478901889Z"
                        }
                      }
                    },
                    "status": {
                      "type": "string",
                      "example": "ok"
                    }
                  }
                }
              }
            }
          }
        },
        "tags": [
          "prod"
        ]
