{
  "openapi": "3.0.2",
  "info": {
    "title": "OGCAPISIMPLE",
    "contact": {
      "name": "Tobias Pressler",
      "email": "pressler.tobias@gmail.com"
    },
    "version": "1.0",
    "description": "Simple Feature based OGC Api"
  },
  "externalDocs": {
    "description": "OGC API specification",
    "url": "https://github.com/opengeospatial/WFS_FES"
  },
  "servers": [{
    "description": "This server"
  }],
  "tags": [{
      "name": "Capabilities",
      "description": "essential characteristics of this API"
    },
    {
      "name": "Data",
      "description": "access to data (features)"
    }
  ],
  "paths": {
    "/": {
      "get": {
        "tags": [
          "Capabilities"
        ],
        "summary": "landing page",
        "description": "The landing page provides links to the API definition, the conformance\nstatements and to the feature collections in this dataset.",
        "operationId": "getLandingPage",
        "responses": {
          "200": {
            "description": "The landing page provides links to the API definition\nthe Conformance declaration (path `/conformance`,\nlink relation `conformance`), and the Feature\nCollections (path `/collections`, link relation\n`data`).",
            "content": {
              "application/json": {
                "schema": {
                  "required": [
                    "links"
                  ],
                  "type": "object",
                  "properties": {
                    "title": {
                      "type": "string",
                      "example": "Airspaces in Austria"
                    },
                    "description": {
                      "type": "string",
                      "example": "Access to data about the airspaces in Austria via a Web API that conforms to the OGC API Features specification."
                    },
                    "links": {
                      "type": "array",
                      "items": {
                        "required": [
                          "href"
                        ],
                        "type": "object",
                        "properties": {
                          "href": {
                            "type": "string",
                            "example": "http://data.example.com/airspace/123"
                          },
                          "rel": {
                            "type": "string",
                            "example": "alternate"
                          },
                          "type": {
                            "type": "string",
                            "example": "application/json"
                          }
                        }
                      }
                    }
                  }
                },
                "example": {
                  "links": [{
                      "href": "http://data.example.org/",
                      "rel": "self",
                      "type": "application/json"
                    },
                    {
                      "href": "http://data.example.org/api",
                      "rel": "service-desc",
                      "type": "application/vnd.oai.openapi+json;version=3.0"
                    },
                    {
                      "href": "http://data.example.org/api.html",
                      "rel": "service-doc",
                      "type": "text/html"
                    },
                    {
                      "href": "http://data.example.org/conformance",
                      "rel": "conformance",
                      "type": "application/json"
                    },
                    {
                      "href": "http://data.example.org/collections",
                      "rel": "data",
                      "type": "application/json"
                    }
                  ]
                }
              },
              "text/html": {
                "schema": {
                  "type": "string"
                }
              }
            }
          },
          "500": {
            "description": "A server error occurred.",
            "content": {
              "application/json": {
                "schema": {
                  "required": [
                    "code"
                  ],
                  "type": "object",
                  "properties": {
                    "code": {
                      "type": "string"
                    },
                    "description": {
                      "type": "string"
                    }
                  },
                  "description": "Information about the exception: an error code plus an optional description."
                }
              },
              "text/html": {
                "schema": {
                  "type": "string"
                }
              }
            }
          }
        }
      }
    },
    "/conformance": {
      "get": {
        "tags": [
          "Capabilities"
        ],
        "summary": "information about specifications that this API conforms to",
        "description": "A list of all conformance classes specified in a standard that the\nserver conforms to.",
        "operationId": "getConformanceDeclaration",
        "responses": {
          "200": {
            "description": "The URIs of all conformance classes supported by the server.\n\nTo support \"generic\" clients that want to access multiple\nOGC API Features implementations - and not \"just\" a specific\nAPI / server, the server declares the conformance\nclasses it implements and conforms to.",
            "content": {
              "application/json": {
                "schema": {
                  "required": [
                    "conformsTo"
                  ],
                  "type": "object",
                  "properties": {
                    "conformsTo": {
                      "type": "array",
                      "items": {
                        "type": "string"
                      }
                    }
                  }
                },
                "example": {
                  "conformsTo": [
                    "http://www.opengis.net/spec/ogcapi-features-1/1.0/conf/core",
                    "http://www.opengis.net/spec/ogcapi-features-1/1.0/conf/oas30",
                    "http://www.opengis.net/spec/ogcapi-features-1/1.0/conf/geojson",
                    "http://www.opengis.net/spec/ogcapi-features-1/1.0/conf/html"
                  ]
                }
              }
            }
          },
          "500": {
            "description": "A server error occurred.",
            "content": {
              "application/json": {
                "schema": {
                  "required": [
                    "code"
                  ],
                  "type": "object",
                  "properties": {
                    "code": {
                      "type": "string"
                    },
                    "description": {
                      "type": "string"
                    }
                  },
                  "description": "Information about the exception: an error code plus an optional description."
                }
              }
            }
          }
        }
      }
    },
    "/collections": {
      "get": {
        "tags": [
          "Capabilities"
        ],
        "summary": "the feature collections in the dataset",
        "operationId": "getCollections",
        "parameters": [{
          "name": "f",
          "in": "query",
          "example": "text/html",
          "description": "Differentiate between an application/json or text/html response.",
          "schema": {
            "type": "string"
          }
        }],
        "responses": {
          "200": {
            "description": "The feature collections shared by this API.\n\nThe dataset is organized as one or more feature collections. This resource\nprovides information about and access to the collections.\n\nThe response contains the list of collections. For each collection, a link\nto the items in the collection (path `/collections/{collectionId}/items`,\nlink relation `items`) as well as key information about the collection.\nThis information includes:\n\n* A local identifier for the collection that is unique for the dataset;\n* A list of coordinate reference systems (CRS) in which geometries may be returned by the server. The first CRS is the default coordinate reference system (the default is always WGS 84 with axis order longitude/latitude);\n\n* An optional extent that can be used to provide an indication of the spatial and temporal extent of the collection - typically derived from the data;",
            "content": {
              "application/json": {
                "schema": {
                  "required": [
                    "collections",
                    "links"
                  ],
                  "type": "object",
                  "properties": {
                    "links": {
                      "type": "array",
                      "items": {
                        "required": [
                          "href"
                        ],
                        "type": "object",
                        "properties": {
                          "href": {
                            "type": "string",
                            "example": "http://data.example.com/airspaces/123"
                          },
                          "rel": {
                            "type": "string",
                            "example": "alternate"
                          },
                          "type": {
                            "type": "string"
                          }
                        }
                      }
                    },
                    "collections": {
                      "type": "array",
                      "items": {
                        "required": [
                          "id",
                          "links"
                        ],
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string",
                            "description": "identifier of the collection used, for example, in URIs",
                            "example": "address"
                          },
                          "links": {
                            "type": "array",
                            "example": [{
                              "href": "http://data.example.com/airspaces",
                              "rel": "item"
                            }],
                            "items": {
                              "required": [
                                "href"
                              ],
                              "type": "object",
                              "properties": {
                                "href": {
                                  "type": "string",
                                  "example": "http://data.example.com/airspaces/123"
                                },
                                "rel": {
                                  "type": "string",
                                  "example": "alternate"
                                },
                                "type": {
                                  "type": "string",
                                  "example": "application/geo+json"
                                }
                              }
                            }
                          },
                          "extent": {
                            "type": "object",
                            "properties": {
                              "spatial": {
                                "type": "object",
                                "properties": {
                                  "bbox": {
                                    "minItems": 1,
                                    "type": "array",
                                    "description": "One or more bounding boxes that describe the spatial extent of the dataset.\nIn the Core only a single bounding box is supported. Extensions may support\nadditional areas. If multiple areas are provided, the union of the bounding\nboxes describes the spatial extent.",
                                    "items": {
                                      "maxItems": 6,
                                      "minItems": 4,
                                      "type": "array",
                                      "description": "Each bounding box is provided as four or six numbers, depending on\nwhether the coordinate reference system includes a vertical axis\n(height or depth):\n\n* Lower left corner, coordinate axis 1\n* Lower left corner, coordinate axis 2\n* Minimum value, coordinate axis 3 (optional)\n* Upper right corner, coordinate axis 1\n* Upper right corner, coordinate axis 2\n* Maximum value, coordinate axis 3 (optional)\n\nThe coordinate reference system of the values is WGS 84 longitude/latitude\n(http://www.opengis.net/def/crs/OGC/1.3/CRS84) unless a different coordinate\nreference system is specified in `crs`.\n\nFor WGS 84 longitude/latitude the values are in most cases the sequence of\nminimum longitude, minimum latitude, maximum longitude and maximum latitude.\nHowever, in cases where the box spans the antimeridian the first value\n(west-most box edge) is larger than the third value (east-most box edge).\n\nIf the vertical axis is included, the third and the sixth number are\nthe bottom and the top of the 3-dimensional bounding box.\n\nIf a feature has multiple spatial geometry properties, it is the decision of the\nserver whether only a single spatial geometry property is used to determine\nthe extent or all relevant geometries.",
                                      "example": [
                                        -180,
                                        -90,
                                        180,
                                        90
                                      ],
                                      "items": {
                                        "type": "number"
                                      }
                                    }
                                  }
                                }
                              }
                            }
                          }
                        }
                      }
                    }
                  }
                },
                "example": {
                  "links": [{
                      "href": "http://data.example.org/collections.json",
                      "rel": "self",
                      "type": "application/json",
                      "title": "this document"
                    },
                    {
                      "href": "http://data.example.org/collections.html",
                      "rel": "alternate",
                      "type": "text/html",
                      "title": "this document as HTML"
                    },
                    {
                      "href": "http://schemas.example.org/1.0/buildings.xsd",
                      "rel": "describedBy",
                      "type": "application/xml",
                      "title": "GML application schema for Acme Corporation building data"
                    },
                    {
                      "href": "http://download.example.org/buildings.gpkg",
                      "rel": "enclosure",
                      "type": "application/geopackage+sqlite3",
                      "title": "Bulk download (GeoPackage)",
                      "length": 472546
                    }
                  ],
                  "collections": [{
                    "id": "buildings",
                    "title": "Buildings",
                    "description": "Buildings in the city of Bonn.",
                    "extent": {
                      "spatial": {
                        "bbox": [
                          [
                            7.01,
                            50.63,
                            7.22,
                            50.78
                          ]
                        ]
                      }
                    },
                    "links": [{
                        "href": "http://data.example.org/collections/airspaces/items",
                        "rel": "items",
                        "type": "application/json"
                      },
                      {
                        "href": "http://data.example.org/collections/buildings/items.html",
                        "rel": "items",
                        "type": "text/html"
                      }
                    ]
                  }]
                }
              },
              "text/html": {
                "schema": {
                  "type": "string"
                }
              }
            }
          },
          "500": {
            "description": "A server error occurred.",
            "content": {
              "application/json": {
                "schema": {
                  "required": [
                    "code"
                  ],
                  "type": "object",
                  "properties": {
                    "code": {
                      "type": "string"
                    },
                    "description": {
                      "type": "string"
                    }
                  },
                  "description": "Information about the exception: an error code plus an optional description."
                }
              },
              "text/html": {
                "schema": {
                  "type": "string"
                }
              }
            }
          }
        }
      }
    },
    "/collections/{collectionId}": {
      "get": {
        "tags": [
          "Capabilities"
        ],
        "summary": "describe the feature collection with id `collectionId`",
        "operationId": "describeCollection",
        "parameters": [{
          "name": "collectionId",
          "in": "path",
          "description": "local identifier of a collection",
          "required": true,
          "schema": {
            "type": "string"
          }
        }],
        "responses": {
          "200": {
            "description": "Information about the feature collection with id `collectionId`.\n\nThe response contains a link to the items in the collection\n(path `/collections/{collectionId}/items`, link relation `items`)\nas well as key information about the collection. This information\nincludes:\n\n* A local identifier for the collection that is unique for the dataset;\n* A list of coordinate reference systems (CRS) in which geometries may be returned by the server. The first CRS is the default coordinate reference system (the default is always WGS 84 with axis order longitude/latitude);\n\n* An optional extent that can be used to provide an indication of the spatial extent of the collection - typically derived from the data;",
            "content": {
              "application/json": {
                "schema": {
                  "required": [
                    "id",
                    "links"
                  ],
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string",
                      "description": "identifier of the collection used, for example, in URIs",
                      "example": "address"
                    },
                    "links": {
                      "type": "array",
                      "example": [{
                        "href": "http://data.example.com/buildings",
                        "rel": "item"
                      }],
                      "items": {
                        "required": [
                          "href"
                        ],
                        "type": "object",
                        "properties": {
                          "href": {
                            "type": "string",
                            "example": "http://data.example.com/airspaces/123"
                          },
                          "rel": {
                            "type": "string",
                            "example": "alternate"
                          },
                          "type": {
                            "type": "string",
                            "example": "application/json"
                          }
                        }
                      }
                    },
                    "extent": {
                      "type": "object",
                      "properties": {
                        "spatial": {
                          "type": "object",
                          "properties": {
                            "bbox": {
                              "minItems": 1,
                              "type": "array",
                              "description": "One or more bounding boxes that describe the spatial extent of the dataset.\nIn the Core only a single bounding box is supported. Extensions may support\nadditional areas. If multiple areas are provided, the union of the bounding\nboxes describes the spatial extent.",
                              "items": {
                                "maxItems": 4,
                                "minItems": 4,
                                "type": "array",
                                "description": "Each bounding box is provided as four or six numbers, depending on\nwhether the coordinate reference system includes a vertical axis\n(height or depth):\n\n* Lower left corner, coordinate axis 1\n* Lower left corner, coordinate axis 2\n* Minimum value, coordinate axis 3 (optional)\n* Upper right corner, coordinate axis 1\n* Upper right corner, coordinate axis 2\n* Maximum value, coordinate axis 3 (optional)\n\nThe coordinate reference system of the values is WGS 84 longitude/latitude\n(http://www.opengis.net/def/crs/OGC/1.3/CRS84) unless a different coordinate\nreference system is specified in `crs`.\n\nFor WGS 84 longitude/latitude the values are in most cases the sequence of\nminimum longitude, minimum latitude, maximum longitude and maximum latitude.\nHowever, in cases where the box spans the antimeridian the first value\n(west-most box edge) is larger than the third value (east-most box edge).\n\nIf the vertical axis is included, the third and the sixth number are\nthe bottom and the top of the 3-dimensional bounding box.\n\nIf a feature has multiple spatial geometry properties, it is the decision of the\nserver whether only a single spatial geometry property is used to determine\nthe extent or all relevant geometries.",
                                "example": [
                                  -180,
                                  -90,
                                  180,
                                  90
                                ],
                                "items": {
                                  "type": "number"
                                }
                              }
                            },
                            "crs": {
                              "type": "string",
                              "description": "Coordinate reference system of the coordinates in the spatial extent\n(property `bbox`). The default reference system is WGS 84 longitude/latitude.\nIn the Core this is the only supported coordinate reference system.\nExtensions may support additional coordinate reference systems and add\nadditional enum values.",
                              "enum": [
                                "http://www.opengis.net/def/crs/OGC/1.3/CRS84"
                              ],
                              "default": "http://www.opengis.net/def/crs/OGC/1.3/CRS84"
                            }
                          },
                          "description": "The spatial extent of the features in the collection."
                        }
                      },
                      "description": "The extent of the features in the collection. In the Core only spatial and temporal\nextents are specified. Extensions may add additional members to represent other\nextents, for example, thermal or pressure ranges."
                    }
                  }
                },
                "example": {
                  "id": "airspaces",
                  "extent": {
                    "spatial": {
                      "bbox": [
                        [
                          7.01,
                          50.63,
                          7.22,
                          50.78
                        ]
                      ]
                    }
                  },
                  "links": [{
                    "href": "http://data.example.org/collections/airspaces/items",
                    "rel": "items",
                    "type": "application/json"
                  }]
                }
              }
            }
          },
          "404": {
            "description": "The requested URI was not found."
          },
          "500": {
            "description": "A server error occurred.",
            "content": {
              "application/json": {
                "schema": {
                  "required": [
                    "code"
                  ],
                  "type": "object",
                  "properties": {
                    "code": {
                      "type": "string"
                    },
                    "description": {
                      "type": "string"
                    }
                  },
                  "description": "Information about the exception: an error code plus an optional description."
                }
              }
            }
          }
        }
      }
    },
    "/collections/{collectionId}/items": {
      "get": {
        "tags": [
          "Data"
        ],
        "summary": "fetch features",
        "description": "Fetch features of the feature collection with id `collectionId`.\n\nEvery feature in a dataset belongs to a collection. A dataset may\nconsist of multiple feature collections. A feature collection is often a\ncollection of features of a similar type, based on a common schema.\n\nUse content negotiation to request HTML or GeoJSON.",
        "operationId": "getFeatures",
        "parameters": [{
            "name": "collectionId",
            "in": "path",
            "description": "local identifier of a collection",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "limit",
            "in": "query",
            "description": "The optional limit parameter limits the number of items that are presented in the response document.\n\nOnly items are counted that are on the first level of the collection in the response document.\nNested objects contained within the explicitly requested items shall not be counted.\n\nMinimum = 1. Maximum = 10000. Default = 10.",
            "required": false,
            "style": "form",
            "explode": false,
            "schema": {
              "maximum": 1000000,
              "minimum": 1,
              "type": "integer",
              "default": 10
            }
          },
          {
            "name": "bbox",
            "in": "query",
            "description": "Only features that have a geometry that intersects the bounding box are selected.\nThe bounding box is provided as four or six numbers, depending on whether the\ncoordinate reference system includes a vertical axis (height or depth):\n\n* Lower left corner, coordinate axis 1\n* Lower left corner, coordinate axis 2\n\n* Upper right corner, coordinate axis 1\n* Upper right corner, coordinate axis 2\n\n\nThe coordinate reference system of the values is WGS 84 longitude/latitude\n(http://www.opengis.net/def/crs/OGC/1.3/CRS84) unless a different coordinate\nreference system is specified in the parameter `bbox-crs`.\n\nFor WGS 84 longitude/latitude the values are in most cases the sequence of\nminimum longitude, minimum latitude, maximum longitude and maximum latitude.\nHowever, in cases where the box spans the antimeridian the first value\n(west-most box edge) is larger than the third value (east-most box edge).\n\nIf the vertical axis is included, the third and the sixth number are\nthe bottom and the top of the 3-dimensional bounding box.\n\nIf a feature has multiple spatial geometry properties, it is the decision of the\nserver whether only a single spatial geometry property is used to determine\nthe extent or all relevant geometries.",
            "required": false,
            "style": "form",
            "explode": false,
            "schema": {
              "maxItems": 4,
              "minItems": 4,
              "type": "array",
              "items": {
                "type": "number"
              }
            }
          },
          {
            "name": "filter",
            "in": "query",
            "description": "Defines a filter that will be applied on items, only items matching the filter will be returned. For wildcard searches use *.",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "The response is a document consisting of features in the collection.\nThe features included in the response are determined by the server\nbased on the query parameters of the request. To support access to\nlarger collections without overloading the client, the API supports\npaged access with links to the next page, if more features are selected\nthat the page size.\n\nThe `bbox` parameter can be used to select only a\nsubset of the features in the collection (the features that are in the\nbounding box ). The `bbox` parameter matches all features\nin the collection that are not associated with a location, too.\n\nThe `limit` parameter may be used to control the subset of the\nselected features that should be returned in the response, the page size.",
            "content": {
              "application/json": {
                "schema": {
                  "required": [
                    "features",
                    "type"
                  ],
                  "type": "object",
                  "properties": {
                    "type": {
                      "type": "string",
                      "enum": [
                        "FeatureCollection"
                      ]
                    },
                    "features": {
                      "type": "array",
                      "items": {
                        "required": [
                          "geometry",
                          "properties",
                          "type"
                        ],
                        "type": "object",
                        "properties": {
                          "type": {
                            "type": "string",
                            "enum": [
                              "Feature"
                            ]
                          },
                          "geometry": {
                            "oneOf": [{
                                "required": [
                                  "coordinates",
                                  "type"
                                ],
                                "type": "object",
                                "properties": {
                                  "type": {
                                    "type": "string",
                                    "enum": [
                                      "Point"
                                    ]
                                  },
                                  "coordinates": {
                                    "minItems": 2,
                                    "type": "array",
                                    "items": {
                                      "type": "number"
                                    }
                                  }
                                }
                              },
                              {
                                "required": [
                                  "coordinates",
                                  "type"
                                ],
                                "type": "object",
                                "properties": {
                                  "type": {
                                    "type": "string",
                                    "enum": [
                                      "MultiPoint"
                                    ]
                                  },
                                  "coordinates": {
                                    "type": "array",
                                    "items": {
                                      "minItems": 2,
                                      "type": "array",
                                      "items": {
                                        "type": "number"
                                      }
                                    }
                                  }
                                }
                              },
                              {
                                "required": [
                                  "coordinates",
                                  "type"
                                ],
                                "type": "object",
                                "properties": {
                                  "type": {
                                    "type": "string",
                                    "enum": [
                                      "LineString"
                                    ]
                                  },
                                  "coordinates": {
                                    "minItems": 2,
                                    "type": "array",
                                    "items": {
                                      "minItems": 2,
                                      "type": "array",
                                      "items": {
                                        "type": "number"
                                      }
                                    }
                                  }
                                }
                              },
                              {
                                "required": [
                                  "coordinates",
                                  "type"
                                ],
                                "type": "object",
                                "properties": {
                                  "type": {
                                    "type": "string",
                                    "enum": [
                                      "MultiLineString"
                                    ]
                                  },
                                  "coordinates": {
                                    "type": "array",
                                    "items": {
                                      "minItems": 2,
                                      "type": "array",
                                      "items": {
                                        "minItems": 2,
                                        "type": "array",
                                        "items": {
                                          "type": "number"
                                        }
                                      }
                                    }
                                  }
                                }
                              },
                              {
                                "required": [
                                  "coordinates",
                                  "type"
                                ],
                                "type": "object",
                                "properties": {
                                  "type": {
                                    "type": "string",
                                    "enum": [
                                      "Polygon"
                                    ]
                                  },
