                        }
                      },
                      "paymentMethods": {
                        "data": [
                          {
                            "type": "paymentMethod",
                            "id": "6e30ccd1-0f76-4208-bd3d-a804f810cba8"
                          }
                        ]
                      }
                    }
                  },
                  "included": [
                    {
