                          },
                          {
                            "weekdays": [
                              "Sonntag"
                            ],
                            "openFromTo": [
                              "07:00",
                              "22:00"
                            ]
                          }
                        ],
                        "amenities": [
                          "restaurant",
                          "wifi",
                          "toilets"
                        ]
                      },
                      "relationships": {
                        "fuelPrices": {
                          "data": [
                            {
                              "type": "fuelPrice",
                              "id": "6b5bff6f-74e1-40a1-9da0-72305292d939"
