          "401": {
            "$ref": "#/components/responses/Unauthorized"
          },
          "404": {
            "$ref": "#/components/responses/NotFound"
          },
          "406": {
            "$ref": "#/components/responses/NotAcceptable"
          },
          "415": {
            "$ref": "#/components/responses/UnsupportedMediaType"
          },
          "500": {
            "$ref": "#/components/responses/InternalServerError"
          }
        }
      }
    },
    "/gas-stations/{gasStationId}/pumps/{pumpId}": {
      "get": {
        "tags": [
          "Fueling"
        ],
        "operationId": "GetPump",
        "summary": "Return current pump information",
        "description": "Returns the current pump status (free, inUse, readyToPay, outOfOrder) and identifier. If the status is readyToPay, the result also contains fuelType, productName, fuelAmount, VAT (amount & rate), priceWithoutVAT, priceIncludingVAT, currency.\n",
        "parameters": [
          {
            "in": "path",
            "name": "gasStationId",
            "schema": {
              "type": "string",
              "format": "uuid"
            },
            "required": true,
            "description": "Gas station ID",
            "example": "583ad807-6a08-4d5a-b3f6-8861b0e355df"
          },
          {
            "in": "path",
            "name": "pumpId",
            "schema": {
              "type": "string",
              "format": "uuid"
            },
            "required": true,
            "description": "Pump ID",
            "example": "a4373543-4083-4f0c-a4a9-4bc458b598fd"
          }
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/vnd.api+json": {
                "schema": {
                  "$ref": "#/components/schemas/PumpResponse"
                }
              }
            }
          },
          "401": {
            "$ref": "#/components/responses/Unauthorized"
          },
          "404": {
            "$ref": "#/components/responses/NotFound"
          },
          "406": {
            "$ref": "#/components/responses/NotAcceptable"
          },
          "500": {
            "$ref": "#/components/responses/InternalServerError"
          }
        }
      }
    },
    "/gas-stations/{gasStationId}/pumps/{pumpId}/wait-for-status-change": {
      "get": {
        "tags": [
          "Fueling"
        ],
        "operationId": "WaitOnPumpStatusChange",
        "summary": "Wait for a status change on a given pump",
        "description": "Uses **long polling** to wait for a status change on a given pump. Returns as soon as the status has changed or after the number of seconds provided by the optional `timeout` query parameter (default timeout is 30 seconds). In case of timeout (408 status code) you're safe to start the request again. Instantaneously returns if `lastStatus` was given and already changed between request. If successful, it returns the same structure as the normal status call\n",
        "parameters": [
          {
            "in": "path",
            "name": "gasStationId",
            "schema": {
              "type": "string",
              "format": "uuid"
            },
            "required": true,
            "description": "Gas station ID",
            "example": "a6ec9bd7-cf0b-416c-b24f-9ce65ab3dfe1"
          },
          {
            "in": "path",
            "name": "pumpId",
            "schema": {
              "type": "string",
              "format": "uuid"
            },
            "required": true,
            "description": "Pump ID",
            "example": "4665fddc-83fe-46bd-85ae-110d71e78357"
          },
          {
            "in": "query",
            "name": "update",
            "schema": {
              "type": "string",
              "enum": [
                "longPolling"
              ]
            },
            "required": true,
            "description": "Use **long polling** for status updates"
          },
          {
            "in": "query",
            "name": "lastStatus",
            "schema": {
              "$ref": "#/components/schemas/PumpStatus"
            },
            "required": false,
            "description": "Pump status",
            "example": "outOfOrder"
          },
          {
            "in": "query",
            "name": "timeout",
            "schema": {
              "type": "integer"
            },
            "required": false,
            "description": "Timeout in seconds",
            "example": 20
          }
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/vnd.api+json": {
                "schema": {
                  "$ref": "#/components/schemas/PumpResponse"
                }
              }
            }
          },
          "400": {
            "$ref": "#/components/responses/BadRequest"
          },
          "401": {
            "$ref": "#/components/responses/Unauthorized"
          },
          "404": {
            "$ref": "#/components/responses/NotFound"
          },
          "406": {
            "$ref": "#/components/responses/NotAcceptable"
          },
          "408": {
            "$ref": "#/components/responses/RequestTimeout"
          },
          "500": {
            "$ref": "#/components/responses/InternalServerError"
          }
        }
      }
    },
    "/gas-station/{gasStationId}/payment": {
      "post": {
        "tags": [
          "Fueling"
        ],
        "operationId": "ProcessPayment",
        "summary": "Process payment",
        "description": "Process payment and notify user if transaction is finished successfully. You can optionally provide `priceIncludingVAT`and `currency` in the request body to check if the price the user has seen is still correct.\n",
        "parameters": [
          {
            "in": "path",
            "name": "gasStationId",
            "schema": {
              "type": "string",
              "format": "uuid"
            },
            "required": true,
            "description": "Gas station ID",
            "example": "a6ec9bd7-cf0b-416c-b24f-9ce65ab3dfe1"
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/vnd.api+json": {
              "schema": {
                "$ref": "#/components/schemas/TransactionRequest"
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Created",
            "content": {
              "application/vnd.api+json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "data": {
                      "type": "object",
                      "properties": {
                        "type": {
                          "type": "string",
                          "enum": [
                            "transaction"
                          ]
                        },
                        "id": {
                          "type": "string",
                          "format": "uuid",
                          "description": "Transaction ID",
                          "example": "c3f037ea-492e-4033-9b4b-4efc7beca16c"
                        },
                        "attributes": {
                          "type": "object",
                          "properties": {
                            "paymentToken": {
                              "type": "string",
                              "example": "f106ac99-213c-4cf7-8c1b-1e841516026b"
                            },
                            "gasStationId": {
                              "type": "string",
                              "format": "uuid",
                              "example": "a6ec9bd7-cf0b-416c-b24f-9ce65ab3dfe1"
                            },
                            "fuelingAppId": {
                              "type": "string",
                              "format": "uuid",
                              "example": "c30bce97-b732-4390-af38-1ac6b017aa4c"
                            },
                            "pumpId": {
                              "type": "string",
                              "format": "uuid",
                              "example": "460ffaad-a3c1-4199-b69e-63949ccda82f"
                            },
                            "vin": {
                              "type": "string",
                              "example": "1B3EL46R36N102271"
                            },
                            "mileage": {
                              "type": "integer",
                              "example": 66435
                            },
                            "VAT": {
                              "type": "object",
                              "properties": {
                                "amount": {
                                  "type": "number",
                                  "format": "float",
                                  "example": 11.07
                                },
                                "rate": {
                                  "type": "number",
                                  "format": "float",
                                  "example": 0.19
                                }
                              }
                            },
                            "priceWithoutVAT": {
                              "type": "number",
                              "format": "float",
                              "example": 58.27
                            },
                            "priceIncludingVAT": {
                              "type": "number",
                              "format": "float",
                              "example": 69.34
                            },
                            "currency": {
                              "$ref": "#/components/schemas/currency"
                            }
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          },
          "400": {
            "$ref": "#/components/responses/BadRequest"
          },
          "401": {
            "$ref": "#/components/responses/Unauthorized"
          },
          "404": {
            "$ref": "#/components/responses/NotFound"
          },
          "406": {
            "$ref": "#/components/responses/NotAcceptable"
          },
          "409": {
            "$ref": "#/components/responses/Conflict"
          },
          "415": {
            "$ref": "#/components/responses/UnsupportedMediaType"
          },
          "422": {
            "$ref": "#/components/responses/UnprocessableEntity"
          },
          "500": {
            "$ref": "#/components/responses/InternalServerError"
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "ApproachingRequest": {
        "type": "object",
        "properties": {
          "data": {
            "type": "object",
            "required": [
              "type",
              "attributes"
            ],
            "properties": {
              "type": {
                "type": "string",
                "enum": [
                  "approaching"
                ]
              },
              "id": {
                "type": "string",
                "format": "uuid",
                "description": "Approaching ID",
                "example": "c3f037ea-492e-4033-9b4b-4efc7beca16c"
              },
              "attributes": {
                "type": "object",
                "required": [
                  "expectedAmount",
                  "carFuelType"
                ],
                "properties": {
                  "expectedAmount": {
                    "type": "number",
                    "format": "float",
                    "description": "Expected amount in liters for refuel",
                    "example": 47.8
                  },
                  "carFuelType": {
                    "type": "string",
                    "enum": [
                      "e85",
                      "ron91",
                      "ron95_e5",
                      "ron95_e10",
                      "ron98",
                      "ron98_e5",
                      "ron100",
                      "diesel",
                      "diesel_gtl",
                      "diesel_b7",
                      "lpg",
                      "cng",
                      "h2",
                      "Truck Diesel",
                      "AdBlue"
                    ],
                    "description": "Fuel type of the car",
                    "example": "ron95_e10"
                  }
                }
              }
            }
          }
        }
      },
      "TransactionRequest": {
        "type": "object",
        "properties": {
          "data": {
            "type": "object",
            "required": [
              "type",
              "attributes"
            ],
            "properties": {
              "type": {
                "type": "string",
                "enum": [
                  "transaction"
                ]
              },
              "id": {
                "type": "string",
                "format": "uuid",
                "description": "Transaction ID",
                "example": "c3f037ea-492e-4033-9b4b-4efc7beca16c"
              },
              "attributes": {
                "type": "object",
                "required": [
                  "paymentToken",
                  "fuelingAppId",
                  "pumpId"
                ],
                "properties": {
                  "paymentToken": {
                    "type": "string",
                    "example": "f106ac99-213c-4cf7-8c1b-1e841516026b"
                  },
                  "fuelingAppId": {
                    "type": "string",
                    "format": "uuid",
                    "description": "Location-based App ID",
                    "example": "c30bce97-b732-4390-af38-1ac6b017aa4c"
                  },
                  "pumpId": {
                    "type": "string",
                    "format": "uuid",
                    "description": "Pump ID",
                    "example": "460ffaad-a3c1-4199-b69e-63949ccda82f"
                  },
                  "vin": {
                    "type": "string",
                    "example": "1B3EL46R36N102271"
                  },
                  "mileage": {
                    "type": "integer",
                    "description": "Current mileage in meters",
                    "example": 66435
                  },
                  "priceIncludingVAT": {
                    "type": "number",
                    "format": "float",
                    "example": 69.34
                  },
                  "currency": {
                    "$ref": "#/components/schemas/currency"
                  }
                }
              }
            }
          }
        }
      },
      "PumpStatus": {
        "type": "string",
        "description": "Current pump status",
        "enum": [
          "free",
          "inUse",
          "readyToPay",
          "outOfOrder"
        ]
      },
      "Pump": {
        "type": "object",
        "properties": {
          "type": {
            "type": "string",
            "description": "Type",
            "enum": [
              "pump"
            ]
          },
          "id": {
            "type": "string",
            "format": "uuid",
            "description": "Pump ID",
            "example": "f106ac99-213c-4cf7-8c1b-1e841516026b"
          },
          "attributes": {
            "type": "object",
            "properties": {
              "status": {
                "$ref": "#/components/schemas/PumpStatus"
              },
              "identifier": {
                "type": "string",
                "description": "Pump identifier",
                "example": "2"
              }
            }
          }
        }
      },
      "PumpResponse": {
        "type": "object",
        "properties": {
          "data": {
            "type": "object",
            "properties": {
              "type": {
                "type": "string",
                "description": "Type",
                "enum": [
                  "pump"
                ]
              },
              "id": {
                "type": "string",
                "format": "uuid",
                "description": "Pump ID",
                "example": "f106ac99-213c-4cf7-8c1b-1e841516026b"
              },
              "attributes": {
                "type": "object",
                "properties": {
                  "status": {
                    "$ref": "#/components/schemas/PumpStatus"
                  },
                  "identifier": {
                    "type": "string",
                    "description": "Pump identifier",
                    "example": "2"
                  },
                  "fuelType": {
                    "type": "string",
                    "example": "ron95_e10"
                  },
                  "productName": {
                    "type": "string",
                    "example": "Super E10"
                  },
                  "fuelAmount": {
                    "type": "number",
                    "format": "float",
                    "description": "Fuel amount in liters",
                    "example": 44.3
                  },
                  "VAT": {
                    "type": "object",
                    "properties": {
                      "amount": {
                        "type": "number",
                        "format": "float",
                        "example": 9.72
                      },
                      "rate": {
                        "type": "number",
                        "format": "float",
                        "example": 0.19
                      }
                    }
                  },
                  "priceWithoutVAT": {
                    "type": "number",
                    "format": "float",
                    "example": 51.37
                  },
                  "priceIncludingVAT": {
                    "type": "number",
                    "format": "float",
                    "example": 61.09
                  },
                  "currency": {
                    "$ref": "#/components/schemas/currency"
                  }
                }
              }
            }
          }
        },
        "example": {
          "data": {
            "type": "pump",
            "id": "baca9c76-82bf-44e7-b8b0-8af44fa4998b",
            "attributes": {
              "identifier": "3",
              "status": "readyToPay",
              "fuelType": "ron95_e10",
              "productName": "Super E10",
              "fuelAmount": 44.3,
              "VAT": {
                "amount": 9.72,
                "rate": 0.19
              },
              "priceWithoutVAT": 51.37,
              "priceIncludingVAT": 61.09,
              "currency": "EUR"
            }
          }
        }
      },
      "FuelPrice": {
        "type": "object",
        "properties": {
          "type": {
            "type": "string",
            "description": "Fuel price",
            "enum": [
              "fuelPrice"
            ]
          },
          "id": {
            "type": "string",
            "description": "Fuel Price ID",
            "example": "2a1319c3-c136-495d-b59a-47b3246d08af"
          },
          "attributes": {
            "type": "object",
            "properties": {
              "fuelType": {
                "type": "string",
                "enum": [
                  "e85",
                  "ron91",
                  "ron95_e5",
                  "ron95_e10",
                  "ron98",
                  "ron98_e5",
                  "ron100",
                  "diesel",
                  "diesel_gtl",
                  "diesel_b7",
                  "lpg",
                  "cng",
                  "h2",
                  "Truck Diesel",
                  "AdBlue"
                ],
                "example": "ron95_e10"
              },
              "price": {
                "type": "number",
                "format": "float",
                "description": "Price in liters",
                "example": 1.379
              },
              "currency": {
                "$ref": "#/components/schemas/currency"
              },
              "productName": {
                "type": "string",
                "example": "Super E10"
              }
            }
          }
        }
