        "requestBody": {
          "description": "Subscription to be created",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "circleNotificationSubscription": {
                    "$ref": "#/components/schemas/CircleNotificationSubscription"
                  }
                },
                "example": {
                  "circleNotificationSubscription": {
                    "clientCorrelator": "0123",
                    "callbackReference": {
                      "callbackData": "1234",
                      "notifyURL": "http://clientApp.example.com/location_notifications/123456"
                    },
                    "address": "acr:10.0.0.1",
                    "checkImmediate": true,
                    "enteringLeavingCriteria": "Entering",
                    "frequency": 10,
                    "latitude": -80.88,
                    "longitude": 41.277,
                    "radius": 500,
                    "trackingAccuracy": 10
                  }
                }
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Successful subscription",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "circleNotificationSubscription": {
                      "$ref": "#/components/schemas/CircleNotificationSubscription"
                    }
                  },
                  "example": {
                    "circleNotificationSubscription": {
                      "clientCorrelator": "0123",
                      "callbackReference": {
                        "callbackData": "1234",
                        "notifyURL": "http://clientApp.example.com/location_notifications/123456"
                      },
                      "address": "acr:10.0.0.1",
                      "checkImmediate": true,
                      "enteringLeavingCriteria": "Entering",
                      "frequency": 10,
                      "latitude": -80.88,
                      "longitude": 41.277,
                      "radius": 500,
                      "trackingAccuracy": 10,
                      "resourceURL": "http://meAppServer.example.com/location/v2/subscriptions/area/circle/subscription123"
                    }
                  }
                }
              }
            }
          },
          "400": {
            "$ref": "#/components/responses/400"
          },
          "401": {
            "$ref": "#/components/responses/401"
          },
          "403": {
            "$ref": "#/components/responses/403"
          },
          "404": {
            "$ref": "#/components/responses/404"
          },
          "406": {
            "$ref": "#/components/responses/406"
          },
          "429": {
            "$ref": "#/components/responses/429"
          }
        },
        "callbacks": {
          "notification": {
            "{$request.body#/circleNotificationSubscription.callbackReference.notifyURL}": {
              "post": {
                "summary": "Callback POST used to send a notification",
                "description": "Notification from Location service, content based on subscription type",
                "operationId": "circleNotificationPOST",
                "requestBody": {
                  "description": "Subscription notification",
                  "required": true,
                  "content": {
                    "application/json": {
                      "schema": {
                        "type": "object",
                        "properties": {
                          "subscriptionNotification": {
                            "$ref": "#/components/schemas/SubscriptionNotification"
                          }
                        },
                        "example": {
                          "subscriptionNotification": {
                            "enteringLeavingCriteria": "Entering",
                            "isFinalNotification": "false,",
                            "link": {
                              "rel": "CircleNotificationSubscription",
                              "href": "http://meAppServer.example.com/location/v2/subscriptions/area/circle/sub123"
                            },
                            "terminalLocation": {
                              "address": "acr:10.0.0.1",
                              "currentLocation": {
                                "accuracy": 100,
                                "altitude": 1001,
                                "latitude": -80.86302,
                                "longitude": 41.277306,
                                "timestamp": {
                                  "seconds": 1483231138,
                                  "nanoSeconds\"": 0
                                }
                              },
                              "locationRetrievalStatus": "Retrieved"
                            }
                          }
                        }
                      }
                    }
                  }
                },
                "responses": {
                  "204": {
                    "$ref": "#/components/responses/204"
                  }
                }
              }
            }
          }
        },
        "x-swagger-router-controller": "subscriptions"
      }
    },
