                    "clientCorrelator": "0123",
                    "callbackReference": {
                      "callbackData": "1234",
                      "notifyURL": "http://clientApp.example.com/location_notifications/123456"
                    },
                    "monitoredAddress": [
                      "acr:10.0.0.1",
                      "acr:10.0.0.2"
