        "requestBody": {
          "description": "Request to create a seller account fulfillment policy.",
          "content": {
            "application/json": {
              "schema": {
                "description": "Request to create a seller account fulfillment policy.",
                "$ref": "#/components/schemas/FulfillmentPolicyRequest"
              }
            }
          },
          "required": true
        },
