      "SetFulfillmentPolicyResponse": {
        "type": "object",
        "properties": {
          "categoryTypes": {
            "type": "array",
            "description": "The CategoryTypeEnum value to which this policy applies. Used to discern accounts that sell motor vehicles from those that don't. (Currently, each policy can be set to only one categoryTypes value at a time.)",
            "items": {
              "$ref": "#/components/schemas/CategoryType"
            }
          },
          "description": {
            "type": "string",
            "description": "An optional seller-defined description of the fulfillment policy for internal use (this value is not displayed to end users). Max length: 250"
          },
          "freightShipping": {
            "type": "boolean",
            "description": "If set to true, the seller offers freight shipping."
          },
          "fulfillmentPolicyId": {
            "type": "string",
            "description": "A unique eBay-assigned ID for a fulfillment policy. This ID is generated when the policy is created."
          },
          "globalShipping": {
            "type": "boolean",
            "description": "If set to true, the seller has opted-in to the Global Shipping Program and eBay automatically sets the international shipping service options to International Priority Shipping. If the value of globalShipping is false, the seller is responsible for specifying one or more international shipping service options if they want to ship internationally."
          },
          "handlingTime": {
            "description": "Specifies the maximum number of business days the seller commits to for preparing and shipping an order after receiving a cleared payment for the order. This time does not include the transit time it takes the shipping carrier to deliver the order. Note that Top-Rated sellers must offer same-day or one-day handling for a listing in order for that listing to receive a Top Rated Plus seal on the View Item or Search Results pages. This field is required when the seller uses a flat or calculated shipping service, and it does not apply when there is no shipping service specified (for example, if the policy specifies only localPickup or freightShipping). In these cases, this field is not used and you can set it to 0. Min: 0 Max: 30",
            "$ref": "#/components/schemas/TimeDuration"
          },
          "localPickup": {
            "type": "boolean",
            "description": "If set to true, the seller offers local pickup of their items."
          },
          "marketplaceId": {
            "type": "string",
            "description": "The ID of the eBay marketplace to which this fulfillment policy applies. If this value is not specified, value defaults to the seller's eBay registration site. For implementation help, refer to <a href='https://developer.ebay.com/devzone/rest/api-ref/account/types/MarketplaceIdEnum.html'>eBay API documentation</a>"
          },
          "name": {
            "type": "string",
            "description": "A user-defined name for this fulfillment policy. Names must be unique for policies assigned to the same marketplace. Max length: 64"
          },
          "pickupDropOff": {
            "type": "boolean",
            "description": "If set to true, the seller offers the &quot;Click and Collect&quot; option. Currently, &quot;Click and Collect&quot; is available only to large retail merchants the eBay AU and UK marketplaces."
          },
          "shippingOptions": {
            "type": "array",
            "description": "A list that defines the seller's shipping configurations for DOMESTIC and INTERNATIONAL order shipments. The list has a single element if the seller ships to only domestic locations. If the seller also ships internationally, a second element defines their international shipping options. Shipping options configure the high-level shipping settings that apply to orders, such as flat-rate or calculated shipping, and any rate tables the seller wants to associate with the shipping services. Each shippingOption element has a shippingServices container that defines the list of shipping services (domestic or international) offered with this fulfillment policy.",
            "items": {
              "$ref": "#/components/schemas/ShippingOption"
            }
          },
          "shipToLocations": {
            "description": "This object contains the regionIncluded and regionExcluded fields, which indicate the areas to where the seller does and doesn't ship. shipToLocations appears at both the top-level of the fulfillment policy as well as within the individual shippingOptions.shippingService containers. Here at the top level, this field returns the list of regionExclude areas that have been defined at this level, plus a complete list of regionIncluded fields that are compiled from all the regionIncluded fields in the policy's shippingOptions.shippingService.shipToLocations containers. For details on configuring this field, see .",
            "$ref": "#/components/schemas/RegionSet"
          },
          "warnings": {
            "type": "array",
            "description": "A list of warnings related to request. This field normally returns empty, which indicates the request did not generate any warnings.",
            "items": {
              "$ref": "#/components/schemas/Error"
            }
          }
        },
        "description": "Complex type that that gets populated with a response containing a fulfillment policy."
      },
      "SetPaymentPolicyResponse": {
        "type": "object",
        "properties": {
          "categoryTypes": {
            "type": "array",
            "description": "The CategoryTypeEnum value to which this policy applies. Used to discern accounts that sell motor vehicles from those that don't. (Currently, each policy can be set to only one categoryTypes value at a time.)",
            "items": {
              "$ref": "#/components/schemas/CategoryType"
            }
          },
          "deposit": {
            "description": "This container is applicable only if the categoryTypes.name field is set to 'MOTORS_VEHICLES'. In this case, sellers can use this field to specify amounts and due dates for deposits on motor vehicle listings on eBay Motors.",
            "$ref": "#/components/schemas/Deposit"
          },
          "description": {
            "type": "string",
            "description": "An optional seller-defined description of the payment policy for internal use (this value is not displayed to end users)."
          },
          "fullPaymentDueIn": {
            "description": "The policy for when final payment is due on invoiced items (in most cases, this payment policy applies to motor vehicles). This value is always returned if categoryTypes is set to MOTORS_VEHICLES.",
            "$ref": "#/components/schemas/TimeDuration"
          },
          "immediatePay": {
            "type": "boolean",
            "description": "If set to true, payment is due upon receipt (eBay generates a receipt when the buyer agrees to purchase an item). Your items will be available for other buyers until payment is complete. This boolean must be set in the payment policy if the seller wants to create a listing that has an &quot;immediate payment&quot; requirement. Default: false"
          },
          "marketplaceId": {
            "type": "string",
            "description": "The ID of the eBay marketplace to which this payment policy applies. If this value is not specified, value defaults to the seller's eBay registration site. For implementation help, refer to <a href='https://developer.ebay.com/devzone/rest/api-ref/account/types/MarketplaceIdEnum.html'>eBay API documentation</a>"
          },
          "name": {
            "type": "string",
            "description": "A user-defined name for this payment policy. Names must be unique for policies assigned to the same marketplace. Max length: 64"
          },
          "paymentInstructions": {
            "type": "string",
            "description": "This user-defined field allows the seller to give payment instructions to the buyer. These instructions appear on the eBay View Item and Checkout pages. eBay recommends the seller use this field to clarify payment policies for motor vehicles (eBay Motors US and CA). For example, sellers can include the specifics on the deposit (if required), pickup/delivery arrangements, and full payment details on the vehicle. Max length: 500"
          },
          "paymentMethods": {
            "type": "array",
            "description": "A list of the payment methods accepted by the seller. Each payment policy must specify at least one payment method. Payment policies used with motor vehicle listings that require a deposit must have PayPal listed has a payment method (deposits require PayPal as the payment method). Also, in order for a buyer to make a full payment on a US or CA motor vehicle, the payment policy must specify at least one of the following as a payment method: CashOnPickup LoanCheck MOCC (money order or cashier's check) PaymentSeeDescription (payment instructions are in the paymentInstructions field) PersonalCheck",
            "items": {
              "$ref": "#/components/schemas/PaymentMethod"
            }
          },
          "paymentPolicyId": {
            "type": "string",
            "description": "A unique eBay-assigned ID for a payment policy. This ID is generated when the policy is created."
          },
          "warnings": {
            "type": "array",
            "description": "A list of warnings related to request. This field normally returns empty, which indicates the request did not generate any warnings.",
            "items": {
              "$ref": "#/components/schemas/Error"
            }
          }
        },
        "description": "Complex type that that gets populated with a response containing a payment policy."
      },
      "SetReturnPolicyResponse": {
        "type": "object",
        "properties": {
          "categoryTypes": {
            "type": "array",
            "description": "For return policies, this field always returns ALL_EXCLUDING_MOTORS_VEHICLES (returns on motor vehicles are not processed through eBay flows.) Default: ALL_EXCLUDING_MOTORS_VEHICLES (for return policies only)",
            "items": {
              "$ref": "#/components/schemas/CategoryType"
            }
          },
          "description": {
            "type": "string",
            "description": "An optional seller-defined description of the return policy for internal use (this value is not displayed to end users)."
          },
          "extendedHolidayReturnsOffered": {
            "type": "boolean",
            "description": "Important! This field has been deprecated as of version 1.2.0, released on May 31, 2018. Any value supplied in this field is ignored, it is neither read nor returned. If set to true, the seller offers an Extended Holiday Returns policy for their listings. IMPORTANT: Extended Holiday Returns is a seasonally available feature that is offered on some eBay marketplaces. To see if the feature is enabled in any given year, check the eBay Seller Center Returns on eBay page of before the holiday season begins."
          },
          "internationalOverride": {
            "description": "This container, if populated, specifies the seller's policies for international returns (items that require postage via an international shipping service). Buy default, the policies for international returns are inherited from the domestic return policy. This container allows the seller to customize the return policy for international returns.",
            "$ref": "#/components/schemas/InternationalReturnOverrideType"
          },
          "marketplaceId": {
            "type": "string",
            "description": "The ID of the eBay marketplace to which this return policy applies. If this value is not specified, value defaults to the seller's eBay registration site. For implementation help, refer to <a href='https://developer.ebay.com/devzone/rest/api-ref/account/types/MarketplaceIdEnum.html'>eBay API documentation</a>"
          },
          "name": {
            "type": "string",
            "description": "A user-defined name for this return policy. Names must be unique for policies assigned to the same marketplace. Max length: 64"
          },
          "refundMethod": {
            "type": "string",
            "description": "Important! This field has been deprecated as of version 1.2.0, released on May 31, 2018. Any value other than MONEY_BACK will be treated as MONEY_BACK (although for a period of time, eBay will store and return the legacy values to preserve backwards compatibility). Indicates the method the seller uses to compensate the buyer for returned items. The return method specified applies only to remorse returns. For implementation help, refer to <a href='https://developer.ebay.com/devzone/rest/api-ref/account/types/RefundMethodEnum.html'>eBay API documentation</a>"
          },
          "restockingFeePercentage": {
            "type": "string",
            "description": "Important! This field has been deprecated as of version 1.2.0, released on May 31, 2018. Any value supplied in this field is ignored, it is neither read nor returned. Optionally set by the seller, the percentage charged if the seller charges buyers a a restocking fee when items are returned due to buyer remorse and/or a purchasing mistake. The total amount charged to the buyer is the cost of the item multiplied by the percentage indicated in this field."
          },
          "returnInstructions": {
            "type": "string",
            "description": "This field contains the seller's detailed explanation for their return policy and is displayed in the Return Policy section of the View Item page. This field is valid in only the following marketplaces (the field is otherwise ignored): Germany (DE) Spain (ES) France (FR) Italy (IT)"
          },
          "returnMethod": {
            "type": "string",
            "description": "This field indicates the method in which the seller handles non-money back return requests for remorse returns. This field is valid in only the US marketplace and the only valid value is REPLACEMENT. For implementation help, refer to <a href='https://developer.ebay.com/devzone/rest/api-ref/account/types/ReturnMethodEnum.html'>eBay API documentation</a>"
          },
          "returnPeriod": {
            "description": "Specifies the amount of time the buyer has to return an item. The return period begins when the item is marked &quot;delivered&quot; at the buyer's specified ship-to location.",
            "$ref": "#/components/schemas/TimeDuration"
          },
          "returnPolicyId": {
            "type": "string",
            "description": "A unique eBay-assigned ID for a return policy. This ID is generated when the policy is created."
          },
          "returnsAccepted": {
            "type": "boolean",
            "description": "If set to true, the seller accepts returns. If set to false, this field indicates that the seller does not accept returns."
          },
          "returnShippingCostPayer": {
            "type": "string",
            "description": "This field indicates who is responsible for paying for the shipping charges for returned items. The field can be set to either BUYER or SELLER. Depending on the return policy and specifics of the return, either the buyer or the seller can be responsible for the return shipping costs. Note that the seller is always responsible for return shipping costs for SNAD-related issues. For implementation help, refer to <a href='https://developer.ebay.com/devzone/rest/api-ref/account/types/ReturnShippingCostPayerEnum.html'>eBay API documentation</a>"
          },
          "warnings": {
            "type": "array",
            "description": "A list of warnings related to request. This field normally returns empty, which indicates the request did not generate any warnings.",
            "items": {
              "$ref": "#/components/schemas/Error"
            }
          }
        },
        "description": "Complex type that that gets populated with a response containing a return policy."
      },
      "ShippingOption": {
        "type": "object",
        "properties": {
          "costType": {
            "type": "string",
            "description": "Required if the policy offers shipping options using a shippingOptions container. Defines whether the shipping cost is FLAT_RATE, CALCULATED, or NOT_SPECIFIED (for use with freight shipping and local pickup). For implementation help, refer to <a href='https://developer.ebay.com/devzone/rest/api-ref/account/types/ShippingCostTypeEnum.html'>eBay API documentation</a>"
          },
          "insuranceFee": {
            "description": "Required if shippingOptions.insuranceOffered is set to true. Sellers can offer buyer-paid shipping insurance only when they ship to AU, FR, or IT. This value indicates the cost the buyer must pay to purchase shipping insurance for the items being shipped.",
            "$ref": "#/components/schemas/Amount"
          },
          "insuranceOffered": {
            "type": "boolean",
            "description": "If set to true, the seller offers buyer-paid shipping insurance. The optionType shows whether this is for either a domestic or international shipment. Buyer-paid shipping insurance is currently supported in only Australia (AU), France (FR), and Italy (IT)."
          },
          "optionType": {
            "type": "string",
            "description": "Required if the policy offers shipping options using a shippingOptions container. Use this field to set the ShippingOption element to either DOMESTIC or INTERNATIONAL. For implementation help, refer to <a href='https://developer.ebay.com/devzone/rest/api-ref/account/types/ShippingOptionTypeEnum.html'>eBay API documentation</a>"
          },
          "packageHandlingCost": {
            "description": "An amount a seller can add to cover packaging, handling, and insurance costs. This cost is an addition to the selected shipping service cost and is included in the final shipping service costs in the output. This field must specify a zero value if freeShipping is set to true. If the seller offers domestic and international calculated shipping but sets this field for only domestic shipping, eBay also applies the cost to all international shipments. To specify a fee for only international shipments, set the a domestic packagingHandlingCost to 0.",
            "$ref": "#/components/schemas/Amount"
          },
          "rateTableId": {
            "type": "string",
            "description": "A unique eBay-assigned ID associated with a user-created shipping rate table. The locality of a shipping rate table can be either DOMESTIC or INTERNATIONAL and you must ensure the value specified in this field references a shipping rate table that matches the type specified in the shippingOptions.optionType field. If you mismatch the types, eBay responds with a 20403 error. Call getRateTable to retrieve information (including rateTableId values) on the rate tables configured by a seller. For information on creating rate tables, see Using shipping rate tables."
          },
          "shippingServices": {
            "type": "array",
            "description": "Required if the policy offers shipping options using a shippingOptions container. Contains a list of shipping services offered for either DOMESTIC or INTERNATIONAL shipments. Sellers can specify up to four domestic shipping services and up to five international shipping services by using separate shippingService containers for each. Note that if the seller is opted in to the Global Shipping Program, they can specify only four other international shipping services, regardless of whether or not Global Shipping is offered as one of the services.",
            "items": {
              "$ref": "#/components/schemas/ShippingService"
            }
          }
        },
        "description": "This complex type defines a seller's shipping configuration for either a DOMESTIC or INTERNATIONAL shipping option. Shipping options configure the high-level settings for shipments, such as flat-rate or calculated shipping, and any rate tables the seller wants to associate with the policy. Each shippingOption element has a shippingServices container that defines the list of shipping carriers and services that are available for the parent shipping option (that is, for either DOMESTIC or INTERNATIONAL shipping). If a seller offers an international shipping option, they must also offer a domestic shipping option. Note that costType (FLAT_RATE or CALCULATED) is set in shippingOptions and that all associated shipping services must be able to support this cost type."
      },
      "ShippingService": {
        "type": "object",
        "properties": {
          "additionalShippingCost": {
            "description": "The cost of shipping each additional item if the same buyer purchases a multiple quantity of the same line item. This field is applicable for policies that cover multiple-quantity, fixed-price listings and is not applicable for policies that apply to single-quantity listings. Sellers can encourage buyers to purchase multiple items and by offering a shipping discount on added items. The value for this field should be lower than the value set for shippingCost and you can set this value to 0 to offer free shipping on added items. If this value is not set, it defaults to the value set for shippingCost.",
            "$ref": "#/components/schemas/Amount"
          },
          "buyerResponsibleForPickup": {
            "type": "boolean",
            "description": "This field is only applicable to vehicle categories on eBay Motors (US and Canada). If set to true, the buyer is responsible for picking up the vehicle. Otherwise, the seller should specify the vehicle pickup arrangements in the item description. The seller cannot modify this flag if the vehicle has bids or if the listing ends within 12 hours. Default: false"
          },
          "buyerResponsibleForShipping": {
            "type": "boolean",
            "description": "This field is applicable for only items listed in vehicle categories on eBay Motors (US and Canada). If set to true, the buyer is responsible for the shipment of the vehicle. Otherwise, the seller should specify the vehicle shipping arrangements in the item description. The seller cannot modify this flag if the vehicle has bids or if the listing ends within 12 hours. Default: false"
          },
          "cashOnDeliveryFee": {
            "description": "The value indicates the Cash on Delivery (COD) fee that the seller charges if the buyer uses the CASH_ON_DELIVERY payment method. This fee is added to the total cost of the item, and the total cost is due from the buyer upon the delivery of the item. This field is applicable only if the buyer-selected payment method is 'COD' and the selected shipping service option supports a Cash on Delivery option. To see if a domestic shipping service supports the Cash on Delivery option, call GeteBayDetails with DetailName set to ShippingServiceDetails, then review the response to see if CODService is set to true for the targeted shipping service.",
            "$ref": "#/components/schemas/Amount"
          },
          "freeShipping": {
            "type": "boolean",
            "description": "If set to true, the seller offers free shipping to the buyer. This field can only be included and set to 'true' for the first domestic shipping service option specified in the shippingServices container (it is ignored if set for subsequent shipping services). The first specified shipping service option has a sortOrder value of 1 or (if the sortOrderId field is not used) it is the shipping service option that's specified first in the shippingServices container."
          },
          "shippingCarrierCode": {
            "type": "string",
            "description": "The shipping carrier, such as 'USPS', 'FedEx', 'UPS', and so on."
          },
          "shippingCost": {
            "description": "Required if the shipping option uses a FLAT_RATE cost type and freeShipping is false. For FLAT_RATE shipping options, the amount specified in this field is the shipping cost for the selected shipping carrier and service. The amount supplied must exclude any additional shipping charges (such as the seller's handling charges or insurance). This field is not applicable in CALCULATED shipping services (the cost for a calculated shipping service cannot be determined until the listing has ended and the buyer has specified a postal code). This value is automatically set to '0.0' if freeShipping is set to true.",
            "$ref": "#/components/schemas/Amount"
          },
          "shippingServiceCode": {
            "type": "string",
            "description": "The shipping service that the shipping carrier uses to ship an item. For example, an overnight, two-day delivery, or other type of service. For details on configuring shipping services, see Setting the shipping carrier and shipping service values."
          },
          "shipToLocations": {
            "description": "This object contains the regionIncluded and regionExcluded fields that define the list of geographical regions that the seller ships to and the list of regions where they do not ship. shipToLocations appears at both the top-level of the fulfillment policy as well as within the individual shippingOptions.shippingService containers. Here, within the shippingService container, sellers can specify a list of regions to where they ship to using regionIncluded (do not set the regiuonExcluded field at this level). Every eBay marketplace supports its own set of regions that you can use in the regionIncluded field. To determine the valid 'Ship-To' values for a marketplace, call GeteBayDetails with DetailName set to ShippingLocationDetails, then review the ShippingLocation fields in the response for the strings that you use in the regionIncluded.regionName field. For more details, see The shipToLocations container. When returned at this level, this container lists only the regionIncluded areas defined for the associated shippingService container.",
            "$ref": "#/components/schemas/RegionSet"
          },
          "sortOrder": {
            "type": "integer",
            "description": "This integer value controls the order that this shipping service option appears in the View Item and Checkout pages, as related to the other specified shipping service options. Sellers can specify up to four domestic shipping services (in four separate shippingService containers), so valid values are 1, 2, 3, and 4. A shipping service option with a sortOrder value of '1' appears at the top of View Item and Checkout pages. Conversely, a shipping service option with a sortOrder value of '4' appears at the bottom of the list. Sellers can specify up to five international shipping services (in five separate shippingService containers, so valid values for international shipping services are 1, 2, 3, 4, and 5. Similarly to domestic shipping service options, the sortOrder value of a international shipping service option controls the placement of that shipping service option in the View Item and Checkout pages. Set up different domestic and international services by configuring two shippingOptions containers, where you set shippingOptions.optionType to either DOMESTIC or INTERNATIONAL to indicate the area supported by the listed shipping services. If the sortOrder field is not supplied, the order of domestic and international shipping service options is determined by the order in which they are listed in the API call. Min: 1. Max: 4 (for domestic shipping service) or 5 (for international shipping service).",
            "format": "int32"
          },
          "surcharge": {
            "description": "A fee that can be charged to US buyers when they have an item shipped via UPS or FedEx to Alaska, Hawaii or Puerto Rico. Use this element only if the shipping option specifies a flat-rate service and the policy targets the MOTORS_VEHICLES category type in the US marketplace. If some line items in an order have a surcharge, the surcharge amount is added only for those line items. This value is returned only when set. Note that you cannot add a surcharge to items shipped via the Global Shipping Program.",
            "$ref": "#/components/schemas/Amount"
          }
        },
        "description": "A complex type that defines the available shipping services offered in the parent shippingOptions container. The shipping services specified here must be able to accommodate the optionType defined in the parent shippingOption container (either DOMESTIC or INTERNATIONAL). Tip: For more on setting up shipping services, see Setting the shipping carrier and shipping service values."
      },
      "TimeDuration": {
        "type": "object",
        "properties": {
          "unit": {
            "type": "string",
            "description": "Required in the TimeDuration container. A time-measurement unit used to specify a period of time. Time-measurement units can be years, months, days, hours, minutes, and other time units (see TimeDurationUnitEnum for a complete list of possible units). The unit is applied to the number in the value field to define a span of time. See the containing object for details and call GeteBayDetails in the Trading API to get the allowable values for the specific object you're configuring. For implementation help, refer to <a href='https://developer.ebay.com/devzone/rest/api-ref/account/types/TimeDurationUnitEnum.html'>eBay API documentation</a>"
          },
          "value": {
            "type": "integer",
            "description": "Required in the TimeDuration container. An amount of time, as measured by the time-measurement units specified in the unit field. Supported values for this field vary according to the object using the time duration. See the containing object for details and call GeteBayDetails in the Trading API to get the allowable values for the specific object you're configuring.",
            "format": "int32"
          }
        },
        "description": "A complex type that specifies a period of time using a specified time-measurement unit."
      },
      "Error": {
        "type": "object",
        "properties": {
          "category": {
            "type": "string",
            "description": "The category type for this error or warning. It takes a string that can have one of three values: Application: Indicates an exception or error occurred in the application code or at runtime. Examples include catching an exception in a service's business logic, system failures, or request errors from a dependency. Business: Used when your service or a dependent service refused to continue processing on the resource because of a business rule violation such as &quot;Seller does not ship item to Antarctica&quot; or &quot;Buyer ineligible to purchase an alcoholic item&quot;. Business errors are not syntactical input errors. Request: Used when there is anything wrong with the request, such as authentication, syntactical errors, rate limiting or missing headers, bad HTTP header values, and so on."
          },
          "domain": {
            "type": "string",
            "description": "Name of the domain containing the service or application."
          },
          "errorId": {
            "type": "integer",
            "description": "A positive integer that uniquely identifies the specific error condition that occurred. Your application can use error codes as identifiers in your customized error-handling algorithms.",
            "format": "int32"
          },
          "inputRefIds": {
            "type": "array",
            "description": "Identifies specific request elements associated with the error, if any. inputRefId's response is format specific. For JSON, use JSONPath notation.",
            "items": {
              "type": "string"
            }
          },
          "longMessage": {
            "type": "string",
            "description": "An expanded version of message that should be around 100-200 characters long, but is not required to be such."
          },
          "message": {
            "type": "string",
            "description": "An end user and app developer friendly device agnostic message. It explains what the error or warning is, and how to fix it (in a general sense). Its value is at most 50 characters long. If applicable, the value is localized in the end user's requested locale."
          },
          "outputRefIds": {
            "type": "array",
            "description": "Identifies specific response elements associated with the error, if any. Path format is the same as inputRefId.",
            "items": {
              "type": "string"
            }
          },
          "parameters": {
            "type": "array",
            "description": "This optional complex field type contains a list of one or more context-specific ErrorParameter objects, with each item in the list entry being a parameter (or input field name) that caused an error condition. Each ErrorParameter object consists of two fields, a name and a value.",
            "items": {
              "$ref": "#/components/schemas/ErrorParameter"
            }
          },
          "subdomain": {
            "type": "string",
            "description": "Name of the domain's subsystem or subdivision. For example, checkout is a subdomain in the buying domain."
          }
        },
        "description": "A container that defines the elements of error and warning messages."
      },
      "ErrorParameter": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "Name of the entity that threw the error."
          },
          "value": {
            "type": "string",
            "description": "A description of the error."
          }
        },
        "description": "Container for an error parameter."
      }
