{
    "components": {
      "headers": {
        "DeprecationHeader": {
          "description": "A header containing the deprecation date of the underlying endpoint. For more information, please refer to the deprecation header RFC:\nhttps://tools.ietf.org/id/draft-dalal-deprecation-header-01.html\n",
          "schema": {
            "format": "date-time",
            "type": "string"
          }
        },
        "InternalGlooNormalizedPathHeader": {
          "description": "An internal header used by Snyk's API-Gateway for analytics.\n",
          "schema": {
            "type": "string"
          }
        },
        "InternalGlooOrgIdHeader": {
          "description": "An internal header used by Snyk's API-Gateway for analytics.\n",
          "schema": {
            "format": "uuid",
            "type": "string"
          }
        },
        "RequestIdResponseHeader": {
          "description": "A header containing a unique id used for tracking this request. If you are reporting an issue to Snyk it's very helpful to provide this ID.\n",
          "schema": {
            "format": "uuid",
            "type": "string"
          }
        },
        "SunsetHeader": {
          "description": "A header containing the date of when the underlying endpoint will be removed. This header is only present if the endpoint has been deprecated. Please refer to the RFC for more information:\nhttps://datatracker.ietf.org/doc/html/rfc8594\n",
          "schema": {
            "format": "date-time",
            "type": "string"
          }
        },
        "VersionRequestedResponseHeader": {
          "description": "A header containing the version of the endpoint requested by the caller.",
          "schema": {
            "$ref": "#/components/schemas/Version"
          }
        },
        "VersionServedResponseHeader": {
          "description": "A header containing the version of the endpoint that was served by the API.",
          "schema": {
            "$ref": "#/components/schemas/Version"
          }
        },
        "VersionStageResponseHeader": {
          "description": "A header containing the version stage of the endpoint. This stage describes the guarantees snyk provides surrounding stability of the endpoint.\n",
          "schema": {
            "enum": [
              "wip",
              "experimental",
              "beta",
              "ga",
              "deprecated",
              "sunset"
            ],
            "type": "string"
          }
        }
      },
      "parameters": {
        "EndingBefore": {
          "description": "Return the page of results immediately before this cursor",
          "in": "query",
          "name": "ending_before",
          "schema": {
            "type": "string"
          }
        },
        "IssueSeverity": {
          "description": "Severity of issues to match",
          "in": "query",
          "name": "severity",
          "schema": {
            "$ref": "#/components/schemas/IssueSeverity"
          }
        },
        "IssueType": {
          "description": "Issue type(s) to match, comma-separated",
          "explode": false,
          "in": "query",
          "name": "type",
          "schema": {
            "items": {
              "$ref": "#/components/schemas/IssueType"
            },
            "type": "array"
          },
          "style": "form"
        },
        "Limit": {
          "description": "Number of results to return per page",
          "in": "query",
          "name": "limit",
          "schema": {
            "default": 10,
            "format": "int32",
            "maximum": 100,
            "minimum": 10,
            "multipleOf": 10,
            "type": "integer"
          }
        },
        "StartingAfter": {
          "description": "Return the page of results immediately after this cursor",
          "in": "query",
          "name": "starting_after",
          "schema": {
            "type": "string"
          }
        },
        "Version": {
          "description": "The requested version of the endpoint to process the request",
          "in": "query",
          "name": "version",
          "required": true,
          "schema": {
            "type": "string"
          }
        }
      },
      "responses": {
        "400": {
          "content": {
            "application/vnd.api+json": {
              "schema": {
                "$ref": "#/components/schemas/ErrorDocument"
              }
            }
          },
          "description": "Bad Request: A parameter provided as a part of the request was invalid.",
          "headers": {
            "deprecation": {
              "$ref": "#/components/headers/DeprecationHeader"
            },
            "snyk-request-id": {
              "$ref": "#/components/headers/RequestIdResponseHeader"
            },
            "snyk-version-lifecycle-stage": {
              "$ref": "#/components/headers/VersionStageResponseHeader"
            },
            "snyk-version-requested": {
              "$ref": "#/components/headers/VersionRequestedResponseHeader"
            },
            "snyk-version-served": {
              "$ref": "#/components/headers/VersionServedResponseHeader"
            },
            "sunset": {
              "$ref": "#/components/headers/SunsetHeader"
            }
          }
        },
        "401": {
          "content": {
            "application/vnd.api+json": {
              "schema": {
                "$ref": "#/components/schemas/ErrorDocument"
              }
            }
          },
          "description": "Unauthorized: the request requires an authentication token or a token with more permissions.",
          "headers": {
            "deprecation": {
              "$ref": "#/components/headers/DeprecationHeader"
            },
            "snyk-request-id": {
              "$ref": "#/components/headers/RequestIdResponseHeader"
            },
            "snyk-version-lifecycle-stage": {
              "$ref": "#/components/headers/VersionStageResponseHeader"
            },
            "snyk-version-requested": {
              "$ref": "#/components/headers/VersionRequestedResponseHeader"
            },
            "snyk-version-served": {
              "$ref": "#/components/headers/VersionServedResponseHeader"
            },
            "sunset": {
              "$ref": "#/components/headers/SunsetHeader"
            }
          }
        },
        "403": {
          "content": {
            "application/vnd.api+json": {
              "schema": {
                "$ref": "#/components/schemas/ErrorDocument"
              }
            }
          },
          "description": "Forbidden: The client does not have access rights to the content.",
          "headers": {
            "deprecation": {
              "$ref": "#/components/headers/DeprecationHeader"
            },
            "snyk-request-id": {
              "$ref": "#/components/headers/RequestIdResponseHeader"
            },
            "snyk-version-lifecycle-stage": {
              "$ref": "#/components/headers/VersionStageResponseHeader"
            },
            "snyk-version-requested": {
              "$ref": "#/components/headers/VersionRequestedResponseHeader"
            },
            "snyk-version-served": {
              "$ref": "#/components/headers/VersionServedResponseHeader"
            },
            "sunset": {
              "$ref": "#/components/headers/SunsetHeader"
            }
          }
        },
        "404": {
          "content": {
            "application/vnd.api+json": {
              "schema": {
                "$ref": "#/components/schemas/ErrorDocument"
              }
            }
          },
          "description": "Not Found: The resource being operated on could not be found.",
          "headers": {
            "deprecation": {
              "$ref": "#/components/headers/DeprecationHeader"
            },
            "snyk-request-id": {
              "$ref": "#/components/headers/RequestIdResponseHeader"
            },
            "snyk-version-lifecycle-stage": {
              "$ref": "#/components/headers/VersionStageResponseHeader"
            },
            "snyk-version-requested": {
              "$ref": "#/components/headers/VersionRequestedResponseHeader"
            },
            "snyk-version-served": {
              "$ref": "#/components/headers/VersionServedResponseHeader"
            },
            "sunset": {
              "$ref": "#/components/headers/SunsetHeader"
            }
          }
        },
        "500": {
          "content": {
            "application/vnd.api+json": {
              "schema": {
                "$ref": "#/components/schemas/ErrorDocument"
              }
            }
          },
          "description": "Internal Server Error: An error was encountered while attempting to process the request.",
          "headers": {
            "deprecation": {
              "$ref": "#/components/headers/DeprecationHeader"
            },
            "snyk-request-id": {
              "$ref": "#/components/headers/RequestIdResponseHeader"
            },
            "snyk-version-lifecycle-stage": {
              "$ref": "#/components/headers/VersionStageResponseHeader"
            },
            "snyk-version-requested": {
              "$ref": "#/components/headers/VersionRequestedResponseHeader"
            },
            "snyk-version-served": {
              "$ref": "#/components/headers/VersionServedResponseHeader"
            },
            "sunset": {
              "$ref": "#/components/headers/SunsetHeader"
            }
          }
        }
      },
      "schemas": {
        "App": {
          "additionalProperties": false,
          "properties": {
            "attributes": {
              "$ref": "#/components/schemas/AppAttributes"
            },
            "id": {
              "format": "uuid",
              "type": "string"
            },
            "links": {
              "$ref": "#/components/schemas/Links"
            },
            "type": {
              "type": "string"
            }
          },
          "required": [
            "type",
            "id",
            "attributes"
          ],
          "type": "object"
        },
        "AppAttributes": {
          "properties": {
            "clientId": {
              "description": "The oauth2 client id for the app.",
              "format": "uuid",
              "type": "string"
            },
            "isPublic": {
              "description": "A boolean to indicate an app is publicly available.",
              "type": "boolean"
            },
            "name": {
              "description": "The name given to the app.",
              "type": "string"
            },
            "redirectUris": {
              "description": "The provided redirect URIs for the app.",
              "items": {
                "type": "string"
              },
              "type": "array"
            },
            "scopes": {
              "description": "The scopes this app is allowed to request during authorization.",
              "items": {
                "type": "string"
              },
              "type": "array"
            }
          },
          "required": [
            "name",
            "clientId",
            "redirectUris",
            "scopes",
            "isPublic"
          ],
          "type": "object"
        },
        "AppOrg": {
          "additionalProperties": false,
          "properties": {
            "attributes": {
              "additionalProperties": false,
              "type": "object"
            },
            "id": {
              "format": "uuid",
              "type": "string"
            },
            "type": {
              "type": "string"
            }
          },
          "required": [
            "type",
            "id"
          ],
          "type": "object"
        },
        "AppWithSecret": {
          "additionalProperties": false,
          "properties": {
            "attributes": {
              "allOf": [
                {
                  "$ref": "#/components/schemas/AppAttributes"
                },
                {
                  "properties": {
                    "clientSecret": {
                      "description": "The oauth2 client secret for the app. This is the only time this secret will be returned, store it securely and don't lose it.",
                      "type": "string"
                    }
                  },
                  "required": [
                    "clientSecret"
                  ],
                  "type": "object"
                }
              ]
            },
            "id": {
              "format": "uuid",
              "type": "string"
            },
            "links": {
              "$ref": "#/components/schemas/Links"
            },
            "type": {
              "type": "string"
            }
          },
          "required": [
            "type",
            "id",
            "attributes",
            "links"
          ],
          "type": "object"
        },
        "CodeIssue": {
          "description": "An issue discovered by SAST code analysis",
          "properties": {
            "attributes": {
              "allOf": [
                {
                  "$ref": "#/components/schemas/IssueSummaryAttributes"
                },
                {
                  "properties": {
                    "fingerprint": {
                      "nullable": true,
                      "type": "string"
                    },
                    "fingerprintVersion": {
                      "nullable": true,
                      "type": "string"
                    },
                    "primaryRegion": {
                      "description": "SARIF code region object",
                      "type": "object"
                    },
                    "priorityScore": {
                      "type": "number"
                    },
                    "priorityScoreFactors": {
                      "description": "Descriptions of factors affecting priority score",
                      "items": {
                        "type": "string"
                      },
                      "type": "array"
                    }
                  },
                  "type": "object"
                }
              ]
            },
            "id": {
              "description": "Code public issue ID",
              "example": "ea536a06-0566-40ca-b96b-155568aa2027",
              "format": "uuid",
              "type": "string"
            },
            "type": {
              "description": "Content type",
              "example": "code-issue",
              "type": "string"
            }
          },
          "required": [
            "type",
            "id",
            "attributes"
          ],
          "type": "object"
        },
        "Error": {
          "additionalProperties": false,
          "properties": {
            "detail": {
              "type": "string"
            },
            "id": {
              "format": "uuid",
              "type": "string"
            },
            "meta": {
              "additionalProperties": true,
              "type": "object"
            },
            "source": {
              "additionalProperties": false,
              "properties": {
                "parameter": {
                  "type": "string"
                },
                "pointer": {
                  "type": "string"
                }
              },
              "type": "object"
            },
            "status": {
              "type": "string"
            }
          },
          "required": [
            "status",
            "detail"
          ],
          "type": "object"
        },
        "ErrorDocument": {
          "additionalProperties": false,
          "properties": {
            "errors": {
              "items": {
                "$ref": "#/components/schemas/Error"
              },
              "minItems": 1,
              "type": "array"
            },
            "jsonapi": {
              "$ref": "#/components/schemas/JsonApi"
            }
          },
          "required": [
            "jsonapi",
            "errors"
          ],
          "type": "object"
        },
        "HelloWorld": {
          "additionalProperties": false,
          "properties": {
            "attributes": {
              "additionalProperties": false,
              "properties": {
                "betaField": {
                  "type": "string"
                },
                "message": {
                  "type": "string"
                },
                "requestSubject": {
                  "additionalProperties": false,
                  "properties": {
                    "clientId": {
                      "format": "uuid",
                      "type": "string"
                    },
                    "publicId": {
                      "format": "uuid",
                      "type": "string"
                    },
                    "type": {
                      "type": "string"
                    }
                  },
                  "required": [
                    "publicId",
                    "type"
                  ],
                  "type": "object"
                }
              },
              "required": [
                "message",
                "betaField",
                "requestSubject"
              ],
              "type": "object"
            },
            "id": {
              "format": "uuid",
              "type": "string"
            },
            "type": {
              "type": "string"
            }
          },
          "required": [
            "type",
            "id",
            "attributes"
          ],
          "type": "object"
        },
        "IssueSeverity": {
          "description": "Severity of an issue",
          "enum": [
            "low",
            "medium",
            "high",
            "critical"
          ],
          "type": "string"
        },
        "IssueSummary": {
          "description": "Summary description of an issue.",
          "properties": {
            "attributes": {
              "$ref": "#/components/schemas/IssueSummaryAttributes"
            },
            "id": {
              "description": "The Issue ID",
              "example": "2bcd80a9-e343-4601-9393-f820d51ab713",
              "format": "uuid",
              "type": "string"
            },
            "links": {
              "$ref": "#/components/schemas/Links"
            },
            "type": {
              "description": "Content type",
              "example": "issue-summary",
              "type": "string"
            }
          },
          "required": [
            "type",
            "id",
            "attributes",
            "links"
          ],
          "type": "object"
        },
        "IssueSummaryAttributes": {
          "properties": {
            "ignored": {
              "description": "Whether the issue has been ignored",
              "type": "boolean"
            },
            "issueType": {
              "$ref": "#/components/schemas/IssueType"
            },
            "severity": {
              "$ref": "#/components/schemas/IssueSeverity"
            },
            "title": {
              "description": "The name of the issue",
              "type": "string"
            }
          },
          "required": [
            "issueType",
            "title",
            "severity",
            "ignored"
          ],
          "type": "object"
        },
        "IssueType": {
          "description": "Issue type. Implies the existence of a resource /issues/detail/{issue-type}/{id}.\n",
          "enum": [
            "code"
          ],
          "type": "string"
        },
        "JsonApi": {
          "additionalProperties": false,
          "properties": {
            "version": {
              "type": "string"
            }
          },
          "required": [
            "version"
          ],
          "type": "object"
        },
        "LinkProperty": {
          "oneOf": [
            {
              "type": "string"
            },
            {
              "additionalProperties": false,
              "properties": {
                "href": {
                  "type": "string"
                },
                "meta": {
                  "additionalProperties": true,
                  "type": "object"
                }
              },
              "required": [
                "href",
                "meta"
              ],
              "type": "object"
            }
          ]
        },
        "Links": {
          "additionalProperties": false,
          "properties": {
            "first": {
              "$ref": "#/components/schemas/LinkProperty"
            },
            "last": {
              "$ref": "#/components/schemas/LinkProperty"
            },
            "next": {
              "$ref": "#/components/schemas/LinkProperty"
            },
            "prev": {
              "$ref": "#/components/schemas/LinkProperty"
            },
            "related": {
              "$ref": "#/components/schemas/LinkProperty"
            },
            "self": {
              "$ref": "#/components/schemas/LinkProperty"
            }
          },
          "type": "object"
        },
        "OrgInvitation": {
          "additionalProperties": false,
          "properties": {
            "attributes": {
              "$ref": "#/components/schemas/OrgInvitationAttributes"
            },
            "id": {
              "format": "uuid",
              "type": "string"
            },
            "type": {
              "type": "string"
            }
          },
          "required": [
            "type",
            "id",
            "attributes"
          ],
          "type": "object"
        },
        "OrgInvitationAttributes": {
          "additionalProperties": false,
          "properties": {
            "email": {
              "description": "The email address of the invitee.",
              "example": "example@email.com",
              "type": "string"
            },
            "isActive": {
              "description": "The active status of the invitation.",
              "type": "boolean"
            },
            "org": {
              "description": "The organization the invite was created for.",
              "example": "Example org",
              "type": "string"
            },
            "role": {
              "description": "The role assigned to the invitee on acceptance.",
              "example": "Developer",
              "type": "string"
            }
          },
          "required": [
            "email",
            "isActive",
            "role",
            "org"
          ],
          "type": "object"
        },
        "Project": {
          "additionalProperties": false,
          "properties": {
            "attributes": {
              "additionalProperties": false,
              "properties": {
                "businessCriticality": {
                  "items": {
                    "type": "string"
                  },
                  "type": "array"
                },
                "created": {
                  "description": "The date that the project was created on",
                  "example": "2021-05-29T09:50:54.014Z",
                  "format": "date-time",
                  "type": "string"
                },
                "environment": {
                  "items": {
                    "type": "string"
                  },
                  "type": "array"
                },
                "hostname": {
                  "description": "The hostname for a CLI project, null if not set",
                  "nullable": true,
                  "type": "string"
                },
                "lifecycle": {
                  "items": {
                    "type": "string"
                  },
                  "type": "array"
                },
                "name": {
                  "type": "string"
                },
                "origin": {
                  "description": "The origin the project was added from",
                  "example": "github",
                  "type": "string"
                },
                "status": {
                  "description": "Describes if a project is currently monitored or it is de-activated",
                  "example": "active",
                  "type": "string"
                },
                "tags": {
                  "items": {
                    "$ref": "#/components/schemas/Tag"
                  },
                  "type": "array"
                },
                "targetReference": {
                  "nullable": true,
                  "type": "string"
                },
                "type": {
                  "description": "The package manager of the project",
                  "example": "maven",
                  "type": "string"
                }
              },
              "required": [
                "name",
                "created",
                "origin",
                "type",
                "status"
              ],
              "type": "object"
            },
            "id": {
              "description": "The ID.",
              "example": "331ede0a-de94-456f-b788-166caeca58bf",
              "format": "uuid",
              "type": "string"
            },
            "relationships": {
              "$ref": "#/components/schemas/Relationships"
            },
            "type": {
              "description": "Content type.",
              "example": "projects",
              "type": "string"
            }
          },
          "required": [
            "type",
            "id",
            "attributes"
          ],
          "type": "object"
        },
        "Relationships": {
          "additionalProperties": {
            "properties": {
              "data": {
                "properties": {
                  "id": {
                    "format": "uuid",
                    "type": "string"
                  },
                  "links": {
                    "$ref": "#/components/schemas/Links"
                  },
                  "type": {
                    "type": "string"
                  }
                },
                "required": [
                  "type",
                  "id"
                ]
              }
            },
            "required": [
              "data"
            ],
            "type": "object"
          },
          "type": "object"
        },
        "Tag": {
          "additionalProperties": false,
          "properties": {
            "key": {
              "type": "string"
            },
            "value": {
              "type": "string"
            }
          },
          "required": [
            "key",
            "value"
          ],
          "type": "object"
        },
        "User": {
          "additionalProperties": false,
          "properties": {
            "attributes": {
              "additionalProperties": false,
              "properties": {
                "email": {
                  "description": "The email of the user.",
                  "example": "user@someorg.com",
                  "type": "string"
                },
                "name": {
                  "description": "The name of the user.",
                  "example": "user",
                  "type": "string"
                },
                "username": {
                  "description": "The username of the user.",
                  "example": "username",
                  "type": "string"
                }
              },
              "type": "object"
            },
            "id": {
              "description": "The Snyk ID corresponding to this user",
              "example": "55a348e2-c3ad-4bbc-b40e-9b232d1f4121",
              "format": "uuid",
              "type": "string"
            },
            "type": {
              "description": "Content type.",
              "example": "user",
              "type": "string"
            }
          },
          "required": [
            "type",
            "id",
            "attributes"
          ],
          "type": "object"
        },
        "Version": {
          "pattern": "^(wip|work-in-progress|experimental|beta|(([0-9]{4})-([0-1][0-9]))-((3[01])|(0[1-9])|([12][0-9])))$",
          "type": "string"
        }
      }
    },
    "info": {
      "title": "Snyk API",
      "version": "v3"
    },
    "openapi": "3.0.3",
    "paths": {
      "/apps/{client_id}/orgs": {
        "get": {
          "description": "Get a list of organizations your installed App has been granted access to by the user.",
          "operationId": "getAppAccessOrgs",
          "parameters": [
            {
              "$ref": "#/components/parameters/Version"
            },
            {
              "description": "The oauth2 client id of your app.",
              "in": "path",
              "name": "client_id",
              "required": true,
              "schema": {
                "format": "uuid",
                "type": "string"
              }
            },
            {
              "$ref": "#/components/parameters/StartingAfter"
            },
            {
              "$ref": "#/components/parameters/EndingBefore"
            },
            {
              "$ref": "#/components/parameters/Limit"
            }
          ],
          "responses": {
            "200": {
              "content": {
                "application/vnd.api+json": {
                  "schema": {
