  /h5/metadata/csr-designations:
    get:
      description: >
        <br />

        <h4>Changelog</h4>

        <div class="panel-body">
            <p><strong>February 21, 2017:</strong></p>
            <ul>
                <li>Renamed Endpoint from "CSR Designations" to "Halo 5 - CSR Designations".</li>
                <li>Removed "{title}" Request Parameter.</li>
                <li>Documented "Accept-Language" Request Header.</li>
            </ul>
        </div>
      operationId: Halo-5-CSR-Designations
      parameters:
        - description: The desired language for the Metadata.
          in: header
          name: Accept-Language
          schema:
            type: string
            default: en
      responses:
        "200":
          description: The response body will contain the requested Metadata.
          content:
            application/json:
              examples:
                response:
                  value: >
                    // A list of CSR designations for the title. CSR stands for
                    Competitive Skill Rank. CSR

                    // is organized into a series of designations, each with a set of tiers within the

                    // designation. The designations are: Bronze, Silver, Gold, Platinum, Diamond, Onyx, and

                    // Champion. Within each designation are tiers, for example, Bronze 1, Bronze 2, Bronze

                    // 3, etc. The Onyx and Champion designations are special. They only have one tier. For

                    // non-Champion players, we keep the raw CSR value and absolute ranking hidden and show

                    // the CSR tier. For Onyx and Champion players, we display the raw CSR value. For

                    // Champion players, we show a leaderboard ranking. To determine what CSR a player has

                    // earned, view the Service Record stats for that player. There is no significance to the

                    // ordering.

                    [
                      {
                        // A localized name for the designation, suitable for display to users. The text is
                        // title cased. 
                        "name": "string",

                        // An image to use as the background for the designation.
                        "bannerImageUrl": "string",

                        // An array of "CSR Tier" entries, one for each tier this designation supports.
                        "tiers": [
                          {
                            // An image to use as the icon for th tier.
                            "iconImageUrl": "string",

                            // An ID that identifies the tier.
                            "id": "int",

                            // Internal use only. Do not use.
                            "contentId": "guid"
                          }
                        ],

                        // An ID that identifies the designation. It happens to be the same as the
                        // designation ordering. This value is the same across all languages. Note that Id =
                        // 0 indicates the player has an "Unranked" designation. The player must complete
                        // additional matches before being assigned an official CSR designation.
                        "id": "int",

                        // Internal use only. Do not use.
                        "contentId": "guid"
                      }
                    ]
      summary: Halo 5 - CSR Designations
  /h5/metadata/enemies:
    get:
      description: >
        <br />

        <h4>Changelog</h4>

        <div class="panel-body">
            <p><strong>February 21, 2017:</strong></p>
            <ul>
                <li>Renamed Endpoint from "Enemies" to "Halo 5 - Enemies".</li>
                <li>Removed "{title}" Request Parameter.</li>
                <li>Documented "Accept-Language" Request Header.</li>
            </ul>
        </div>

        <div class="panel-body">
            <p><strong>April 20, 2016:</strong></p>
            <ul>
                <li>Corrected documentation typo. The field "largeIconImageUrl" was incorrectly named "lageIconImageUrl".</li>
            </ul>
        </div>
      operationId: Halo-5-Enemies
      parameters:
        - description: The desired language for the Metadata.
          in: header
          name: Accept-Language
          schema:
            type: string
            default: en
      responses:
        "200":
          description: The response body will contain the requested Metadata.
          content:
            application/json:
              examples:
                response:
                  value: >
                    // A list of enemies for the title. There is no significance
                    to the ordering.

                    [
                      {
                        // The faction that this enemy is affiliated with. One of the following options:
                        //   - UNSC
                        //   - Covenant
                        //   - Promethean
                        "faction": "string",

                        // A localized name for the object, suitable for display to users. The text is title
                        // cased. 
                        "name": "string",

                        // A localized description, suitable for display to users. Note: This may be null.
                        "description": "string",

                        // A reference to a large image for icon use. This may be null if there is no image
                        // defined.
                        "largeIconImageUrl": "string",

                        // A reference to a small image for icon use. This may be null if there is no image
                        // defined.
                        "smallIconImageUrl": "string",

                        // The ID that uniquely identifies this enemy.
                        "id": "int",

                        // Internal use only. Do not use.
                        "contentId": "guid"
                      }
                    ]
      summary: Halo 5 - Enemies
  /h5/metadata/flexible-stats:
    get:
      description: >
        <br />

        <h4>Changelog</h4>

        <div class="panel-body">
            <p><strong>February 21, 2017:</strong></p>
            <ul>
                <li>Renamed Endpoint from "Flexible Stats" to "Halo 5 - Flexible Stats".</li>
                <li>Removed "{title}" Request Parameter.</li>
