{
	"openapi": "3.0.0",
	"info": {
		"description": "This is the Admin API Documentation of Materia Server (@materia/server).",
		"version": "1.0.0",
		"title": "Materia Server",
		"contact": {
			"email": "team@getmateria.com"
		},
		"license": {
			"name": "Apache 2.0",
			"url": "http://www.apache.org/licenses/LICENSE-2.0.html"
		}
	},
	"tags": [{
			"name": "Actions",
			"description": "Manage actions/hooks on queries/endpoints events",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		},
		{
			"name": "Addons",
			"description": "Manage addons in your Materia application",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		},
		{
			"name": "Api",
			"description": "Manage the API REST exposed by your Materia application",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		},
		{
			"name": "App",
			"description": "Manage your Materia application",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		},
		{
			"name": "Client",
			"description": "Manage the client/frontend of your Materia application",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		},
		{
			"name": "Database",
			"description": "Check database connection and synchronize with app state",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		},
		{
			"name": "Dependencies",
			"description": "Manage dependencies in your Materia application",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		},
		{
			"name": "Entities",
			"description": "Manage entities in your Materia application",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		},
		{
			"name": "Files",
			"description": "Manage folders and files in your Materia application tree",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		},
		{
			"name": "Git",
			"description": "Manage your git repository",
			"externalDocs": {
				"description": "Find out more",
				"url": "https://getmateria.com"
			}
		}
	],
	"paths": {
		"/infos": {
			"get": {
				"tags": [
					"App"
				],
				"summary": "Get current Materia application infos",
				"responses": {
					"200": {
						"description": "Success",
						"content": {
							"application/json": {
								"schema": {
									"properties": {
										"id": {
											"type": "string",
											"example": "WhEZXlzIaXU="
										},
										"package": {
											"type": "string",
											"example": "my-materia-app"
										},
										"name": {
											"type": "string",
											"example": "My Materia App"
										},
										"path": {
											"type": "string",
											"example": "C:\\Users\\USER\\Documents\\materia\\apps\\my-materia-app"
										},
										"icon": {
											"type": "string",
											"example": "green-500"
										},
										"hasStatic": {
											"type": "boolean",
											"example": "false"
										},
										"url": {
											"type": "string",
											"example": "http://localhost:8080"
										},
										"mode": {
											"type": "string",
											"example": "dev"
										},
										"addons": {
											"type": "array",
											"example": []
										},
										"entities": {
											"type": "array",
											"example": []
										},
										"relations": {
											"type": "array",
											"example": []
										},
										"api": {
											"type": "array",
											"example": []
										},
										"permissions": {
											"type": "array",
											"example": []
										},
										"controllers": {
											"type": "array",
											"example": []
										},
										"models": {
											"type": "array",
											"example": []
										},
										"server": {
											"type": "object",
											"example": {
												"dev": {
													"host": "localhost",
													"port": "8080"
												}
											}
										},
										"client": {
											"type": "object",
											"example": {}
										},
										"database": {
											"type": "object",
											"example": {
												"disabled": true,
												"config": {}
											}
										},
										"live": {
											"type": "object",
											"example": {
												"url": null
											}
										}
									}
								}
							}
						}
					}
				}
			}
		},
		"/infos/minimal": {
			"get": {
				"tags": [
					"App"
				],
				"summary": "Get current Materia application minimal infos",
				"responses": {
					"200": {
						"description": "Success",
						"content": {
							"application/json": {
								"schema": {
									"properties": {
										"id": {
											"type": "string",
											"example": "WhEZXlzIaXU="
										},
										"package": {
											"type": "string",
											"example": "my-materia-app"
										},
										"name": {
											"type": "string",
											"example": "My Materia App"
										},
										"path": {
											"type": "string",
											"example": "C:\\Users\\USER\\Documents\\materia\\apps\\my-materia-app"
										},
										"icon": {
											"type": "string",
											"example": "green-500"
										},
										"hasStatic": {
											"type": "boolean",
											"example": false
										},
										"url": {
											"type": "string",
											"example": "http://localhost:8080"
										},
										"mode": {
											"type": "string",
											"example": "dev"
										},
										"addons": {
											"type": "array",
											"example": []
										}
									}
								}
							}
						}
					}
				}
			}
		},
		"/token": {
			"post": {
				"tags": [
					"App"
				],
				"summary": "Get authorization token for Materia Admin API",
				"responses": {
					"200": {
						"description": "Success",
						"content": {
							"application/json": {
								"schema": {
									"properties": {
										"access_token": {
											"type": "string",
											"example": "0g3Nlfh6wi0dX8KGP/BM+au/eo1KSF4TuoXsXLvw0nQ="
										}
									}
								}
							}
						}
					},
					"500": {
						"description": "Internal server error"
					}
				},
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"properties": {
									"client_id": {
										"type": "string",
										"description": "Your Materia admin login",
										"example": "admin"
									},
									"client_secret": {
										"type": "string",
										"description": "Your Materia admin root password",
										"example": "@SeCURedPassWORD"
									},
									"grant_type": {
										"type": "string",
										"example": "client_credentials"
									}
								}
							},
							"examples": {
								"one": {
									"value": "My Super Aplication name"
								}
							}
						}
					}
				},
				"security": []
			}

		},
		"/restart": {
			"post": {
				"tags": [
					"App"
				],
				"summary": "Restart currently launched app",
				"responses": {
					"200": {
						"description": "Success",
						"content": {
							"application/json": {
								"schema": {
									"properties": {
										"message": {
											"type": "string",
											"example": "Shutting down the server... waiting for restart."
										}
									}
								}
							}
						}
					}
				}
			}
		},
		"/config": {
			"post": {
				"tags": ["App"],
				"summary": "Set application settings",
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"type": "object",
								"example": {
									"general": {
										"name": "Materia Application",
										"icon": "green-500",
										"package": "materia-application",
										"path": "c:\\Users\\USER\\Documents\\materia\\apps\\materia-application"
									},
									"server": {
										"dev": {
											"host": "localhost",
											"port": 8083,
											"ssl": false
										}
									}
								}
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Success",
						"content": {
							"application/json": {
								"schema": {
									"type": "object",
									"example": {
										"general": {
											"name": "Materia Application",
											"icon": "green-500",
											"package": "materia-application",
											"path": "c:\\Users\\USER\\Documents\\materia\\apps\\materia-application"
										},
										"server": {
											"dev": {
												"host": "localhost",
												"port": 8083,
												"ssl": false
											}
										},
										"database": {
											"type": "sqlite",
											"storage": "database.sqlite"
										}
									}
								}
							}
						}
					},
					"500": {
						"description": "Internal serveur error"
					},
					"404": {
						"description": "File not found"
					}
				}

			},
			"delete": {
				"tags": ["App"],
				"summary": "Delete an application setting",
				"parameters": [{
						"name": "type",
						"in": "query",
						"required": true,
						"description": "The setting type to delete ('client' or 'database')",
						"schema": {
							"type": "string"
						}
					},
					{
						"name": "mode",
						"required": true,
						"in": "query",
						"description": "The setting environment mode ('dev' or 'prod')",
						"schema": {
							"type": "string"
						}
					}
				],
				"responses": {
					"500": {
						"description": "Internal serveur error"
					},
					"404": {
						"description": "File not found"
					}
				}
			}

		},
		"/files/isDirectory/:path": {
			"get": {
				"tags": [
					"Files"
				],
				"summary": "Retrieve information of whether an item in the file tree is a folder or file",
				"operationId": "isFileTreeItemDirectory",
				"parameters": [{
					"name": "path",
					"in": "path",
					"required": true,
					"description": "The relative or absolute path of the file tree element",
					"schema": {
						"type": "string"
					}
				}],
				"responses": {
					"500": {
						"description": "Internal serveur error"
					},
					"404": {
						"description": "File not found"
					}
				}
			}
		},
		"/files": {
			"get": {
				"tags": [
					"Files"
				],
				"summary": "Retrieve a file or folder content",
				"operationId": "getFileOrFolder",
				"parameters": [{
						"name": "path",
						"in": "query",
						"required": true,
						"description": "The relative or absolute path of the file tree element",
						"schema": {
							"type": "string"
						}
					},
					{
						"name": "depth",
						"in": "query",
						"required": false,
						"description": "Depth of the file tree structure if the item is a folder",
						"schema": {
							"type": "number"
						}
					}
				],
				"responses": {
					"500": {
						"description": "Internal serveur error"
					},
					"404": {
						"description": "File not found"
					}
				}
			},
			"post": {
				"tags": [
					"Files"
				],
				"summary": "Add a file or folder in your application tree",
				"description": "",
				"operationId": "addFileOrFolder",
				"parameters": [{
					"name": "path",
					"in": "query",
					"description": "Relative or absolute path where the item will be created",
					"required": true,
					"schema": {
						"type": "string"
					}
				}],
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"properties": {
									"isDir": {
										"type": "boolean",
										"description": "Whether or not the new item is a directory",
										"example": false
									},
									"content": {
										"description": "The content if the new item is a file",
										"type": "string",
										"example": "Hello world"
									}
								}
							}
						}
					}
				},
				"responses": {
					"500": {
						"description": "Internal serveur error"
					},
					"404": {
						"description": "File not found"
					}
				}
			},
			"put": {
				"tags": [
					"Files"
				],
				"summary": "Move an existing file or folder in your application tree",
				"description": "",
				"operationId": "moveFile",
				"parameters": [{
					"name": "path",
					"in": "query",
					"schema": {
						"type": "string"
					},
					"required": true,
					"description": "Relative or absolute path of the item to be move"
				}],
				"responses": {
					"500": {
						"description": "Internal server error"
					}
				},
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"properties": {
									"newPath": {
										"type": "string",
										"description": "Relative or absolute path where the item will be moved",
										"example": "hello_world2.txt"
									}
								}
							}
						}
					},
					"required": true
				}
			},
			"delete": {
				"tags": [
					"Files"
				],
				"summary": "Delete a file or folder from your application tree",
				"parameters": [{
					"name": "path",
					"in": "query",
					"description": "Relative or absolute path of the tree item to be delete",
					"required": true,
					"schema": {
						"type": "string"
					}
				}],
				"responses": {
					"500": {
						"description": "Internal server error"
					}
				}
			}
		},
		"/controllers": {
			"get": {
				"tags": ["Api"],
				"summary": "Get the list of controllers existing in your application",
				"responses": {
					"200": {
						"description": "Success"
					}
				}
			}
		},
		"/controllers/:name": {
			"get": {
				"tags": ["Api"],
				"summary": "Get the content of a controller",
				"responses": {
					"200": {
						"description": "Success",
						"content": {
							"text/plain": {
								"schema": {
									"type": "string",
									"example": "export DefaultCtrl {\n  constructor(app){\n    this.app = app \n   }\n } \n module.exports = DefaultCtrl"
								}

							}
						}
					},
					"500": {
						"description": "Internal server error"
					}
				},
				"parameters": [{
					"name": "name",
					"in": "path",
					"required": true,
					"description": "Name of the controller you want to retrieve the content",
					"schema": {
						"type": "string"
					}
				}]
			}
		},
		"/endpoints": {
			"get": {
				"tags": ["Api"],
				"summary": "Get the list of endpoints existing in your application",
				"responses": {
					"200": {
						"description": "Success"
					}
				}
			},
			"post": {
				"tags": ["Api"],
				"summary": "Add a new endpoint",
				"responses": {
					"200": {
						"description": "Success"
					}
				},
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"properties": {
									"endpoint": {
										"properties": {
											"method": {
												"type": "string",
												"example": "get"
											},
											"url": {
												"type": "string",
												"example": "/todos"
											},
											"controller": {
												"type": "string",
												"example": "default"
											},
											"action": {
												"type": "string",
												"example": "getTodos"
											},
											"query": {
												"type": "object",
												"example": {
													"entity": "todos",
													"id": "findAll"
												}
											},
											"params": {
												"type": "array",
												"example": []
											},
											"permissions": {
												"type": "array",
												"example": ["Anyone"]
											}
										}
									},
									"code": {
										"type": "string",
										"example": "class defaultCtrl { constructor(app) { this.app = app; } getTodos(req, res, next) { return this.app.entities.get('todos').getQuery('findAll').run() } } module.exports = defaultCtrl; "
									},
									"options": {
										"type": "object",
										"example": {"save": true}
									}
								}
							}
						}
					}
				}
			},
			"put": {
				"tags": ["Api"],
				"summary": "Update an endpoint",
				"responses": {
					"200": {
						"description": "Success"
					}
				},
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"properties": {
									"newEndpoint": {
										"properties": {
											"method": {
												"type": "string",
												"example": "get"
											},
											"url": {
												"type": "string",
												"example": "/todos2"
											},
											"controller": {
												"type": "string",
												"example": "default"
											},
											"action": {
												"type": "string",
												"example": "getTodos"
											},
											"query": {
												"type": "object",
												"example": {
													"entity": "todos",
													"id": "findAll"
												}
											},
											"params": {
												"type": "array",
												"example": []
											},
											"permissions": {
												"type": "array",
												"example": ["Anyone"]
											}
										}
									},
									"code": {
										"type": "string",
										"example": "class defaultCtrl { constructor(app) { this.app = app; } getTodos(req, res, next) { return this.app.entities.get('todos').getQuery('findAll').run() } } module.exports = defaultCtrl; "
									},
									"options": {
										"type": "object",
										"example": {"save": true}
									},
									"oldEnpointId": {
										"type": "array",
										"example": ["get", "/todos"]
									}
								}
							}
						}
					}
				}
			}
		},
		"/endpoints/generate": {
			"post": {
				"tags": ["Api"],
				"summary": "Generate default endpoints (GET/POST/PUT/DELETE) for a given entity",
				"responses": {
					"200": {
						"description": "Success"
					}
				}
			}
		},
		"/endpoints/:id": {
			"delete": {
				"tags": ["Api"],
				"summary": "Delete an enpoint",
				"parameters": [{
					"name": "id",
					"in": "path",
					"description": "The unique identifier (btoa(method + url)) of the endpoint to delete.",
					"required": true,
					"schema": {
						"type": "string"
					}
				}],
				"responses": {
					"200": {
						"description": "Success"
					}
				}
			}
		},
		"/git": {
			"get": {
				"tags": ["Git"],
				"summary": "Load current git repository infos (working copy, branches, history ..)",
				"responses": {
					"200": {
						"description": "Success"
					}
				}
			}
		},
		"/git/statuses": {
			"get": {
				"tags": ["Git"],
				"summary": "Get current diffs",
				"responses": {
					"200": {
						"description": "Success"
					}
				}
			}
		},
		"/git/history": {
			"get": {
				"tags": ["Git"],
				"summary": "Get git repository history",
				"responses": {
					"200": {
						"description": "Success"
					}
				}
			}
		},
		"/git/history/:hash": {
			"get": {
				"tags": ["Git"],
				"summary": "Get git repository commit content from history",
				"responses": {
					"200": {
						"description": "Success"
					}
				},
				"parameters": [{
					"name": "hash",
					"in": "path",
					"description": "Commit hash",
					"required": true,
					"schema": {
						"type": "string"
					}
				}]
			}
		},
		"/git/history/:hash/file": {
			"get": {
				"tags": ["Git"],
				"summary": "Get git repository commit file content from history",
				"responses": {
					"200": {
						"description": "Success"
					}
				},
				"parameters": [{
						"name": "hash",
						"in": "path",
						"description": "Commit hash",
