{
    "openapi": "3.0.0",
    "info": {
        "version": "1.0.0",
        "title": "TEI Publisher API",
        "description": "This is the core API for TEI Publisher 7. It describes all endpoints used by TEI Publisher web components, plus additional operations added for external access."
    },
    "servers": [
        {
            "description": "Endpoint for testing on localhost",
            "url":"http://localhost:8080/exist/apps/ProHD"
        }
    ],
    "tags": [
        {
            "name": "documents",
            "description": "Retrieve document content transformed to target format"
        },
        {
            "name": "collection",
            "description": "Browse the collection hierarchy and upload new files"
        },
        {
            "name": "odd",
            "description": "Manage the ODD files stored in TEI Publisher"
        },
        {
            "name": "search",
            "description": "Searching the collection"
        },
        {
            "name": "transform",
            "description": "Transform content on the fly"
        },
        {
            "name": "view",
            "description": "View documents via an HTML template"
        },
        {
            "name": "apps",
            "description": "Operations to generate/manage apps"
        },
        {
            "name": "dts",
            "description": "Implementation of the DTS (Distributed Text Services) API"
        },
        {
            "name": "info",
            "description": "Information about the server"
        }
    ],
    "components": {
        "securitySchemes": {
            "basicAuth": {
                "type": "http",
                "scheme": "basic"
            },
            "cookieAuth": {
                "type": "apiKey",
                "name": "teipublisher.com.login",
                "in": "cookie"
            }
        }
    },
    "paths": {
        "/{file}.html": {
            "get": {
                "summary": "Retrieve HTML page",
                "description": "Retrieve an HTML page expanded through eXist templating. This is used to load the top-level HTML pages, e.g. `index.html`.",
                "tags": [ "view" ],
                "operationId": "vapi:html",
                "x-error-handler": "vapi:handle-error",
                "parameters": [
                    {
                        "name": "file",
                        "in": "path",
                        "required": true,
                        "schema":{
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "HTML of the page",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "The document was not found",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/{doc}": {
            "get": {
                "summary": "Retrieve the HTML template used for displaying a document",
                "description": "Get the HTML template associated with the given document. This is called whenever the users tries to view a document. The actual content of the document will then be loaded by the template.",
                "tags": [ "view" ],
                "operationId": "vapi:view",
                "x-error-handler": "vapi:handle-error",
                "parameters": [
                    {
                        "name": "doc",
                        "in": "path",
                        "description": "Relative path to the document",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "example": "test/orlik_to_serafin.xml"
                        },
                        "allowReserved": true
                    },
                    {
                        "name": "template",
                        "in": "query",
                        "description": "Name of the template to be used. Overwrites any default template setting.",
                        "schema":{
                            "type": "string"
                        }
                    },
                    {
                        "name": "view",
                        "in": "query",
                        "schema": {
                            "type": "string",
                            "enum": [
                                "page",
                                "div",
                                "single"
                            ]
                        },
                        "example": "div",
                        "description": "The view type used by the main view which displays the document, e.g. 'page' or 'div'. This has an influence on the generated links, which need to differ when linking to a page rather than a section."
                    },
                    {
                        "name": "odd",
                        "in": "query",
                        "schema":{
                            "type": "string"
                        }
                    },
                    {
                        "name": "action",
                        "in": "query",
                        "schema":{
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "HTML view for the document",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "The document was not found",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "An error occurred",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/{doc}.md": {
            "get": {
                "summary": "Retrieve the HTML template for a markdown file and render it",
                "description": "Get the HTML template associated with the given markdown document. This is called whenever the users tries to view a markdown file. The actual content of the document will then be loaded by the template.",
                "tags": [ "view" ],
                "operationId": "vapi:view",
                "x-error-handler": "vapi:handle-error",
                "parameters": [
                    {
                        "name": "doc",
                        "in": "path",
                        "description": "Relative path to the document",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "example": "about.md"
                        },
                        "allowReserved": true
                    },
                    {
                        "name": "template",
                        "in": "query",
                        "description": "Name of the template to be used. Overwrites any default template setting.",
                        "schema":{
                            "type": "string",
                            "default": "markdown.html"
                        }
                    },
                    {
                        "name": "suffix",
                        "in": "query",
                        "schema":{
                            "type": "string",
                            "default": ".md"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "HTML view for the document",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "The document was not found",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "An error occurred",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/version": {
            "get": {
                "summary": "Get version information about this service",
                "description": "Get the version of TEI Publisher running this service as well as the API version implemented",
                "operationId": "iapi:version",
                "tags": [ "info" ],
                "responses": {
                    "200":{
                        "description": "Version information about this service",
                        "content": {
                            "application/json":{
                                "schema":{
                                    "type": "object",
                                    "properties": {
                                        "api":{
                                            "type": "string"
                                        },
                                        "app": {
                                            "type": "object",
                                            "properties": {
                                                "name":{
                                                    "type": "string"
                                                },
                                                "version":{
                                                    "type": "string"
                                                }
                                            }
                                        },
                                        "engine": {
                                            "type": "object",
                                            "properties": {
                                                "name": {
                                                    "type": "string"
                                                },
                                                "version": {
                                                    "type": "string"
                                                }
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/collection/{path}": {
            "get": {
                "summary": "Get formatted overview of documents in the collection",
                "tags": [ "collection" ],
                "operationId": "capi:list",
                "parameters": [
                    {
                        "name": "path",
                        "in": "path",
                        "required": true,
                        "schema":{
                            "type": "string",
                            "example": "test"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Returns HTML describing the collection",
                        "content":{
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/collection": {
            "get": {
                "summary": "Get formatted overview of documents in the collection",
                "tags": [
                    "collection"
                ],
                "operationId": "capi:list",
                "responses": {
                    "200": {
                        "description": "Returns HTML describing the collection",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/upload": {
            "post": {
                "summary": "Upload a number of files to the top-level data collection of the app",
                "tags": [
                    "collection"
                ],
                "operationId": "capi:upload",
                "x-constraints": {
                    "group": "tei"
                },
                "requestBody": {
                    "content": {
                        "multipart/form-data": {
                            "schema": {
                                "type": "object",
                                "properties": {
                                    "files[]": {
                                        "type": "array",
                                        "items": {
                                            "type": "string",
                                            "format": "binary"
                                        }
                                    }
                                }
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "Upload response",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "properties": {
                                        "name": {
                                            "type": "string"
                                        },
                                        "path": {
                                            "type": "string"
                                        },
                                        "type": {
                                            "type": "string"
                                        },
                                        "size": {
                                            "type": "number"
                                        }
                                    }
                                }
                            }
                        }
                    },
                    "401": {
                        "description": "Permission denied",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object",
                                    "properties": {
                                        "description": {
                                            "type": "string"
                                        },
                                        "details": {
                                            "type": "object",
                                            "nullable": true
                                        }
                                    }
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "Upload collection not found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "Upload collection not found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/upload/{collection}": {
            "post": {
                "summary": "Upload a number of files",
                "tags": [
                    "collection"
                ],
                "operationId": "capi:upload",
                "x-constraints": {
                    "group": "tei"
                },
                "parameters": [
                    {
                        "name": "collection",
                        "in": "path",
                        "schema":{
                            "type": "string"
                        },
                        "required": true
                    }
                ],
                "requestBody":{
                    "content": {
                        "multipart/form-data": {
                            "schema":{
                                "type": "object",
                                "properties": {
                                    "files[]": {
                                        "type": "array",
                                        "items":{
                                            "type": "string",
                                            "format": "binary"
                                        }
                                    }
                                }
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "Upload response",
                        "content": {
                            "application/json": {
                                "schema":{
                                    "properties": {
                                        "name": {
                                            "type": "string"
                                        },
                                        "path": {
                                            "type": "string"
                                        },
                                        "type": {
                                            "type": "string"
                                        },
                                        "size": {
                                            "type": "number"
                                        }
                                    }
                                }
                            }
                        }
                    },
                    "401": {
                        "description": "Permission denied",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object",
                                    "properties": {
                                        "description": {
                                            "type": "string"
                                        },
                                        "details": {
                                            "type": "object",
                                            "nullable": true
                                        }
                                    }
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "Upload collection not found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object"
                                }
                            }
                        }
                    },
                    "500": {
                        "description": "Upload collection not found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/document/{id}": {
            "get": {
                "summary": "Get the source of a document",
                "description": "Get the source of a document, either as XML, text or binary.",
                "tags": ["documents"],
                "operationId": "dapi:source",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "required": true,
                        "schema":{
                            "type": "string"
                        }
                    },
                    {
                        "name": "type",
                        "in": "query",
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Content of the document",
                        "content": {
                            "application/xml": {
                                "schema":{
                                    "type": "string"
                                }
                            },
                            "text/markdown": {
                                "schema":{
                                    "type": "string"
                                }
                            },
                            "text/text": {
                                "schema":{
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "410": {
                        "description": "Document deleted"
                    }
                }

            },
            "delete": {
                "summary": "Delete a document",
                "tags": ["documents"],
                "operationId": "dapi:delete",
                "x-constraints": {
                    "group": "tei"
                },
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "required": true,
                        "schema":{
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "410": {
                        "description": "Document deleted"
                    }
                }
            }
        },
        "/api/document/{id}/html": {
            "get": {
                "summary": "Retrieve document content as HTML",
                "description": "Retrieve whole document transformed to HTML via ODD",
                "tags": [
                    "documents"
                ],
                "operationId": "dapi:html",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "example": "test/F-rom.xml"
                        }
                    },
                    {
                        "name": "odd",
                        "in": "query",
                        "schema": {
                            "type": "string",
                            "example": "shakespeare.odd"
                        }
                    },
                    {
                        "name": "base",
                        "description": "Base URI to use",
                        "in": "query",
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "wc",
                        "description": "Include TEI Publisher webcomponents",
                        "in": "query",
                        "schema": {
                            "type": "boolean",
                            "default": true
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Returns the entire document as HTML",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "Document not found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/document/{id}/pdf": {
            "get": {
                "summary": "Retrieve document content as PDF",
                "description": "Retrieve whole document transformed via ODD and FO",
                "tags": [
                    "documents"
                ],
                "operationId": "dapi:pdf",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "example": "test/F-rom.xml"
                        },
                        "description": "Path to the XML document"
                    },
                    {
                        "name": "source",
                        "in": "query",
                        "schema": {
                            "type": "boolean",
                            "default": false
                        },
                        "description": "Set to true to see generated XML FO source"
                    },
                    {
                        "name": "token",
                        "in": "query",
                        "schema": {
                            "type": "string",
                            "nullable": true
                        }
                    },
                    {
                        "name": "cache",
                        "in": "query",
                        "schema":{
                            "type": "boolean",
                            "default": false
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Returns a PDF",
                        "content": {
                            "application/pdf": {
                                "schema": {
                                    "type": "string",
                                    "format": "binary"
                                }
                            },
                            "application/xml": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "Document not found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/document/{id}/tex": {
            "get": {
                "summary": "Retrieve document content as PDF",
                "description": "Retrieve whole document transformed via ODD and LaTeX",
                "tags": [ "documents" ],
                "operationId": "dapi:latex",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "example": "test/F-rom.xml"
                        }
                    },
                    {
                        "name": "source",
                        "in": "query",
                        "schema":{
                            "type": "boolean",
                            "default": false
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Returns a PDF",
                        "content": {
                            "application/pdf": {
                                "schema": {
                                    "type": "string",
                                    "format": "binary"
                                }
                            },
                            "application/x-latex": {
                                "schema":{
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "Document not found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/document/{id}/epub": {
            "get": {
                "summary": "Retrieve document content as EPub",
                "description": "Retrieve whole document as EPub transformed via ODD",
                "tags": [
                    "documents"
                ],
                "operationId": "dapi:epub",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "example": "doc/documentation.xml"
                        }
                    },
                    {
                        "name": "token",
                        "in": "query",
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "lang",
                        "in": "query",
                        "schema":{
                            "type": "string"
                        }
                    },
                    {
                        "name": "odd",
                        "in": "query",
                        "schema": {
                            "type": "string",
                            "example": "docbook.odd"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Returns an ePUB",
                        "content": {
                            "application/epub+zip": {
                                "schema": {
                                    "type": "string",
                                    "format": "binary"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "Document not found",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/document/{id}/contents": {
            "get": {
                "summary": "Retrieve a formatted table of contents for the document",
                "description": "Retrieve a formatted table of contents for the document, transformed via ODD",

                "tags": [
                    "documents"
                ],
                "operationId": "dapi:table-of-contents",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "example": "test/kant_rvernunft_1781.TEI-P5.xml"
                        }
                    },
                    {
                        "name": "odd",
                        "in": "query",
                        "schema": {
                            "type": "string",
                            "example": "teipublisher.odd"
                        }
                    },
                    {
                        "name": "view",
                        "in": "query",
                        "schema": {
                            "type": "string",
                            "enum": [
                                "page",
                                "div",
                                "single"
                            ]
                        },
                        "example": "div",
                        "description": "The view type used by the main view which displays the document, e.g. 'page' or 'div'. This has an influence on the generated links, which need to differ when linking to a page rather than a section."
                    },
                    {
                        "name": "target",
                        "in": "query",
                        "schema":{
                            "type": "string"
                        },
                        "description": "The target channel into which link selection events should be send (if the user clicks on a link)"
                    },
                    {
                        "name": "icons",
                        "in": "query",
                        "schema":{
                            "type": "boolean",
                            "default": true
                        },
                        "description": "Should an expand/collapse icon be displayed next to headings having nested child sections?"
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Returns the formatted table of contents as HTML",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        }
                    },
                    "404": {
                        "description": "Document not found",
                        "content": {
                            "application/json": {
                                "schema":{
                                    "type": "object"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/api/parts/{doc}/{format}": {
            "get": {
                "summary": "Retrieve parts of a document transformed via ODD",
                "description": "Retrieve a document fragment, usually a division or page, transformed to HTML via the ODD. This operation is internally used by pb-view, but can also be helpful with pb-load or in other contexts.",
                "operationId": "dapi:get-fragment",
                "tags": [ "documents" ],
                "parameters": [
                    {
                        "name": "doc",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string"
                        },
                        "example": "doc/documentation.xml",
                        "description": "path to the document, relative to TEI Publisher's data root colletion"
                    },
                    {
                        "name": "format",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "enum": [
                                "json",
                                "html"
                            ]
                        },
                        "description": "the format to return: either `json` or `html`. The first returns the HTML inside a JSON record with additional information about the next/previous page and so on. The latter is mainly for debugging purposes."
                    },
                    {
                        "name": "odd",
                        "in": "query",
                        "schema": {
                            "type": "string",
                            "example": "shakespeare.odd"
                        }
                    },
                    {
                        "name": "view",
                        "in": "query",
                        "schema":{
                            "type": "string",
                            "enum": [
