            ]
          },
          "Max": {
            "allOf": [
              { "$ref": "#/components/schemas/String" },
              {
                "description": "For a numeric field, the maximum value in the field."
              }
            ]
          },
          "Avg": {
            "allOf": [
              { "$ref": "#/components/schemas/Double" },
              {
                "description": "For a numeric field, the average value in the field."
              }
            ]
          },
          "Stddev": {
            "allOf": [
              { "$ref": "#/components/schemas/Double" },
              { "description": "For a numeric field, the standard deviation." }
            ]
          },
          "CountLong": {
            "allOf": [
              { "$ref": "#/components/schemas/Long" },
              {
                "description": "The number of values in the field. <code>CountLong</code> is used instead of <code>Count</code> if the value is greater than 2,147,483,647."
              }
            ]
          },
          "CountDistinctLong": {
            "allOf": [
