        "description": "Specifies the schedule you want your task to use for repeated executions. For more information, see <a href=\"https://docs.aws.amazon.com/AmazonCloudWatch/latest/events/ScheduledEvents.html\">Schedule Expressions for Rules</a>."
      },
      "TaskArn": {
        "type": "string",
        "pattern": "^arn:(aws|aws-cn|aws-us-gov|aws-iso|aws-iso-b):datasync:[a-z\\-0-9]*:[0-9]{12}:task/task-[0-9a-f]{17}$",
        "maxLength": 128
      },
      "Time": { "type": "string", "format": "date-time" },
      "EndpointType": {
        "type": "string",
        "enum": ["PUBLIC", "PRIVATE_LINK", "FIPS"]
      },
      "PrivateLinkConfig": {
        "type": "object",
        "properties": {
          "VpcEndpointId": {
            "allOf": [
              { "$ref": "#/components/schemas/VpcEndpointId" },
              {
                "description": "The ID of the VPC endpoint that is configured for an agent. An agent that is configured with a VPC endpoint will not be accessible over the public internet."
              }
            ]
