          "PrivateLinkEndpoint": {
            "allOf": [
              { "$ref": "#/components/schemas/Endpoint" },
              {
                "description": "The private endpoint that is configured for an agent that has access to IP addresses in a <a href=\"https://docs.aws.amazon.com/vpc/latest/userguide/endpoint-service.html\">PrivateLink</a>. An agent that is configured with this endpoint will not be accessible over the public internet."
              }
            ]
          },
          "SubnetArns": {
            "allOf": [
              { "$ref": "#/components/schemas/PLSubnetArnList" },
              {
                "description": "The Amazon Resource Names (ARNs) of the subnets that are configured for an agent activated in a VPC or an agent that has access to a VPC endpoint."
              }
            ]
          },
          "SecurityGroupArns": {
            "allOf": [
              { "$ref": "#/components/schemas/PLSecurityGroupArnList" },
              {
                "description": "The Amazon Resource Names (ARNs) of the security groups that are configured for the EC2 resource that hosts an agent activated in a VPC or an agent that has access to a VPC endpoint."
              }
            ]
          }
        },
        "description": "The VPC endpoint, subnet, and security group that an agent uses to access IP addresses in a VPC (Virtual Private Cloud)."
