    "/login/{silo_name}/local": {
      "post": {
        "tags": [
          "login"
        ],
        "summary": "Authenticate a user (i.e., log in) via username and password",
        "operationId": "login_local",
        "parameters": [
          {
            "in": "path",
            "name": "silo_name",
            "required": true,
