              "schema": {
                "$ref": "#/components/schemas/UsernamePasswordCredentials"
              }
            }
          },
          "required": true
        },
        "responses": {
          "303": {
            "description": "redirect (see other)",
            "headers": {
              "location": {
                "description": "HTTP \"Location\" header",
                "style": "simple",
                "required": true,
                "schema": {
                  "type": "string"
                }
              }
            }
          },
          "4XX": {
            "$ref": "#/components/responses/Error"
          },
          "5XX": {
            "$ref": "#/components/responses/Error"
          }
        }
      }
    },
    "/login/{silo_name}/saml/{provider_name}": {
      "get": {
        "tags": [
          "login"
        ],
        "summary": "Prompt user login",
        "description": "Either display a page asking a user for their credentials, or redirect them to their identity provider.",
        "operationId": "login_saml_begin",
        "parameters": [
          {
            "in": "path",
            "name": "provider_name",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/Name"
            }
          },
          {
            "in": "path",
            "name": "silo_name",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/Name"
            }
          }
        ],
        "responses": {
          "302": {
            "description": "redirect (found)",
            "headers": {
              "location": {
                "description": "HTTP \"Location\" header",
                "style": "simple",
                "required": true,
                "schema": {
                  "type": "string"
                }
              }
            }
          },
          "4XX": {
            "$ref": "#/components/responses/Error"
          },
          "5XX": {
            "$ref": "#/components/responses/Error"
          }
        }
      },
      "post": {
        "tags": [
          "login"
        ],
        "summary": "Authenticate a user (i.e., log in) via SAML",
        "operationId": "login_saml",
        "parameters": [
          {
            "in": "path",
            "name": "provider_name",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/Name"
            }
          },
          {
            "in": "path",
            "name": "silo_name",
            "required": true,
            "schema": {
              "$ref": "#/components/schemas/Name"
            }
          }
        ],
        "requestBody": {
          "content": {
            "application/octet-stream": {
              "schema": {
                "type": "string",
                "format": "binary"
              }
            }
          },
          "required": true
        },
        "responses": {
          "303": {
            "description": "redirect (see other)",
            "headers": {
              "location": {
                "description": "HTTP \"Location\" header",
                "style": "simple",
                "required": true,
                "schema": {
                  "type": "string"
                }
              }
            }
          },
          "4XX": {
            "$ref": "#/components/responses/Error"
          },
          "5XX": {
            "$ref": "#/components/responses/Error"
          }
        }
      }
    },
