info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Logpoint Logpoint
  title: Logpoint Logpoint
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: 'Add a comments to incidents. '
      parameters:
      - description: The version
        example: '0.1'
        in: query
        name: Version
        required: true
        schema: {type: string}
      - description: Comma separated list of the Incident Object IDs.
        example: 5a62bd8cce983de89085429c
        in: query
        name: Incident Object IDs
        required: true
        schema: {type: string}
      - description: Comma separated list of the comments to add to each incident.
        example: Comment1
        in: query
        name: Comments
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: LogPoint Add Incident Comment
  tmp1:
    post:
      description: Assign or Reassign incident.
      parameters:
      - description: The version
        example: '0.1'
        in: query
        name: Version
        required: true
        schema: {type: string}
      - description: Comma separated list of the Incident Object IDs
        example: 5a62bd8cce983de89085429c,5b62bd7cce983de89085429e
        in: query
        name: Incident Object IDs
        required: true
        schema: {type: string}
      - description: The ID of the new assignee
        example: 59b0eecfd8aaa4334ee41707
        in: query
        name: New Assignee
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: LogPoint Assign or Reassign Incident
  tmp10:
    post:
      description: Retrieve a user's timezone
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp10'}
          description: Successful request
      summary: Logpoint Get User Timezone
  tmp11:
    post:
      description: Get users and user groups involved in any LogPoint recorded incidents.
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp11'}
          description: Successful request
      summary: LogPoint Get Users
  tmp12:
    post:
      description: Reopen incident by Incident ID
      parameters:
      - description: The version
        example: '0.1'
        in: query
        name: Version
        required: true
        schema: {type: string}
      - description: Comma separated list of the Incident IDs
        example: 5a62bd8cce983de89085429c,5b62bd7cce983de89085429e
        in: query
        name: Incident Object IDs
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp12'}
          description: Successful request
      summary: LogPoint Reopen Incident
  tmp13:
    post:
      description: Resolve an incident
      parameters:
      - description: The version
        example: '0.1'
        in: query
        name: Version
        required: true
        schema: {type: string}
      - description: Comma separated list of the Incident IDs
        example: 5a62bd8cce983de89085429c,5b62bd7cce983de89085429e
        in: query
        name: Incident Object IDs
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp13'}
          description: Successful request
      summary: LogPoint Resolve Incident
  tmp14:
    post:
      description: Run Ad Hoc Query/Get Search Results
      parameters:
      - description: Repositories
        example: _logpoint or '[]'/empty list for all repos
        in: query
        name: Repos
        required: true
        schema: {type: string}
      - description: Limit
        example: '10'
        in: body
        name: Limit
        required: true
        schema: {type: string}
      - description: Time Range
        example: '''Last 24 hours'''
        in: query
        name: Time Range
        required: true
        schema: {type: string}
      - description: Query
        example: user=*
        in: query
        name: Query
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp14'}
          description: Successful request
      summary: Logpoint Run Adhoc Query
  tmp2:
    post:
      description: Close an incident
      parameters:
      - description: The version
        example: '0.1'
        in: query
        name: Version
        required: true
        schema: {type: string}
      - description: Comma separated list of the Incident IDs
        example: 5a62bd8cce983de89085429c,5b62bd7cce983de89085429e
        in: query
        name: Incident Object  IDs
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: LogPoint Close Incident
  tmp3:
    post:
      description: Retrieve Devices
      parameters:
      - description: The maximum number of results to return. Do not enter a limit
          to return all
        example: '100'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: LogPoint Get Devices
  tmp4:
    post:
      description: Retrieve incidents from a specific time range.
      parameters:
      - description: The version
        example: '0.1'
        in: query
        name: Version
        required: true
        schema: {type: string}
      - description: Start of the datetime range to gather incidents. If you want
          to use a relative time, see the README for details.
        example: '2020-01-10T00:00:00Z'
        in: query
        name: Time Start
        required: true
        schema: {type: string}
      - description: End of the datetime range to gather incidents. If you want to
          use a relative time, see the README for details.
        example: '2020-04-21T00:00:00Z'
        in: query
        name: Time End
        required: true
        schema: {type: string}
      - description: The maximum number of results to return. Do not enter a limit
          to return all
        example: '100'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp4'}
          description: Successful request
      summary: LogPoint Get Full Incidents
  tmp5:
    post:
      description: Retrieve information associated with a particular incident
      parameters:
      - description: Object ID of particular incident.
        example: 5e8ffe4621a2303599905b6d
        in: query
        name: Incident Object ID
        required: true
        schema: {type: string}
      - description: The Incident ID of the value field 'incident_id' of the particular
          incident.
        example: a7250944dae828ceadbec826d1a20d6f
        in: query
        name: Incident ID
        required: true
        schema: {type: string}
      - description: Value of field 'detection_timestamp' of the particular incident.
          If you want to use a relative time, see the README for details.
        example: '2020-04-21T00:00:00Z'
        in: query
        name: Date
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp5'}
          description: Successful request
      summary: LogPoint Get Incident
  tmp6:
    post:
      description: Get LiveSearches
      parameters:
      - description: The maximum number of results to return. Do not enter a limit
          to return all
        example: '100'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp6'}
          description: Successful request
      summary: LogPoint Get LiveSearches
  tmp7:
    post:
      description: Retrieve Logpoint(s)
      parameters:
      - description: The maximum number of results to return. Do not enter a limit
          to return all
        example: '100'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp7'}
          description: Successful request
      summary: LogPoint Get LogPoints
  tmp8:
    post:
      description: Retrieve a query by Search ID
      parameters:
      - description: Search Identifier
        example: c970d599-c107-41c1-a54c-a2129eef4062
        in: query
        name: Search ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp8'}
          description: Successful request
      summary: LogPoint Get Query by Search ID
  tmp9:
    post:
      description: Retrieve Repos
      parameters:
      - description: The maximum number of results to return. Do not enter a limit
          to return all
        example: '100'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp9'}
          description: Successful request
      summary: Logpoint Get Repos
tags:
- {name: SIEM & Log Management}
