info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Venafi Trust Protection Platform
  title: Venafi Trust Protection Platform
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Detaches one or more Application objects and corresponding Device
        objects from an existing certificate.
      parameters:
      - description: An array of one or more Application objects, specified by their
          distinguished names, that uniquely identify them in the Venafi platform
        example: bundles.swimlane.io
        in: query
        name: Application DN
        required: true
        schema: {type: string}
      - description: The Distinguished Name (DN) of the certificate
        example: swimlane certificate
        in: query
        name: Certificate DN
        required: true
        schema: {type: string}
      - description: The action to take after dissociating the Application DN and
          device from the Certificate DN
        example: false or true
        in: query
        name: Delete Orphans
        required: true
        schema: {type: boolean}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Venafi Dissociate Object from Certificate
  tmp1:
    post:
      description: Get details about a certificate
      parameters:
      - description: The Config GUID for the Certificate object
        example: '{2b6b673f-2c55-46fc-935a-5836eae9b9db}'
        in: query
        name: GUID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Venafi Get Certificate
  tmp10:
    post:
      description: Marks a key for deletion
      parameters:
      - description: An array of keyset Ids. The numeric keyset identifier of a set
          of valid public and private key pairs. You can get the KeysetId, from POST
          SSH/KeyUsage or the Aperture inventory for SSH keys
        example: '{2649452D57E72135742359561B9E3BD670F28084}'
        in: query
        name: Key ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp10'}
          description: Successful request
      summary: Venafi Remove SSH Key
  tmp11:
    post:
      description: Requests immediate renewal for an existing certificate
      parameters:
      - description: The Distinguished Name (DN) of the certificate to renew
        example: \VED\Policy\Marketing\Zoey
        in: query
        name: Certificate DN
        required: true
        schema: {type: string}
      - description: The PKCS#10 Certificate Signing Request (CSR) to use for the
          renewal. Input should be the CSR as a string
        example: '-----BEGIN CERTIFICATE REQUEST-----MIIFPjC...  -----END CERTIFICATE
          REQUEST-----'
        in: query
        name: PKCS10 As String
        required: false
        schema: {type: string}
      - description: The PKCS#10 Certificate Signing Request (CSR) to use for the
          renewal. Input should be the CSR as a attachment
        example: cert.pkcs
        in: query
        name: PKCS10 As Attachment
        required: false
        schema: {type: string}
      - description: The action to control a previously disabled certificate.  Default
          is false - do not renew previously disabled certificate
        example: 'true'
        in: query
        name: Reenable
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp11'}
          description: Successful request
      summary: Venafi Renew Certificate
  tmp12:
    post:
      description: Venafi Request Certificate
      parameters:
      - description: Additional information, such as the name and version of the calling
          application, that describes the source of this enrollment, renewal, or provisioning
          request. The default is Web SDK
        example: Swimlane {version}
        in: query
        name: Origin
        required: false
        schema: {type: string}
      - description: The City field for the certificate Subject DN. Specify a value
          when requesting a centrally generated CSR
        example: Kansas City
        in: query
        name: City
        required: false
        schema: {type: string}
      - description: The encryption algorithm for the public key
        example: RSA or ECC
        in: query
        name: Key Algorithm
        required: false
        schema: {type: string}
      - description: The level of management that Trust Protection Platform applies
          to the certificate
        example: Enrollment or Monitoring or Provisioning or Unassigned
        in: query
        name: Management Type
        required: false
        schema: {type: string}
      - description: The Venafi Policy DN
        example: \VED\Policy\TLS/SSL\Administration\CA Templates\MSCA - 1 Year SSL
        in: query
        name: Policy DN
        required: false
        schema: {type: string}
      - description: The setting to control whether manual intervention is required
          for certificate renewal
        example: 'True'
        in: query
        name: Disable Automatic Renewal
        required: false
        schema: {type: boolean}
      - description: The Country field for the certificate Subject DN. Specify a value
          when requesting a centrally generated CSR
        example: United States
        in: query
        name: Country
        required: false
        schema: {type: string}
      - description: Use this parameter when KeyAlgorithm is RSA. The number of bits
          to allow for key generation. The default is 2048
        example: '2048'
        in: body
        name: Key Bit Size
        required: false
        schema: {type: string}
      - description: "The PKCS#10 Certificate Signing Request (CSR). Omit escape characters\
          \ such as \n or \r\n. If this value is provided, any Subject DN fields and\
          \ the KeyBitSize in the request are ignored"
        example: CSR Request Goes here
        in: query
        name: PKCS10
        required: false
        schema: {type: string}
      - description: Either the ObjectName or Subject parameter is required. Both
          parameters can appear in the same request when Subject is the friendly name
          for the Certificate object
        example: swimlane.com
        in: query
        name: Object Name
        required: false
        schema: {type: string}
      - description: The person, entity, or caller of this request. The default is
          Web SDK. Avoid overriding the default unless the caller is a significant
          enterprise application that is tightly integrated with Trust Protection
          Platform, such as a custom web portal
        example: Swimlane
        in: query
        name: Created By
        required: false
        schema: {type: string}
      - description: The certificate type you are requesting
        example: Default X.509 or Code Signing or Device or Server or User
        in: query
        name: Certificate Type
        required: false
        schema: {type: string}
      - description: The State field for the certificate Subject DN. Specify a value
          when requesting a centrally generated CSR
        example: Missouri
        in: query
        name: State
        required: false
        schema: {type: string}
      - description: The department or division within the organization that is responsible
          for maintaining the certificate
        example: Swimlane Integrations
        in: query
        name: Organizational Unit
        required: false
        schema: {type: string}
      - description: The action to control a previously disabled certificate
        example: 'false'
        in: query
        name: Reenable
        required: false
        schema: {type: boolean}
      - description: The Organization field for the certificate Subject DN. Specify
          a value when the CSR centrally generates
        example: Swimlane
        in: query
        name: Organization
        required: false
        schema: {type: string}
      - description: For Elliptic Curve Cryptography (ECC), use this parameter in
          conjunction with KeyAlgorithm
        example: P256 or P384 or P521 - defaults to P256
        in: query
        name: Elliptic Curve
        required: false
        schema: {type: string}
      - description: Certificate Authority Domain Name
        example: \VED\Policy\TLS/SSL\Administration\CA Templates\MSCA - 1 Year SSL
        in: query
        name: CADN
        required: true
        schema: {type: string}
      - description: Subject or name of the certificate
        example: swimlane.com
        in: query
        name: Subject
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp12'}
          description: Successful request
      summary: Venafi Request Certificate
  tmp13:
    post:
      description: Starts rotation of a specified keyset
      parameters:
      - description: The numeric keyset identifier of a set of valid public and private
          key pairs. You can get the KeysetId, from POST SSH/KeyUsage or the Aperture
          inventory for SSH keys
        example: 310E64EF92231B55A34E02A2779E14236CC7A326
        in: query
        name: Keyset ID
        required: true
        schema: {type: string}
      - description: The setting to manage keyset rotation of devices based on connectivity
          to Trust Protection Platform
        example: 'true'
        in: query
        name: Allow Skip On Rotation
        required: true
        schema: {type: boolean}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp13'}
          description: Successful request
      summary: Venafi Rotate SSH Key
  tmp14:
    post:
      description: Returns certificate details of all identified certificates matching
        search filters
      parameters:
      - description: 'Include only certificates by error state: No error 0 or in an
          error state 1'
        example: '1'
        in: body
        name: In Error
        required: false
        schema: {type: string}
      - description: Find certificates with a validation state of Blank, Success,
          or Failure
        example: Blank, Success, or Failure
        in: query
        name: Validation State
        required: false
        schema: {type: string}
      - description: Find certificates by Common name attribute of Subject DN
        example: swimlane.com
        in: query
        name: Common Name
        required: false
        schema: {type: string}
      - description: Find one or more folders for searching for certificates. If you
          want the parent and its subfolders, use ParentDnRecursive instead
        example: \VED\Policy\TLS/SSL\Administration
        in: query
        name: Parent DN
        required: false
        schema: {type: string}
      - description: The number of results to skip
        example: '5'
        in: body
        name: Offset
        required: false
        schema: {type: string}
      - description: Find certificates with a key size that is less than the one you
          specified
        example: '1025'
        in: body
        name: Key Size Less
        required: false
        schema: {type: string}
      - description: Include only certificates that are enabled 0 or disabled 1
        example: '1'
        in: body
        name: Disabled
        required: false
        schema: {type: string}
      - description: Find certificates with a stage less than the specified stage
          (does not include specified stage)
        example: '500'
        in: body
        name: Stage Less
        required: false
        schema: {type: string}
      - description: Find certificates that expire after a certain date. Specify YYYY-MM-DD
          or the ISO 8601 format
        example: '2020-04-01'
        in: query
        name: Valid To Greater
        required: false
        schema: {type: string}
      - description: Find certificates by Serial number
        example: 13279B74000000000053
        in: query
        name: Serial
        required: false
        schema: {type: string}
      - description: Find certificates by Subject Alternate Name (SAN) Distinguished
          Name Server (DNS)
        example: sso.venafi.example
        in: query
        name: SAN-DNS
        required: false
        schema: {type: string}
      - description: Find certificates with a key size that is larger than the one
          you specified
        example: '1024'
        in: body
        name: Key Size Greater
        required: false
        schema: {type: string}
      - description: Find certificates by issuer. Use the CN ,O, L, S, and C values
          from the certificate request
        example: Swimlane
        in: query
        name: Issuer
        required: false
        schema: {type: string}
      - description: Find certificates with a stage greater than the specified stage
          (does not include specified stage)
        example: '799'
        in: body
        name: Stage Greater
        required: false
        schema: {type: string}
      - description: Find certificates that expire before a certain date. Specify
          YYYY-MM-DD or the ISO 8601 format
        example: '2021-01-01'
        in: query
        name: Valid To Less
        required: false
        schema: {type: string}
      - description: Find certificates within a specific folder and its subfolders.
          Accepts a single value
        example: \VED\Policy\TLS/SSL
        in: query
        name: Parent DN Recursive
        required: false
        schema: {type: string}
      - description: Find certificates by SAN Email RFC822
        example: first.last@swimlane.com
        in: query
        name: SAN-Email
        required: false
        schema: {type: string}
      - description: The State field for the certificate Subject DN. Specify a value
          when requesting a centrally generated CSR
        example: Missouri
        in: query
        name: State
        required: false
        schema: {type: string}
      - description: Include only certificates that have a pending workflow resolution,
          for example, an outstanding workflow ticket
        example: None
        in: query
        name: Pending Workflow
        required: false
        schema: {type: string}
      - description: Find certificates using one or more stages in the certificate
          lifecycle.
        example: '499'
        in: body
        name: Stage
        required: false
        schema: {type: string}
      - description: Find certificates that were created at an exact date and time.
          Specify YYYY-MM-DD or the ISO 8601 format
        example: YYYY-MM-DDTHH:MM:SS.mmmmmmmZ
        in: query
        name: Created On
        required: false
        schema: {type: string}
      - description: Include only certificates with network validation enabled 0 or
          disabled 1
        example: '1'
        in: body
        name: Network Validation Disabled
        required: false
        schema: {type: string}
      - description: Find certificates that did not successfully validate using SSL/TLS.
          Specify one or more Certificates SSL and TLS validation states
        example: MismatchWithKnown
        in: query
        name: TLS Validation Failure
        required: false
        schema: {type: string}
      - description: Find certificates by SAN IP Address
        example: 10.20.30.40
        in: query
        name: SAN-IP
        required: false
        schema: {type: string}
      - description: Find certificates by approver
        example: First Last
        in: query
        name: Approver
        required: false
        schema: {type: string}
      - description: Find certificates with a Management type of Unassigned, Monitoring,
          Enrollment, or Provisioning
        example: Unassigned, Monitoring, Enrollment, or Provisioning
        in: query
        name: Management Type
        required: false
        schema: {type: string}
      - description: Find certificates by SAN Uniform Resource Identifier (URI)
        example: swimlane.com
        in: query
        name: SAN-URI
        required: false
        schema: {type: string}
      - description: Find certificates by SAN User Principal Name (UPN) or OtherName
        example: first.last@swimlane.com
        in: query
        name: SAN-UPN
        required: false
        schema: {type: string}
      - description: Include only certificates with validation enabled 0 or disabled
          1
        example: '1'
        in: body
        name: Validation Disabled
        required: false
        schema: {type: string}
      - description: Find certificates by one or more SHA-1 thumbprints
        example: 71E8672798C03842735293EF49425EF06C7FA8AB
        in: query
        name: Thumbprint
        required: false
        schema: {type: string}
      - description: Find certificates by algorithm for the public key
        example: RSA
        in: query
        name: Key Algorithm
        required: false
        schema: {type: string}
      - description: Find certificates by category of usage. Use CodeSigning, Device,
          Server, and/or User
        example: CodeSigning, Device, Server, and/or User
        in: query
        name: Certificate Type
        required: false
        schema: {type: string}
      - description: Find certificates by the date of issue. Specify YYYY-MM-DD or
          the ISO 8601 format
        example: '2020-05-01'
        in: query
        name: Valid From
        required: false
        schema: {type: string}
      - description: Find certificates by the algorithm used to sign the certificate
        example: SHA256RSA
        in: query
        name: Signature Algorithm
        required: false
        schema: {type: string}
      - description: The Organization field for the certificate Subject DN. Specify
          a value when the CSR centrally generates
        example: Swimlane
        in: query
        name: Organization
        required: false
        schema: {type: string}
      - description: Find certificates by date of issue that expire from a certain
          date. Specify YYYY-MM-DD or the ISO 8601 format
        example: '2020-04-01'
        in: query
        name: Valid From Greater
        required: false
        schema: {type: string}
      - description: Find a certificate object
        example: my certificate
        in: query
        name: Name
        required: false
        schema: {type: string}
      - description: Find certificates created after this date and time. Specify YYYY-MM-DD
          or the ISO 8601 format
        example: '2020-04-13'
        in: query
        name: Created On Greater
        required: false
        schema: {type: string}
      - description: Find certificates by date of issue that expire less than a certain
          date. Specify YYYY-MM-DD or the ISO 8601 format
        example: '2021-01-01'
        in: query
        name: Valid From Less
        required: false
        schema: {type: string}
      - description: The Country field for the certificate Subject DN. Specify a value
          when requesting a centrally generated CSR
        example: United States
        in: query
        name: Country
        required: false
        schema: {type: string}
      - description: Find certificates that contain at least one invalid Chain of
          Trust. Specify one or more Certificates Chain Codes
        example: MismatchedChain
        in: query
        name: Chain Validation Failure
        required: false
        schema: {type: string}
      - description: Find certificates by Locality/City attribute of Subject Distinguished
          Name (SDN)
        example: London
        in: query
        name: Locality
        required: false
        schema: {type: string}
      - description: Find certificates based on the Certificate CN or DNS SAN.  Default
          0. 1 Only return certificates that have a CN or DNS SAN that begin with
          *. wild card notation
        example: '1'
        in: body
        name: Is Wild Card
        required: false
        schema: {type: string}
      - description: Include one or more of the following certificate attributes in
          the return value; Issuer, KeyAlgorithm, KeySize, Subject
        example: KeySize
        in: query
        name: Optional Fields
        required: false
        schema: {type: string}
      - description: Find certificates by contact
        example: First Last
        in: query
        name: Contact
        required: false
        schema: {type: string}
      - description: The maximum number of certificates to return. Specify any positive
          integer
        example: '100'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      - description: Find certificates by public key size
        example: '2048'
        in: body
        name: Key Size
        required: false
        schema: {type: string}
      - description: Find certificates based on the signer. Default 0. 1 returns self-signed
          certificates
        example: '1'
        in: body
        name: Is Self Signed
        required: false
        schema: {type: string}
      - description: Find certificates created before this date and time. Specify
          YYYY-MM-DD or the ISO 8601 format
        example: '2020-04-12'
        in: query
        name: Created On Less
        required: false
        schema: {type: string}
      - description: The department or division within the organization that is responsible
          for maintaining the certificate
        example: Swimlane Integrations
        in: query
        name: Organizational Unit
        required: false
        schema: {type: string}
      - description: Find certificates by expiration date. Specify YYYY-MM-DD or the
          ISO 8601 format
        example: '2020-05-01'
        in: query
        name: Valid To
