      - description: An array of GUIDs that identify devices
        example: '{6955f049-19c5-412a-ad99-974fa4a4183e}'
        in: query
        name: Device GUID
        required: false
        schema: {type: string}
      - description: An array of names of users that own the keys
        example: root
        in: query
        name: Username
        required: false
        schema: {type: string}
      - description: An array of key lengths
        example: '1024'
        in: query
        name: Length
        required: false
        schema: {type: string}
      - description: The maximum allowed key length
        example: '2048'
        in: body
        name: Max Key Length
        required: false
        schema: {type: string}
      - description: 'An array of key types: DSA, ECDSA, ED25519, RSA, or RSA1'
        example: ED25519, ECDSA
        in: query
        name: Algorithm
        required: false
        schema: {type: string}
      - description: The result that occurred while processing keys
        example: '5'
        in: query
        name: Process Status
        required: false
        schema: {type: string}
