                scheduler:
                  description: Scheduler type for this traffic shaper.
                  enum:
                    - HFSC
                    - CBQ
                    - FAIRQ
                    - CODELQ
                    - PRIQ
                  type: string
                tbrconfig:
                  description: TBR size for this traffic shaper.
