        "description": "Allows an entity with an accessible API endpoint to subscribe to a set of event topics",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "title": "EventSubscription",
                "type": "object",
                "description": "Describes a subscription, which is composed of:\n\n* A list of event `topics` to subscribe (see list below for meaning)\n* A destination `endpoint` to be notified when that event occurs in the source system\n* A list of hierarchyElements where those events might occur. \n* An option to choose to receive events ocurring only directly at the given hierarchyElements or if those occurring at any children elements as well should be included as well. Default is not to include children events",
                "x-tags": [
                  "api"
                ],
                "properties": {
                  "includeChildrenEvents": {
                    "type": "boolean",
                    "default": "false",
                    "description": "An option to choose to receive events ocurring only directly at the given hierarchyElements (FALSE) or if those occurring at any children elements as well should be included as well (TRUE). FALSE is the default, if the field is left blank"
                  },
                  "hierarchyElements": {
                    "type": "array",
                    "description": "A list of hierarchyElements where events might occur. ",
                    "uniqueItems": true,
                    "minItems": 1,
                    "items": {
                      "type": "object",
                      "title": "Reference",
                      "description": "A reference to an identifiable managed object where the identifier is unique. It is composed of a string identifying the class of the referenced object and a unique string identifier.",
                      "additionalProperties": false,
                      "minProperties": 1,
                      "maxProperties": 1,
                      "x-tags": [
                        "common"
                      ],
                      "properties": {
                        "className": {
                          "type": "string",
                          "description": "The class name of the referenced object in PascalCase",
                          "example": "UKNumberPlate",
                          "minLength": 1
                        },
                        "id": {
                          "type": "string",
                          "description": "The unique identifier within the given class ",
                          "example": "LA51ABC"
                        }
                      },
                      "required": [
                        "className",
                        "id"
                      ]
                    }
                  },
                  "endpoint": {
                    "type": "string",
                    "format": "url",
                    "description": "API endpoint expecting JSON POST messages with payloads corresponding to the type of event",
                    "example": "https://myapi.com/apds-events"
                  },
                  "topics": {
                    "type": "array",
                    "description": "The event topic (must be one of the supported enumeration options)",
                    "uniqueItems": true,
                    "minItems": 1,
                    "items": {
                      "type": "string",
                      "title": "EventTypeEnum",
                      "description": "Describes the possible types of event subscriptions\n\nSupported event types:\n\n- `PlaceCreated`: A child place hierarchy element was created\n- `PlaceUpdated`: A place hierarchy element was updated\n- `PlaceDeleted`: A place hierarchy element was deleted\n- `AssignedRightCreated`: An assigned right was created at any of scoped place hierarchy elements\n- `AssignedRightUpdated`: An assigned right was updated at any of scoped place hierarchy elements\n- `AssignedRightDeleted`: An assigned right was deleted at any of scoped place hierarchy elements\n- `SessionCreated`: A session was created at any of scoped place hierarchy elements\n- `SessionUpdated`: A session was updated at any of scoped place hierarchy elements\n- `SessionDeleted`: A session was deleted at any of scoped place hierarchy elements\n- `ObservationCreated`: An observation was created at any of scoped place hierarchy elements\n- `ObservationUpdated`: An observation was updated at any of scoped place hierarchy elements\n- `ObservationDeleted`: An observation was deleted at any of scoped place hierarchy elements\n- `RightSpecificationCreated`: A right specification was created and associated with any of scoped place hierarchy elements\n- `RightSpecificationUpdated`: A right specification associated with any of scoped place hierarchy elements was updated\n- `RightSpecificationDeleted`: A right specification associated with any of scoped place hierarchy elements was deleted\n- `RateTableUpdated`: A rate table associated with a right specification in turn associated with any of scoped place hierarchy elements was updated\n- `RateTableDeleted`: A rate table associated with a right specification in turn associated with any of scoped place hierarchy elements was updated",
                      "x-tags": [
                        "api"
                      ],
                      "enum": [
                        "PlaceCreated",
                        "PlaceUpdated",
                        "PlaceDeleted",
                        "AssignedRightCreated",
                        "AssignedRightUpdated",
                        "AssignedRightDeleted",
                        "SessionCreated",
                        "SessionUpdated",
                        "SessionDeleted",
                        "ObservationCreated",
                        "ObservationUpdated",
                        "ObservationDeleted",
                        "RightSpecificationCreated",
                        "RightSpecificationUpdated",
                        "RightSpecificationDeleted",
                        "RateTableUpdated",
                        "RateTableDeleted"
                      ]
                    }
                  }
                },
                "required": [
                  "endpoint",
                  "topics"
                ]
              }
            }
          },
          "description": "The event subscription configuration"
        },
        "callbacks": {
          "placeEvent": {
            "{$request.body#/endpoint}": {
              "post": {
                "summary": "The endpoint for webhook subscribers",
                "responses": {
                  "200": {
                    "description": "OK"
                  }
                },
                "description": "This endpoint describes the payloads that webhook subscribers should expect to receive\n\n**IMPORTANT**: This specification is a working **DRAFT**. Please **do not use it as the basis for APDS implementations at this stage**.",
                "requestBody": {
