                                },
                                {
                                  "$ref": "#/paths/~1sessions/post/requestBody/content/application~1json/schema"
                                },
                                {
                                  "$ref": "#/paths/~1rates/post/requestBody/content/application~1json/schema"
                                }
                              ]
                            }
                          },
                          "eventSubscriptionId": {
                            "type": "string",
                            "description": "The unique identifier for the event subscription that triggered this event"
