{
  "openapi": "3.0.3",
  "info": {
    "title": "OpenPix",
    "description": "A OpenPix é uma Plataforma de Gestão de Pagamentos. Para utilizar nossa API você deve acessar  __[https://api.openpix.com.br/](https://api.openpix.com.br/)__ e somente o mesmo. A OpenPix não aceita subdominios para a API.",
    "version": "1.0.0"
  },
  "servers": [
    {
      "url": "https://api.openpix.com.br/",
      "description": "Production server"
    }
  ],
  "security": [
    {
      "AppID": []
    }
  ],
  "paths": {
    "/api/openpix/v1/charge/{id}": {
      "delete": {
        "tags": [
          "charge"
        ],
        "summary": "Delete a charge",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "charge ID or correlation ID. You will need URI encoding if your correlation ID has characters outside the ASCII set or reserved characters (%, \\#, /).",
            "required": true,
            "schema": {
              "type": "string"
            },
            "examples": {
              "chargeId": {
                "value": "Q2hhcmdlOjYwM2U3NDlhNDI1NjAyYmJiZjRlN2JlZA=="
              },
              "correlationID": {
                "value": "fe7834b4060c488a9b0f89811be5f5cf"
              }
            }
          }
        ],
        "responses": {
          "200": {
            "description": "The charge deleted",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "status": {
                      "type": "string"
                    },
                    "id": {
                      "type": "string",
                      "description": "the id previously informed to be found and deleted"
                    }
                  },
                  "example": {
                    "status": "OK",
                    "id": "fe7834b4060c488a9b0f89811be5f5cf"
                  }
                }
              }
            }
          },
          "400": {
            "description": "An error message",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "error": {
                      "type": "string"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "get": {
        "tags": [
          "charge"
        ],
        "summary": "Get one charge",
        "parameters": [
          {
            "name": "id",
            "in": "path",
            "description": "charge ID or correlation ID. You will need URI encoding if your correlation ID has characters outside the ASCII set or reserved characters (%, \\#, /).",
            "required": true,
            "schema": {
              "type": "string"
            },
            "examples": {
              "chargeId": {
                "value": "Q2hhcmdlOjYwM2U3NDlhNDI1NjAyYmJiZjRlN2JlZA=="
              },
              "correlationID": {
                "value": "fe7834b4060c488a9b0f89811be5f5cf"
              }
            }
          }
        ],
        "responses": {
          "200": {
            "description": "The charge retrieve using the given ID",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "charge": {
                      "type": "object",
                      "$ref": "#/components/schemas/Charge"
                    }
                  },
                  "example": {
                    "charge": {
                      "status": "ACTIVE",
                      "customer": {
                        "name": "Dan",
                        "email": "email0@example.com",
                        "phone": "5511999999999",
                        "taxID": {
                          "taxID": "31324227036",
                          "type": "BR:CPF"
                        }
                      },
                      "value": 100,
                      "comment": "good",
                      "correlationID": "9134e286-6f71-427a-bf00-241681624586",
                      "paymentLinkID": "7777-6f71-427a-bf00-241681624586",
                      "paymentLinkUrl": "https://openpix.com.br/pay/9134e286-6f71-427a-bf00-241681624586",
