                          "type": "number"
                        },
                        "limit": {
                          "type": "number"
                        },
                        "totalCount": {
                          "type": "number"
                        },
                        "hasPreviousPage": {
                          "type": "boolean"
                        },
                        "hasNextPage": {
                          "type": "boolean"
                        }
