info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Symantec Endpoint Detection And Response
  title: Symantec Endpoint Detection And Response
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Cancel an isolate, rejoin, or delete file command that is in progress
      parameters:
      - description: ID of Symantec Endpoint Detection and Response Command
        example: d242dd6d-d4ab-4207-9581-d923e56fd121
        in: query
        name: Command ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Symantec Endpoint Detection And Response Cancel A Command
  tmp1:
    post:
      description: Delete all instances of that file on the specified endpoint.
      parameters:
      - description: Device ID of the target computer
        example: cb46d251-151d-4583-a8fb-ebff7c42cfd8
        in: query
        name: Device UID
        required: true
        schema: {type: string}
      - description: SHA256 hash of file to delete
        example: 8692251329fef60490be1c26281710b7e88250fd82b3f679f87d6785db854ed5
        in: query
        name: File Hash
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Symantec Endpoint Detection And Response Command Delete File
  tmp10:
    post:
      description: Issue a Data Recorder Search Action
      parameters:
      - description: The query condition. Refer to Readme under Tasks Setup for additional
          information.
        example: 'file.sha2: 142e1d688ef0568370c37187fd9f2351d7ddeda574f8bfa9b0fa4ef42db85aa2'
        in: query
        name: Query Condition(s)
        required: true
        schema: {type: string}
      - description: List of a Device ID or ID(s) of a endpoint or endpoints(s) to
          trigger a Data Recorder Search on.
        example: cb46d251-151d-4583-a8fb-ebff7c42cfd8
        in: query
        name: Device UID(s)
        required: false
        schema: {type: string}
      - description: List of a SEPM Group or Groups(s) to trigger a Data Recorder
          Search on
        example: My Group
        in: query
        name: SEPM_Group(s)
        required: false
        schema: {type: string}
      - description: List of a ipv4 address or ipv4 addresses to trigger a Data Recorder
          Search on
        example: 192.168.1.1
        in: query
        name: IPV4 Addresses
        required: false
        schema: {type: string}
      - description: List of a Hostname or Hostname(s) of to trigger a Data Recorder
          Search on.
        example: 170915-000042
        in: query
        name: Host Name(s)
        required: false
        schema: {type: string}
      - description: Specifies the beginning of the search time frame. Refer to Readme
          under Tasks Setup for additional information..
        example: '2017-01-01T00:00:00.000Z'
        in: query
        name: Start Time
        required: false
        schema: {type: string}
      - description: Specifies the end of the search time frame. Refer to Readme under
          Tasks Setup for additional information..
        example: '2017-01-08T00:00:00.000Z'
        in: query
        name: End Time
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp10'}
          description: Successful request
      summary: Symantec Endpoint Detection And Response Initiate A Data Recorder Search
  tmp11:
    post:
      description: Query Command State, if command is finished retrieve results.
      parameters:
      - description: ID of Symantec Endpoint Detection and Response Command
        example: d242dd6d-d4ab-4207-9581-d923e56fd121
        in: query
        name: Command ID
        required: true
        schema: {type: string}
      - description: Limit the number of results returned per request. (Default is
          10)
        example: '9'
        in: body
        name: Limit Results
        required: false
        schema: {type: string}
      - description: Specifies the maximum number of statuses to return. Maximum is
          1000. (Default is 50)
        example: '100'
        in: body
        name: Limit
        required: false
        schema: {type: string}
      - description: Specifies the end of the search time frame. Refer to Readme under
          Tasks Setup for additional information.
        example: '2017-01-08T00:00:00.000Z'
        in: query
        name: End Time
        required: false
        schema: {type: string}
      - description: This value determines the length of time in seconds that the
          query results will be cached on the server. Each API HTTP request resets
          the cache countdown to the specified value.
        example: '60'
        in: query
        name: Keep Alive Seconds
        required: false
        schema: {type: boolean}
      - description: Specifies the beginning of the search time frame. Refer to Readme
          under Tasks Setup for additional information.
        example: '2017-01-01T00:00:00.000Z'
        in: query
        name: Start Time
        required: false
        schema: {type: string}
      - description: 'Specifies fields to be returned by the query. There is a performance
          benefit to reducing the returned fields. This is recommended if it works
          for your application. Maximum supported fields: 10'
        example: Text List From a Record.
        in: query
        name: Fields
        required: false
        schema: {type: string}
      - description: Specifies the total number of slices being fetched. To enable
          sliced query request, set 2 <= max_slice < 20.
        example: '10'
        in: body
        name: Max Slice
        required: false
        schema: {type: string}
      - description: Specifies the total number of slices being fetched. To enable
          sliced query request, set 2 <= max_slice < 20.
        example: log_time
        in: query
        name: Slice Field
        required: false
        schema: {type: string}
      - description: The ID of the slice being requested. 0 <= slice_id < max_slice.
        example: '9'
        in: body
        name: Slice ID
        required: false
        schema: {type: string}
      - description: The amount of seconds to wait before checking on the status of
          the command again. (Default is 10 seconds)
        example: '9'
        in: body
        name: Seconds to Wait
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp11'}
          description: Successful request
      summary: Symantec Endpoint Detection And Response Query A Command Status
  tmp2:
    post:
      description: Issue a command to search your endpoints hard drive for EOCs (such
        as files, processes, registry keys, and services)
      parameters:
      - description: The query condition. Refer to Readme under Tasks Setup for additional
          information.
        example: file.md5:d41d8cd98f00b204e9800998ecf8427e
        in: query
        name: Query Condition(s)
        required: true
        schema: {type: string}
      - description: List of a Device ID or ID(s) of a endpoint or endpoints(s) to
          trigger an evidence of compromise search (EOC) on.
        example: cb46d251-151d-4583-a8fb-ebff7c42cfd8
        in: query
        name: Device UID(s)
        required: false
        schema: {type: string}
      - description: List of a SEPM Group or Groups(s) to trigger an evidence of compromise
          search (EOC) on
        example: My Group
        in: query
        name: SEPM_Group(s)
        required: false
        schema: {type: string}
      - description: List of a ipv4 address or ipv4 addresses of to trigger an evidence
          of compromise search (EOC) on.
        example: 192.168.1.1
        in: query
        name: IPV4 Addresses
        required: false
        schema: {type: string}
      - description: List of a Hostname or Hostname(s) of to trigger an evidence of
          compromise search (EOC) on.
        example: 170915-000042
        in: query
        name: Host Name(s)
        required: false
        schema: {type: string}
      - description: Specifies the beginning of the search time frame. Refer to Readme
          under Tasks Setup for additional information.
        example: '2017-01-01T00:00:00.000Z'
        in: query
        name: Start Time
        required: false
        schema: {type: string}
      - description: Specifies the end of the search time frame. Refer to Readme under
          Tasks Setup for additional information.
        example: '2017-01-08T00:00:00.000Z'
        in: query
        name: End Time
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Symantec Endpoint Detection And Response Command Evidence of Compromise
        (EOC) Action
  tmp3:
    post:
      description: Cut off the connections that the endpoint has to internal networks
        and external networks.
      parameters:
      - description: A list of Device ID(s) of a endpoint to isolate.
        example: cb46d251-151d-4583-a8fb-ebff7c42cfd8
        in: query
        name: Target(s)
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Symantec Endpoint Detection And Response Command Isolate Endpoint
  tmp4:
    post:
      description: "Re-establish that endpoint\u2019s connections to internal networks\
        \ and external networks. You can only rejoin endpoints that are isolated"
      parameters:
      - description: A list of Device ID(s) of a endpoint to rejoin.
        example: cb46d251-151d-4583-a8fb-ebff7c42cfd8
        in: query
        name: Target(s)
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp4'}
          description: Successful request
      summary: Symantec Endpoint Detection And Response Command Rejoin Endpoint
  tmp5:
    post:
      description: Get EDR Appliance(s) Information
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp5'}
          description: Successful request
