                "properties": {
                  "links": {
                    "type": "object",
                    "properties": {
                      "related": {
                        "type": "string",
                        "example": "https://api.aleth.io/v1/accounts/0x50126e8fcb9be29f83c6bbd913cc85b40eaf86fc/contractMessages"
