        "required": false,
        "description": "A comma-separated list of protocols to be included in the query.",
        "schema": {
          "allOf": [
            {
              "$ref": "#/components/parameters/AllProtocols"
            },
            {
              "format": "comma-separated-list"
            }
          ]
        }
      },
      "MetricsEarnBorrow": {
        "name": "metrics",
        "in": "query",
        "required": false,
        "description": "A comma-separated list of metrics to be included in the query. 
If this parameter isn't provided, all available metrics will be included.
",
        "schema": {
          "type": "string",
          "format": "comma-separated-list",
          "enum": [
            "earn_apr",
            "borrow_apr"
          ]
        }
      },
      "MetricsStats": {
        "name": "metrics",
        "in": "query",
        "required": false,
        "schema": {
          "type": "string",
          "format": "comma-separated-list",
          "enum": [
            "collateral_ratio",
            "outstanding_debt",
            "supply_volume"
          ]
        }
      },
      "DeFiBefore": {
        "name": "before",
        "description": "Filter data points before a given timestamp.",
        "in": "query",
        "required": false,
        "schema": {
          "type": "integer",
          "format": "unixtime"
        }
      },
      "DeFiAfter": {
        "name": "after",
        "description": "Filter data points after a given timestamp.",
        "in": "query",
        "required": false,
        "schema": {
          "type": "integer",
          "format": "unixtime"
        }
      },
      "Granularity": {
        "name": "granularity",
        "description": "The aggregation window for the timeseries data points, measured in hours.",
        "in": "query",
        "required": false,
        "schema": {
          "type": "integer",
          "default": 24,
          "minimum": 1,
          "maximum": 8760
        }
      },
      "PageLimit": {
        "name": "page[limit]",
        "in": "query",
        "required": false,
        "description": "The maximum number of items to be included in a page.",
        "schema": {
          "type": "number",
          "minimum": 1,
          "maximum": 100,
          "default": 10
        }
      },
      "PageNext": {
        "name": "page[next]",
        "in": "query",
        "required": false,
        "description": "Instructs the server to fetch the next page of items, starting immediately *after* the provided cursor value. Cannot be used together with `page[prev]` in the same request.",
        "schema": {
          "type": "string"
        },
        "example": "016cfb85008d000010008ca21f438ee5"
      },
      "PagePrev": {
        "name": "page[prev]",
        "in": "query",
        "required": false,
        "description": "Instructs the server to fetch the previous page of items, starting immediately *before* the provided cursor value. Cannot be used together with `page[next]` in the same request.",
        "schema": {
          "type": "string"
        },
        "example": "016cfb85008d000010008ca21f438ee5"
      }
    }
