          },
          {
            "$ref": "#/components/parameters/DeFiBefore"
          },
          {
            "$ref": "#/components/parameters/DeFiAfter"
          },
          {
            "$ref": "#/components/parameters/Granularity"
          }
        ],
        "produces": [
          "application/vnd.api+json"
