        "x-beta": true,
        "operationId": "imports-create",
        "tags": ["Imports API"],
        "summary": "Create a import",
        "description": "Enqueues an import - currently only supported for users. Amongst other things, the users import allows associating slack channels (if you have already setup the oauth apps).",
        "parameters": [
          { "$ref": "#/components/parameters/x_magicbell_api_key_header" },
          { "$ref": "#/components/parameters/x_magicbell_api_secret_header" }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": { "$ref": "#/components/schemas/WrappedUsersImport" },
              "example": {
                "import": {
                  "users": [
                    {
                      "external_id": "ugiabqertz",
                      "email": "johndoe@example.com",
                      "first_name": "John",
                      "last_name": "Doe",
                      "custom_attributes": {
                        "age": 32,
                        "country": "Spain"
                      },
                      "channels": {
                        "slack": {
                          "providers": [
                            {
                              "oauth": {
                                "channel_id": "U039446XF3Y",
                                "app": {
                                  "app_id": "your_slack_app_id",
                                  "team_id": "workspace_id_from_slack"
                                }
                              }
                            }
                          ]
                        }
                      }
                    }
                  ]
                }
              }
            }
          }
        },
        "responses": {
          "201": {
            "description": "Created (Enqueues the import)",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "additionalProperties": false,
                  "properties": {
                    "import": {
                      "$ref": "#/components/schemas/CreatedUsersImport"
                    }
                  }
                },
                "example": {
                  "import": {
                    "id": "67e839ab-98a8-48cf-9bcb-12d2b906c9c3",
                    "status": "processing",
                    "summary": {},
                    "errors": []
                  }
                }
              }
            }
          },
          "422": {
            "description": "Invalid data",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "additionalProperties": false,
                  "properties": {
                    "errors": {
                      "type": "array",
                      "description": "List of validation errors"
                    }
                  }
                },
                "example": {
                  "errors": [
                    {
                      "message": "Param 'import.users' must be filled"
                    }
                  ]
                }
              }
            }
          }
        }
      }
    },
    "/imports/{import_id}": {
      "get": {
        "x-beta": true,
        "operationId": "imports-get",
        "tags": ["Imports API"],
        "summary": "Get the status of an import",
        "description": "Query the status of the import for a summary of imported records and failures for each record that could not be imported successfully.",
        "parameters": [
          { "$ref": "#/components/parameters/import_id" },
          { "$ref": "#/components/parameters/x_magicbell_api_key_header" },
          { "$ref": "#/components/parameters/x_magicbell_api_secret_header" }
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/WrappedImport"
                },
                "example": {
                  "import": {
                    "id": "90200969-fcc7-4dee-b62f-928cc3b226d9",
                    "status": "processed",
                    "summary": {
                      "total": 1,
                      "failures": 1
                    },
                    "failures": {
                      "users": [
                        {
                          "email": "invalidEmail",
                          "external_id": null,
                          "errors": {
                            "email": ["must be an email address"],
                            "channels": {
                              "slack": {
                                "providers": {
                                  "0": {
                                    "oauth": {
                                      "app": {
                                        "app_id": ["could not find the slack app"]
                                      }
                                    }
                                  },
                                  "1": {
                                    "oauth": {
                                      "app": {
                                        "app_id": ["could not find the slack app"]
                                      }
                                    }
                                  }
                                }
                              }
                            }
                          }
                        }
                      ]
                    }
                  }
                }
              }
            }
          },
          "404": {
            "description": "The import is not found",
            "content": {
              "application/json": {
                "example": {
                  "errors": [
                    {
                      "message": "An import with the the given id doesn't exist"
                    }
                  ]
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "parameters": {
      "x_magicbell_api_key_header": {
        "in": "header",
        "name": "X-MAGICBELL-API-KEY",
        "schema": { "type": "string" },
        "required": true,
        "description": "The (public) API key of your MagicBell project."
      },
      "x_magicbell_api_secret_header": {
        "in": "header",
        "name": "X-MAGICBELL-API-SECRET",
        "schema": { "type": "string" },
        "required": true,
        "description": "The API secret of your MagicBell project."
      },
      "x_magicbell_user_email_header": {
        "in": "header",
        "name": "X-MAGICBELL-USER-EMAIL",
        "schema": { "type": "string" },
        "description": "Email address of the user. Provide the X-MAGICBELL-USER-EXTERNAL-ID header instead if you identify users by ID."
      },
      "x_magicbell_user_external_id_header": {
        "in": "header",
        "name": "X-MAGICBELL-USER-EXTERNAL-ID",
        "schema": { "type": "string" },
        "description": "ID of the user. Provide the X-MAGICBELL-USER-EMAIL header instead if you identify users by email."
      },
      "x_magicbell_user_hmac_header": {
        "in": "header",
        "name": "X-MAGICBELL-USER-HMAC",
        "schema": { "type": "string" },
        "description": "HMAC calculated with the API secret and ID, or email, of the user. Required if the project has HMAC enabled."
      },
      "authorization_header": {
        "in": "header",
        "name": "Authorization",
        "schema": { "type": "string" },
        "required": true,
        "description": "The Bearer JWT token of your MagicBell user."
      },
      "import_id": {
        "in": "path",
        "name": "import_id",
        "schema": { "type": "string" },
        "required": true,
        "description": "ID of the import.\nThe ID of the import is returned when the import is created."
      },
      "notification_id": {
        "in": "path",
        "name": "notification_id",
        "schema": { "type": "string" },
        "required": true,
        "description": "ID of the user notification.\nThe ID of a user notification can be obtained from the \"Fetch user notifications\" API endpoint or from push events sent to the MagicBell React library.\n"
      },
      "workspace_id": {
        "in": "path",
        "name": "workspace_id",
        "description": "ID of the workspace",
        "required": true,
        "schema": { "type": "string" }
      },
      "project_id": {
        "in": "path",
        "name": "project_id",
        "description": "ID of the project",
        "required": true,
        "schema": { "type": "string" }
      },
      "archived_query_param": {
        "in": "query",
        "name": "archived",
        "schema": { "type": "boolean" },
        "description": "A filter on the notifications based on the archived state. Specify false to select unarchived notifications. Defaults to null."
      },
      "read_query_param": {
        "in": "query",
        "name": "read",
        "schema": { "type": "boolean" },
        "description": "A filter on the notifications based on the read state. Specify false to select unread notifications. Defaults to null."
      },
      "seen_query_param": {
        "in": "query",
        "name": "seen",
        "schema": { "type": "boolean" },
        "description": "A filter on the notifications based on the seen state. Specify false to select unseen notifications. Defaults to null."
      },
      "categories_query_param": {
        "in": "query",
        "name": "categories",
        "schema": {
          "type": "array",
          "items": {
            "type": "string"
          }
        },
        "description": "A filter on the notifications based on the category. If you want to get uncategorized notifications, use the \"uncategorized\" value.\nThe value can be either an array of strings or a comma-separated string."
      },
      "topics_query_param": {
        "in": "query",
        "name": "topics",
        "schema": {
          "type": "array",
          "items": {
            "type": "string"
          }
        },
        "description": "A filter on the notifications based on the topic."
      },
      "topic_subscription_query_param": {
        "in": "query",
        "name": "topic",
        "schema": {
          "type": "string"
        },
        "description": "The topic for which we'd like to filter topic subscriptions."
      },
      "topic": {
        "in": "path",
        "name": "topic",
        "schema": {
          "type": "string"
        },
        "description": "The topic for which we'd like to filter topic subscriptions."
      }
    },
