                  "summary": "Starting a process instance at its default initial activity",
                  "value": {
                    "variables": {
                      "aVariable": {
                        "value": "aStringValue",
                        "type": "String"
                      },
                      "anotherVariable": {
                        "value": true,
                        "type": "Boolean"
                      }
                    },
                    "businessKey": "myBusinessKey"
                  }
                },
                "example-2": {
                  "summary": "Starting a process instance with variables in return",
                  "value": {
                    "variables": {
                      "aVariable": {
                        "value": "aStringValue",
                        "type": "String"
                      },
                      "anotherVariable": {
                        "value": true,
                        "type": "Boolean"
                      }
                    },
                    "businessKey": "myBusinessKey",
                    "withVariablesInReturn": true
                  }
                },
                "example-3": {
