              "schema": {
                "$ref": "#/components/schemas/StartProcessInstanceDto"
              },
              "examples": {
                "example-1": {
                  "summary": "Starting a process instance at its default initial activity",
                  "value": {
                    "variables": {
                      "aVariable": {
                        "value": "aStringValue",
                        "type": "String"
                      },
                      "anotherVariable": {
                        "value": true,
                        "type": "Boolean"
                      }
                    },
                    "businessKey": "myBusinessKey"
                  }
                },
                "example-2": {
                  "summary": "Starting a process instance with variables in return",
                  "value": {
                    "variables": {
                      "aVariable": {
                        "value": "aStringValue",
                        "type": "String"
                      },
                      "anotherVariable": {
                        "value": true,
                        "type": "Boolean"
                      }
                    },
                    "businessKey": "myBusinessKey",
                    "withVariablesInReturn": true
                  }
                },
                "example-3": {
                  "summary": "Starting a process instance at two specific activities",
                  "value": {
                    "variables": {
                      "aProcessVariable": {
                        "value": "aStringValue",
                        "type": "String"
                      }
                    },
                    "businessKey": "myBusinessKey",
