                        "description": "Type of the content being sent",
                        "required": true,
                        "style": "simple",
                        "schema": {
                            "type": "string",
                            "default": "application/json"
                        }
                    }
                ],
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/TestJSONataExpression"
                            },
                            "example": {
                                "Expression": "status = \\\"canceled\\\"",
                                "Document": "{\\\"status\\\":\\\"canceled\\\"}"
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "text/plain": {
                                "examples": {
                                    "response": {
                                        "value": "True"
                                    }
                                }
                            }
                        }
                    }
                },
                "deprecated": false
            }
        },
