{
    "openapi": "3.0.0",
    "info": {
        "title": "Telegram Bot API",
        "description": "Auto-generated OpenAPI schema by TGScraper.",
        "version": "5.6.0"
    },
    "servers": [
        {
            "url": "https://api.telegram.org/bot{token}",
            "variables": {
                "token": {
                    "default": "1234:AAbbcc",
                    "description": "Bot's unique authentication token, given by @BotFather."
                }
            }
        }
    ],
    "externalDocs": {
        "description": "Official Telegram Bot API documentation.",
        "url": "https://core.telegram.org/bots/api"
    },
    "components": {
        "responses": {
            "BadRequest": {
                "description": "Bad request, you have provided malformed data.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "Unauthorized": {
                "description": "The authorization token is invalid or it has been revoked.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "Forbidden": {
                "description": "This action is forbidden.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "NotFound": {
                "description": "The specified resource was not found.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "Conflict": {
                "description": "There is a conflict with another instance using webhook or polling.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "TooManyRequests": {
                "description": "You're doing too many requests, retry after a while.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "ServerError": {
                "description": "The bot API is experiencing some issues, try again later.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            },
            "UnknownError": {
                "description": "An unknown error occurred.",
                "content": {
                    "application/json": {
                        "schema": {
                            "$ref": "#/components/schemas/Error"
                        }
                    }
                }
            }
        },
        "schemas": {
            "Response": {
                "type": "object",
                "description": "Represents the default response object.",
                "required": [
                    "ok"
                ],
                "properties": {
                    "ok": {
                        "type": "boolean"
                    }
                }
            },
            "Success": {
                "description": "Request was successful, the result is returned.",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/Response"
                    },
                    {
                        "type": "object",
                        "required": [
                            "result"
                        ],
                        "properties": {
                            "result": {
                                "type": "object"
                            }
                        }
                    }
                ]
            },
            "Error": {
                "description": "Request was unsuccessful, so an error occurred.",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/Response"
                    },
                    {
                        "type": "object",
                        "required": [
                            "error_code",
                            "description"
                        ],
                        "properties": {
                            "error_code": {
                                "type": "integer"
                            },
                            "description": {
                                "type": "string"
                            },
                            "parameters": {
                                "$ref": "#/components/schemas/ResponseParameters"
                            }
                        }
                    }
                ]
            },
            "Update": {
                "description": "This object represents an incoming update.At most one of the optional parameters can be present in any given update.",
                "required": [
                    "update_id"
                ],
                "properties": {
                    "update_id": {
                        "type": "integer"
                    },
                    "message": {
                        "$ref": "#/components/schemas/Message"
                    },
                    "edited_message": {
                        "$ref": "#/components/schemas/Message"
                    },
                    "channel_post": {
                        "$ref": "#/components/schemas/Message"
                    },
                    "edited_channel_post": {
                        "$ref": "#/components/schemas/Message"
                    },
                    "inline_query": {
                        "$ref": "#/components/schemas/InlineQuery"
                    },
                    "chosen_inline_result": {
                        "$ref": "#/components/schemas/ChosenInlineResult"
                    },
                    "callback_query": {
                        "$ref": "#/components/schemas/CallbackQuery"
                    },
                    "shipping_query": {
                        "$ref": "#/components/schemas/ShippingQuery"
                    },
                    "pre_checkout_query": {
                        "$ref": "#/components/schemas/PreCheckoutQuery"
                    },
                    "poll": {
                        "$ref": "#/components/schemas/Poll"
                    },
                    "poll_answer": {
                        "$ref": "#/components/schemas/PollAnswer"
                    },
                    "my_chat_member": {
                        "$ref": "#/components/schemas/ChatMemberUpdated"
                    },
                    "chat_member": {
                        "$ref": "#/components/schemas/ChatMemberUpdated"
                    },
                    "chat_join_request": {
                        "$ref": "#/components/schemas/ChatJoinRequest"
                    }
                },
                "type": "object"
            },
            "WebhookInfo": {
                "description": "Contains information about the current status of a webhook.",
                "required": [
                    "url",
                    "has_custom_certificate",
                    "pending_update_count"
                ],
                "properties": {
                    "url": {
                        "type": "string"
                    },
                    "has_custom_certificate": {
                        "type": "boolean"
                    },
                    "pending_update_count": {
                        "type": "integer"
                    },
                    "ip_address": {
                        "type": "string"
                    },
                    "last_error_date": {
                        "type": "integer"
                    },
                    "last_error_message": {
                        "type": "string"
                    },
                    "max_connections": {
                        "type": "integer"
                    },
                    "allowed_updates": {
                        "type": "array",
                        "items": {
                            "type": "string"
                        }
                    }
                },
                "type": "object"
            },
            "User": {
                "description": "This object represents a Telegram user or bot.",
                "required": [
                    "id",
                    "is_bot",
                    "first_name"
                ],
                "properties": {
                    "id": {
                        "type": "integer"
                    },
                    "is_bot": {
                        "type": "boolean"
                    },
                    "first_name": {
                        "type": "string"
                    },
                    "last_name": {
                        "type": "string"
                    },
                    "username": {
                        "type": "string"
                    },
                    "language_code": {
                        "type": "string"
                    },
                    "can_join_groups": {
                        "type": "boolean"
                    },
                    "can_read_all_group_messages": {
                        "type": "boolean"
                    },
                    "supports_inline_queries": {
                        "type": "boolean"
                    }
                },
                "type": "object"
            },
            "Chat": {
                "description": "This object represents a chat.",
                "required": [
                    "id",
                    "type"
                ],
                "properties": {
                    "id": {
                        "type": "integer"
                    },
                    "type": {
                        "type": "string"
                    },
                    "title": {
                        "type": "string"
                    },
                    "username": {
                        "type": "string"
                    },
                    "first_name": {
                        "type": "string"
                    },
                    "last_name": {
                        "type": "string"
                    },
                    "photo": {
                        "$ref": "#/components/schemas/ChatPhoto"
                    },
                    "bio": {
                        "type": "string"
                    },
                    "has_private_forwards": {
                        "type": "boolean",
                        "default": true
                    },
                    "description": {
                        "type": "string"
                    },
                    "invite_link": {
                        "type": "string"
                    },
                    "pinned_message": {
                        "$ref": "#/components/schemas/Message"
                    },
                    "permissions": {
                        "$ref": "#/components/schemas/ChatPermissions"
                    },
                    "slow_mode_delay": {
                        "type": "integer"
                    },
                    "message_auto_delete_time": {
                        "type": "integer"
                    },
                    "has_protected_content": {
                        "type": "boolean",
                        "default": true
                    },
                    "sticker_set_name": {
                        "type": "string"
                    },
                    "can_set_sticker_set": {
                        "type": "boolean",
                        "default": true
                    },
                    "linked_chat_id": {
                        "type": "integer"
                    },
                    "location": {
                        "$ref": "#/components/schemas/ChatLocation"
                    }
                },
                "type": "object"
            },
            "Message": {
                "description": "This object represents a message.",
                "required": [
                    "message_id",
                    "date",
                    "chat"
                ],
                "properties": {
                    "message_id": {
                        "type": "integer"
                    },
                    "from": {
                        "$ref": "#/components/schemas/User"
                    },
                    "sender_chat": {
                        "$ref": "#/components/schemas/Chat"
                    },
                    "date": {
                        "type": "integer"
                    },
                    "chat": {
                        "$ref": "#/components/schemas/Chat"
                    },
                    "forward_from": {
                        "$ref": "#/components/schemas/User"
                    },
                    "forward_from_chat": {
                        "$ref": "#/components/schemas/Chat"
                    },
                    "forward_from_message_id": {
                        "type": "integer"
                    },
                    "forward_signature": {
                        "type": "string"
                    },
                    "forward_sender_name": {
                        "type": "string"
                    },
                    "forward_date": {
                        "type": "integer"
                    },
                    "is_automatic_forward": {
                        "type": "boolean",
                        "default": true
                    },
                    "reply_to_message": {
                        "$ref": "#/components/schemas/Message"
                    },
                    "via_bot": {
                        "$ref": "#/components/schemas/User"
                    },
                    "edit_date": {
                        "type": "integer"
                    },
                    "has_protected_content": {
                        "type": "boolean",
                        "default": true
                    },
                    "media_group_id": {
                        "type": "string"
                    },
                    "author_signature": {
                        "type": "string"
                    },
                    "text": {
                        "type": "string"
                    },
                    "entities": {
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/MessageEntity"
                        }
                    },
                    "animation": {
                        "$ref": "#/components/schemas/Animation"
                    },
                    "audio": {
                        "$ref": "#/components/schemas/Audio"
                    },
                    "document": {
                        "$ref": "#/components/schemas/Document"
                    },
                    "photo": {
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/PhotoSize"
                        }
                    },
                    "sticker": {
                        "$ref": "#/components/schemas/Sticker"
                    },
                    "video": {
                        "$ref": "#/components/schemas/Video"
                    },
                    "video_note": {
                        "$ref": "#/components/schemas/VideoNote"
                    },
                    "voice": {
                        "$ref": "#/components/schemas/Voice"
                    },
                    "caption": {
                        "type": "string"
                    },
                    "caption_entities": {
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/MessageEntity"
                        }
                    },
                    "contact": {
                        "$ref": "#/components/schemas/Contact"
                    },
                    "dice": {
                        "$ref": "#/components/schemas/Dice"
                    },
                    "game": {
                        "$ref": "#/components/schemas/Game"
                    },
                    "poll": {
                        "$ref": "#/components/schemas/Poll"
                    },
                    "venue": {
                        "$ref": "#/components/schemas/Venue"
                    },
                    "location": {
                        "$ref": "#/components/schemas/Location"
                    },
                    "new_chat_members": {
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/User"
                        }
                    },
                    "left_chat_member": {
                        "$ref": "#/components/schemas/User"
                    },
                    "new_chat_title": {
                        "type": "string"
                    },
                    "new_chat_photo": {
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/PhotoSize"
                        }
                    },
                    "delete_chat_photo": {
                        "type": "boolean",
                        "default": true
                    },
                    "group_chat_created": {
                        "type": "boolean",
                        "default": true
                    },
                    "supergroup_chat_created": {
                        "type": "boolean",
                        "default": true
                    },
                    "channel_chat_created": {
                        "type": "boolean",
                        "default": true
                    },
                    "message_auto_delete_timer_changed": {
                        "$ref": "#/components/schemas/MessageAutoDeleteTimerChanged"
                    },
                    "migrate_to_chat_id": {
                        "type": "integer"
                    },
                    "migrate_from_chat_id": {
                        "type": "integer"
                    },
                    "pinned_message": {
                        "$ref": "#/components/schemas/Message"
                    },
                    "invoice": {
                        "$ref": "#/components/schemas/Invoice"
                    },
                    "successful_payment": {
                        "$ref": "#/components/schemas/SuccessfulPayment"
                    },
                    "connected_website": {
                        "type": "string"
                    },
                    "passport_data": {
                        "$ref": "#/components/schemas/PassportData"
                    },
                    "proximity_alert_triggered": {
                        "$ref": "#/components/schemas/ProximityAlertTriggered"
                    },
                    "voice_chat_scheduled": {
                        "$ref": "#/components/schemas/VoiceChatScheduled"
                    },
                    "voice_chat_started": {
                        "$ref": "#/components/schemas/VoiceChatStarted"
                    },
                    "voice_chat_ended": {
                        "$ref": "#/components/schemas/VoiceChatEnded"
                    },
                    "voice_chat_participants_invited": {
                        "$ref": "#/components/schemas/VoiceChatParticipantsInvited"
                    },
                    "reply_markup": {
                        "$ref": "#/components/schemas/InlineKeyboardMarkup"
                    }
                },
                "type": "object"
            },
            "MessageId": {
                "description": "This object represents a unique message identifier.",
                "required": [
                    "message_id"
                ],
                "properties": {
                    "message_id": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "MessageEntity": {
                "description": "This object represents one special entity in a text message. For example, hashtags, usernames, URLs, etc.",
                "required": [
                    "type",
                    "offset",
                    "length"
                ],
                "properties": {
                    "type": {
                        "type": "string"
                    },
                    "offset": {
                        "type": "integer"
                    },
                    "length": {
                        "type": "integer"
                    },
                    "url": {
                        "type": "string"
                    },
                    "user": {
                        "$ref": "#/components/schemas/User"
                    },
                    "language": {
                        "type": "string"
                    }
                },
                "type": "object"
            },
            "PhotoSize": {
                "description": "This object represents one size of a photo or a file / sticker thumbnail.",
                "required": [
                    "file_id",
                    "file_unique_id",
                    "width",
                    "height"
                ],
                "properties": {
                    "file_id": {
                        "type": "string"
                    },
                    "file_unique_id": {
                        "type": "string"
                    },
                    "width": {
                        "type": "integer"
                    },
                    "height": {
                        "type": "integer"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Animation": {
                "description": "This object represents an animation file (GIF or H.264/MPEG-4 AVC video without sound).",
                "required": [
                    "file_id",
                    "file_unique_id",
                    "width",
                    "height",
                    "duration"
                ],
                "properties": {
                    "file_id": {
                        "type": "string"
                    },
                    "file_unique_id": {
                        "type": "string"
                    },
                    "width": {
                        "type": "integer"
                    },
                    "height": {
                        "type": "integer"
                    },
                    "duration": {
                        "type": "integer"
                    },
                    "thumb": {
                        "$ref": "#/components/schemas/PhotoSize"
                    },
                    "file_name": {
                        "type": "string"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Audio": {
                "description": "This object represents an audio file to be treated as music by the Telegram clients.",
                "required": [
                    "file_id",
                    "file_unique_id",
                    "duration"
                ],
                "properties": {
                    "file_id": {
                        "type": "string"
                    },
                    "file_unique_id": {
                        "type": "string"
                    },
                    "duration": {
                        "type": "integer"
                    },
                    "performer": {
                        "type": "string"
                    },
                    "title": {
                        "type": "string"
                    },
                    "file_name": {
                        "type": "string"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    },
                    "thumb": {
                        "$ref": "#/components/schemas/PhotoSize"
                    }
                },
                "type": "object"
            },
            "Document": {
                "description": "This object represents a general file (as opposed to photos, voice messages and audio files).",
                "required": [
                    "file_id",
                    "file_unique_id"
                ],
                "properties": {
                    "file_id": {
                        "type": "string"
                    },
                    "file_unique_id": {
                        "type": "string"
                    },
                    "thumb": {
                        "$ref": "#/components/schemas/PhotoSize"
                    },
                    "file_name": {
                        "type": "string"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Video": {
                "description": "This object represents a video file.",
                "required": [
                    "file_id",
                    "file_unique_id",
                    "width",
                    "height",
                    "duration"
                ],
                "properties": {
                    "file_id": {
                        "type": "string"
                    },
                    "file_unique_id": {
                        "type": "string"
                    },
                    "width": {
                        "type": "integer"
                    },
                    "height": {
                        "type": "integer"
                    },
                    "duration": {
                        "type": "integer"
                    },
                    "thumb": {
                        "$ref": "#/components/schemas/PhotoSize"
                    },
                    "file_name": {
                        "type": "string"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "VideoNote": {
                "description": "This object represents a video message (available in Telegram apps as of v.4.0).",
                "required": [
                    "file_id",
                    "file_unique_id",
                    "length",
                    "duration"
                ],
                "properties": {
                    "file_id": {
                        "type": "string"
                    },
                    "file_unique_id": {
                        "type": "string"
                    },
                    "length": {
                        "type": "integer"
                    },
                    "duration": {
                        "type": "integer"
                    },
                    "thumb": {
                        "$ref": "#/components/schemas/PhotoSize"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Voice": {
                "description": "This object represents a voice note.",
                "required": [
                    "file_id",
                    "file_unique_id",
                    "duration"
                ],
                "properties": {
                    "file_id": {
                        "type": "string"
                    },
                    "file_unique_id": {
                        "type": "string"
                    },
                    "duration": {
                        "type": "integer"
                    },
                    "mime_type": {
                        "type": "string"
                    },
                    "file_size": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Contact": {
                "description": "This object represents a phone contact.",
                "required": [
                    "phone_number",
                    "first_name"
                ],
                "properties": {
                    "phone_number": {
                        "type": "string"
                    },
                    "first_name": {
                        "type": "string"
                    },
                    "last_name": {
                        "type": "string"
                    },
                    "user_id": {
                        "type": "integer"
                    },
                    "vcard": {
                        "type": "string"
                    }
                },
                "type": "object"
            },
            "Dice": {
                "description": "This object represents an animated emoji that displays a random value.",
                "required": [
                    "emoji",
                    "value"
                ],
                "properties": {
                    "emoji": {
                        "type": "string"
                    },
                    "value": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "PollOption": {
                "description": "This object contains information about one answer option in a poll.",
                "required": [
                    "text",
                    "voter_count"
                ],
                "properties": {
                    "text": {
                        "type": "string"
                    },
                    "voter_count": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "PollAnswer": {
                "description": "This object represents an answer of a user in a non-anonymous poll.",
                "required": [
                    "poll_id",
                    "user",
                    "option_ids"
                ],
                "properties": {
                    "poll_id": {
                        "type": "string"
                    },
                    "user": {
                        "$ref": "#/components/schemas/User"
                    },
                    "option_ids": {
                        "type": "array",
                        "items": {
                            "type": "integer"
                        }
                    }
                },
                "type": "object"
            },
            "Poll": {
                "description": "This object contains information about a poll.",
                "required": [
                    "id",
                    "question",
                    "options",
                    "total_voter_count",
                    "is_closed",
                    "is_anonymous",
                    "type",
                    "allows_multiple_answers"
                ],
                "properties": {
                    "id": {
                        "type": "string"
                    },
                    "question": {
                        "type": "string"
                    },
                    "options": {
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/PollOption"
                        }
                    },
                    "total_voter_count": {
                        "type": "integer"
                    },
                    "is_closed": {
                        "type": "boolean"
                    },
                    "is_anonymous": {
                        "type": "boolean"
                    },
                    "type": {
                        "type": "string"
                    },
                    "allows_multiple_answers": {
                        "type": "boolean"
                    },
                    "correct_option_id": {
                        "type": "integer"
                    },
                    "explanation": {
                        "type": "string"
                    },
                    "explanation_entities": {
                        "type": "array",
                        "items": {
                            "$ref": "#/components/schemas/MessageEntity"
                        }
                    },
                    "open_period": {
                        "type": "integer"
                    },
                    "close_date": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Location": {
                "description": "This object represents a point on the map.",
                "required": [
                    "longitude",
                    "latitude"
                ],
                "properties": {
                    "longitude": {
                        "type": "number"
                    },
                    "latitude": {
                        "type": "number"
                    },
                    "horizontal_accuracy": {
                        "type": "number"
                    },
                    "live_period": {
                        "type": "integer"
                    },
                    "heading": {
                        "type": "integer"
                    },
                    "proximity_alert_radius": {
                        "type": "integer"
                    }
                },
                "type": "object"
            },
            "Venue": {
                "description": "This object represents a venue.",
                "required": [
                    "location",
                    "title",
                    "address"
                ],
                "properties": {
                    "location": {
                        "$ref": "#/components/schemas/Location"
                    },
                    "title": {
                        "type": "string"
