info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Securonix SNYPR
  title: Securonix SNYPR
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Run an activity query on Securonix
      parameters:
      - description: Query string to query Securonix with. See README for examples
        example: ''
        in: query
        name: Query String
        required: true
        schema: {type: string}
      - description: Limit query data from a date until now, in format 07/22/2019
          23:59:59
        example: ''
        in: query
        name: From Date
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Securonix Run Activity Query
  tmp1:
    post:
      description: Get all Securonix Policies
      parameters:
      - description: Limit the number of policies returned
        example: ''
        in: body
        name: limit
        required: false
        schema: {type: string}
      - description: Filter policies returned with a filter, see README
        example: ''
        in: query
        name: Filter
        required: false
        schema: {type: string}
      - description: String delimiter to split input data on the query string. Defaults
          to ','
        example: ','
        in: query
        name: Pair Delimiter
        required: false
        schema: {type: string}
      - description: Assignment delimiter to split the query string. Defaults to '='
        example: '='
        in: query
        name: Value Delimiter
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Securonix Get All Policies
  tmp2:
    post:
      description: Get all Securonix Users
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Securonix Get All Users
  tmp3:
    post:
      description: Get Securonix Top Violations
      parameters:
      - description: Limit the number of policies returned
        example: ''
        in: body
        name: limit
        required: false
        schema: {type: string}
      - description: Offset from the top to start grabbing from
        example: ''
        in: body
        name: Offset
        required: false
        schema: {type: string}
      - description: Number of days to look back on
        example: ''
        in: body
        name: Number of Days
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Securonix Get Top Violations
  tmp4:
    post:
      description: Get Securonix Top Violators
      parameters:
      - description: Limit the number of policies returned
        example: ''
        in: body
        name: limit
        required: false
        schema: {type: string}
      - description: Offset from the top to start grabbing from
        example: ''
        in: body
        name: Offset
        required: false
        schema: {type: string}
      - description: Number of days to look back on
        example: ''
        in: body
        name: Number of Days
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp4'}
          description: Successful request
      summary: Securonix Get Top Violators
  tmp5:
    post:
      description: Get Violation Events by policy name
      parameters:
      - description: Limit of events to return, defaults to 1000
        example: ''
        in: body
        name: Limit
        required: false
        schema: {type: string}
      - description: Name of policy to check for violation events
        example: ''
        in: query
        name: Policy Name
        required: true
        schema: {type: string}
      - description: Time to filter events generated from
        example: ''
        in: query
        name: From Time
        required: true
        schema: {type: string}
      - description: Time to filter events generated to
        example: ''
        in: query
        name: To Time
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp5'}
          description: Successful request
      summary: Securonix Get Violation Events by Policy Name
  tmp6:
    post:
      description: Get Top Threats from Securonix
      parameters:
      - description: Limit to return
        example: ''
        in: body
        name: Limit
        required: false
        schema: {type: string}
      - description: Offset from beginning to grab from
        example: ''
        in: body
        name: Offset
        required: false
        schema: {type: string}
      - description: Number of days in the past to look through
        example: ''
        in: body
        name: Number of Days
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp6'}
          description: Successful request
      summary: Securonix Get Top Threats
