{
  "components": {
    "schemas": {
      "io.k8s.api.autoscaling.v2beta1.ContainerResourceMetricSource": {
        "description": "ContainerResourceMetricSource indicates how to scale on a resource metric known to Kubernetes, as specified in requests and limits, describing each pod in the current scale target (e.g. CPU or memory).  The values will be averaged together before being compared to the target.  Such metrics are built in to Kubernetes, and have special scaling options on top of those available to normal per-pod metrics using the \"pods\" source.  Only one \"target\" type should be set.",
        "properties": {
          "container": {
            "default": "",
            "description": "container is the name of the container in the pods of the scaling target",
            "type": "string"
          },
          "name": {
            "default": "",
            "description": "name is the name of the resource in question.",
            "type": "string"
          },
          "targetAverageUtilization": {
            "description": "targetAverageUtilization is the target value of the average of the resource metric across all relevant pods, represented as a percentage of the requested value of the resource for the pods.",
            "format": "int32",
            "type": "integer"
          },
          "targetAverageValue": {
            "allOf": [
              {
                "$ref": "#/components/schemas/io.k8s.apimachinery.pkg.api.resource.Quantity"
              }
            ],
            "description": "targetAverageValue is the target value of the average of the resource metric across all relevant pods, as a raw value (instead of as a percentage of the request), similar to the \"pods\" metric source type."
          }
        },
        "required": [
          "name",
          "container"
        ],
        "type": "object"
      },
      "io.k8s.api.autoscaling.v2beta1.ContainerResourceMetricStatus": {
        "description": "ContainerResourceMetricStatus indicates the current value of a resource metric known to Kubernetes, as specified in requests and limits, describing a single container in each pod in the current scale target (e.g. CPU or memory).  Such metrics are built in to Kubernetes, and have special scaling options on top of those available to normal per-pod metrics using the \"pods\" source.",
        "properties": {
          "container": {
            "default": "",
            "description": "container is the name of the container in the pods of the scaling target",
            "type": "string"
          },
          "currentAverageUtilization": {
            "description": "currentAverageUtilization is the current value of the average of the resource metric across all relevant pods, represented as a percentage of the requested value of the resource for the pods.  It will only be present if `targetAverageValue` was set in the corresponding metric specification.",
            "format": "int32",
            "type": "integer"
          },
          "currentAverageValue": {
            "allOf": [
              {
                "$ref": "#/components/schemas/io.k8s.apimachinery.pkg.api.resource.Quantity"
              }
            ],
            "default": {},
            "description": "currentAverageValue is the current value of the average of the resource metric across all relevant pods, as a raw value (instead of as a percentage of the request), similar to the \"pods\" metric source type. It will always be set, regardless of the corresponding metric specification."
          },
          "name": {
            "default": "",
            "description": "name is the name of the resource in question.",
            "type": "string"
          }
        },
        "required": [
          "name",
          "currentAverageValue",
          "container"
        ],
        "type": "object"
      },
      "io.k8s.api.autoscaling.v2beta1.CrossVersionObjectReference": {
        "description": "CrossVersionObjectReference contains enough information to let you identify the referred resource.",
        "properties": {
          "apiVersion": {
            "description": "API version of the referent",
            "type": "string"
          },
          "kind": {
            "default": "",
            "description": "Kind of the referent; More info: https://git.k8s.io/community/contributors/devel/sig-architecture/api-conventions.md#types-kinds\"",
            "type": "string"
          },
          "name": {
            "default": "",
            "description": "Name of the referent; More info: http://kubernetes.io/docs/user-guide/identifiers#names",
            "type": "string"
          }
        },
        "required": [
          "kind",
          "name"
        ],
        "type": "object"
      },
      "io.k8s.api.autoscaling.v2beta1.ExternalMetricSource": {
        "description": "ExternalMetricSource indicates how to scale on a metric not associated with any Kubernetes object (for example length of queue in cloud messaging service, or QPS from loadbalancer running outside of cluster). Exactly one \"target\" type should be set.",
        "properties": {
          "metricName": {
            "default": "",
            "description": "metricName is the name of the metric in question.",
            "type": "string"
          },
          "metricSelector": {
            "allOf": [
              {
                "$ref": "#/components/schemas/io.k8s.apimachinery.pkg.apis.meta.v1.LabelSelector"
              }
            ],
            "description": "metricSelector is used to identify a specific time series within a given metric."
          },
          "targetAverageValue": {
            "allOf": [
              {
                "$ref": "#/components/schemas/io.k8s.apimachinery.pkg.api.resource.Quantity"
              }
            ],
            "description": "targetAverageValue is the target per-pod value of global metric (as a quantity). Mutually exclusive with TargetValue."
          },
          "targetValue": {
            "allOf": [
              {
                "$ref": "#/components/schemas/io.k8s.apimachinery.pkg.api.resource.Quantity"
              }
            ],
            "description": "targetValue is the target value of the metric (as a quantity). Mutually exclusive with TargetAverageValue."
          }
        },
        "required": [
          "metricName"
        ],
        "type": "object"
      },
      "io.k8s.api.autoscaling.v2beta1.ExternalMetricStatus": {
        "description": "ExternalMetricStatus indicates the current value of a global metric not associated with any Kubernetes object.",
        "properties": {
          "currentAverageValue": {
            "allOf": [
              {
                "$ref": "#/components/schemas/io.k8s.apimachinery.pkg.api.resource.Quantity"
              }
            ],
            "description": "currentAverageValue is the current value of metric averaged over autoscaled pods."
          },
          "currentValue": {
            "allOf": [
              {
                "$ref": "#/components/schemas/io.k8s.apimachinery.pkg.api.resource.Quantity"
              }
            ],
            "default": {},
            "description": "currentValue is the current value of the metric (as a quantity)"
          },
          "metricName": {
            "default": "",
            "description": "metricName is the name of a metric used for autoscaling in metric system.",
            "type": "string"
          },
          "metricSelector": {
            "allOf": [
              {
                "$ref": "#/components/schemas/io.k8s.apimachinery.pkg.apis.meta.v1.LabelSelector"
              }
            ],
            "description": "metricSelector is used to identify a specific time series within a given metric."
          }
        },
        "required": [
          "metricName",
          "currentValue"
        ],
        "type": "object"
      },
      "io.k8s.api.autoscaling.v2beta1.HorizontalPodAutoscaler": {
        "description": "HorizontalPodAutoscaler is the configuration for a horizontal pod autoscaler, which automatically manages the replica count of any resource implementing the scale subresource based on the metrics specified.",
        "properties": {
          "apiVersion": {
            "description": "APIVersion defines the versioned schema of this representation of an object. Servers should convert recognized schemas to the latest internal value, and may reject unrecognized values. More info: https://git.k8s.io/community/contributors/devel/sig-architecture/api-conventions.md#resources",
            "type": "string"
