        {
            "url": "https://api.vtex.com/{accountName}/pricing",
            "description": "VTEX server url",
            "variables": {
                "accountName": {
                    "description": "Name of the VTEX account. Used as part of the URL",
                    "default": "{accountName}"
                }
            }
        }
    ],
