        "description": "Update suffix of the user record.",
        "required": false,
        "schema": {
          "type": "string"
        }
      },
      "newUsername": {
        "in": "query",
        "name": "new_username",
        "description": "Update the user name of the user record.",
        "required": false,
        "schema": {
