{
  "openapi": "3.0.0",
  "info": {
    "version": "2018-09-24",
    "x-release": "v4",
    "title": "Amazon Managed Blockchain",
    "description": "Amazon Managed Blockchain is a fully managed service for creating and managing blockchain networks using open source frameworks. Blockchain allows you to build applications where multiple parties can securely and transparently run transactions and share data without the need for a trusted, central authority. Currently, Managed Blockchain supports the Hyperledger Fabric open source framework.",
    "x-logo": {
      "url": "https://raw.githubusercontent.com/chainjet/assets/master/integrations/aws/Arch_Amazon-Managed-Blockchain_64.svg"
    },
    "termsOfService": "https://aws.amazon.com/service-terms/",
    "contact": {
      "name": "Mike Ralphson",
      "email": "mike.ralphson@gmail.com",
      "url": "https://github.com/mermade/aws2openapi",
      "x-twitter": "PermittedSoc"
    },
    "license": {
      "name": "Apache 2.0 License",
      "url": "http://www.apache.org/licenses/"
    },
    "x-providerName": "amazonaws.com",
    "x-serviceName": "managedblockchain",
    "x-origin": [
      {
        "contentType": "application/json",
        "url": "https://raw.githubusercontent.com/aws/aws-sdk-js/master/apis/managedblockchain-2018-09-24.normal.json",
        "converter": {
          "url": "https://github.com/mermade/aws2openapi",
          "version": "1.0.0"
        },
        "x-apisguru-driver": "external"
      }
    ],
    "x-apiClientRegistration": {
      "url": "https://portal.aws.amazon.com/gp/aws/developer/registration/index.html?nc2=h_ct"
    },
    "x-apisguru-categories": ["cloud"],
    "x-preferred": true
  },
  "externalDocs": {
    "description": "Amazon Web Services documentation",
    "url": "https://docs.aws.amazon.com/managedblockchain/"
  },
  "servers": [
    {
      "url": "http://managedblockchain.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The ManagedBlockchain multi-region endpoint"
    },
    {
      "url": "https://managedblockchain.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The ManagedBlockchain multi-region endpoint"
    },
    {
      "url": "http://managedblockchain.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The ManagedBlockchain endpoint for China (Beijing) and China (Ningxia)"
    },
    {
      "url": "https://managedblockchain.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The ManagedBlockchain endpoint for China (Beijing) and China (Ningxia)"
    }
  ],
  "paths": {
    "/networks/{networkId}/members": {
      "post": {
        "operationId": "CreateMember",
        "description": "Creates a member within a Managed Blockchain network.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/CreateMemberOutput" }
              }
            }
          },
          "480": {
            "description": "InvalidRequestException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidRequestException"
                }
              }
            }
          },
          "481": {
            "description": "AccessDeniedException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/AccessDeniedException"
                }
              }
            }
          },
          "482": {
            "description": "ResourceNotFoundException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ResourceNotFoundException"
                }
              }
            }
          },
          "483": {
            "description": "ResourceAlreadyExistsException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ResourceAlreadyExistsException"
                }
              }
            }
          },
          "484": {
            "description": "ResourceNotReadyException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ResourceNotReadyException"
                }
              }
            }
          },
          "485": {
            "description": "ThrottlingException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ThrottlingException" }
              }
            }
          },
          "486": {
            "description": "ResourceLimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ResourceLimitExceededException"
                }
              }
            }
          },
          "487": {
            "description": "InternalServiceErrorException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServiceErrorException"
                }
              }
            }
          },
          "488": {
            "description": "TooManyTagsException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TooManyTagsException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "networkId",
            "in": "path",
            "required": true,
            "description": "The unique identifier of the network in which the member is created.",
            "schema": { "type": "string", "minLength": 1, "maxLength": 32 }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": [
                  "ClientRequestToken",
                  "InvitationId",
                  "MemberConfiguration"
                ],
                "properties": {
                  "ClientRequestToken": {
                    "description": "A unique, case-sensitive identifier that you provide to ensure the idempotency of the operation. An idempotent operation completes no more than one time. This identifier is required only if you make a service request directly using an HTTP client. It is generated automatically if you use an AWS SDK or the AWS CLI.",
                    "type": "string",
                    "minLength": 1,
                    "maxLength": 64
                  },
                  "InvitationId": {
                    "description": "The unique identifier of the invitation that is sent to the member to join the network.",
                    "type": "string",
                    "minLength": 1,
                    "maxLength": 32
                  },
                  "MemberConfiguration": {
                    "description": "Configuration properties of the member.",
                    "type": "object",
                    "properties": {
                      "Name": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/NetworkMemberNameString"
                          },
                          { "description": "The name of the member." }
                        ]
                      },
                      "Description": {
                        "allOf": [
                          { "$ref": "#/components/schemas/DescriptionString" },
                          {
                            "description": "An optional description of the member."
                          }
                        ]
                      },
                      "FrameworkConfiguration": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/MemberFrameworkConfiguration"
                          },
                          {
                            "description": "Configuration properties of the blockchain framework relevant to the member."
                          }
                        ]
                      },
                      "LogPublishingConfiguration": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/MemberLogPublishingConfiguration"
                          },
                          { "description": "<p/>" }
                        ]
                      },
                      "Tags": {
                        "allOf": [
                          { "$ref": "#/components/schemas/InputTagMap" },
                          {
                            "description": "<p>Tags assigned to the member. Tags consist of a key and optional value. For more information about tags, see <a href=\"https://docs.aws.amazon.com/managed-blockchain/latest/hyperledger-fabric-dev/tagging-resources.html\">Tagging Resources</a> in the <i>Amazon Managed Blockchain Hyperledger Fabric Developer Guide</i>.</p> <p>When specifying tags during creation, you can specify multiple key-value pairs in a single request, with an overall maximum of 50 tags added to each resource.</p>"
                          }
                        ]
                      },
                      "KmsKeyArn": {
                        "allOf": [
                          { "$ref": "#/components/schemas/ArnString" },
                          {
                            "description": "<p>The Amazon Resource Name (ARN) of the customer managed key in AWS Key Management Service (AWS KMS) to use for encryption at rest in the member. This parameter is inherited by any nodes that this member creates.</p> <p>Use one of the following options to specify this parameter:</p> <ul> <li> <p> <b>Undefined or empty string</b> - The member uses an AWS owned KMS key for encryption by default.</p> </li> <li> <p> <b>A valid symmetric customer managed KMS key</b> - The member uses the specified key for encryption.</p> <p>Amazon Managed Blockchain doesn't support asymmetric keys. For more information, see <a href=\"https://docs.aws.amazon.com/kms/latest/developerguide/symmetric-asymmetric.html\">Using symmetric and asymmetric keys</a> in the <i>AWS Key Management Service Developer Guide</i>.</p> <p>The following is an example of a KMS key ARN: <code>arn:aws:kms:us-east-2:111122223333:key/1234abcd-12ab-34cd-56ef-1234567890ab</code> </p> </li> </ul>"
                          }
                        ]
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "summary": "Create Member"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "get": {
        "operationId": "ListMembers",
        "description": "Returns a listing of the members in a network and properties of their configurations.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ListMembersOutput" }
              }
            }
          },
          "480": {
            "description": "InvalidRequestException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidRequestException"
                }
              }
            }
          },
          "481": {
            "description": "AccessDeniedException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/AccessDeniedException"
                }
              }
            }
          },
          "482": {
            "description": "ThrottlingException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ThrottlingException" }
              }
            }
          },
          "483": {
            "description": "InternalServiceErrorException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServiceErrorException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "networkId",
            "in": "path",
            "required": true,
            "description": "The unique identifier of the network for which to list members.",
            "schema": { "type": "string", "minLength": 1, "maxLength": 32 }
          },
          {
            "name": "name",
            "in": "query",
            "required": false,
            "description": "The optional name of the member to list.",
            "schema": { "type": "string" }
          },
          {
            "name": "status",
            "in": "query",
            "required": false,
            "description": "An optional status specifier. If provided, only members currently in this status are listed.",
            "schema": {
              "type": "string",
              "enum": [
                "CREATING",
                "AVAILABLE",
                "CREATE_FAILED",
                "UPDATING",
                "DELETING",
                "DELETED",
                "INACCESSIBLE_ENCRYPTION_KEY"
              ]
            }
          },
          {
            "name": "isOwned",
            "in": "query",
            "required": false,
            "description": "An optional Boolean value. If provided, the request is limited either to members that the current AWS account owns (<code>true</code>) or that other AWS accounts own (<code>false</code>). If omitted, all members are listed.",
            "schema": { "type": "boolean" }
          },
          {
            "name": "maxResults",
            "in": "query",
            "required": false,
            "description": "The maximum number of members to return in the request.",
            "schema": { "type": "integer", "minimum": 1, "maximum": 20 }
          },
          {
            "name": "nextToken",
            "in": "query",
            "required": false,
            "description": "The pagination token that indicates the next set of results to retrieve.",
            "schema": { "type": "string", "maxLength": 128 }
          },
          {
            "name": "MaxResults",
            "in": "query",
            "schema": { "type": "string" },
            "description": "Pagination limit",
            "required": false
          },
          {
            "name": "NextToken",
            "in": "query",
            "schema": { "type": "string" },
            "description": "Pagination token",
            "required": false
          }
        ],
        "summary": "List Members"
      }
    },
    "/networks": {
      "post": {
        "operationId": "CreateNetwork",
        "description": "Creates a new blockchain network using Amazon Managed Blockchain.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/CreateNetworkOutput" }
              }
            }
          },
          "480": {
            "description": "InvalidRequestException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidRequestException"
                }
              }
            }
          },
          "481": {
            "description": "AccessDeniedException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/AccessDeniedException"
                }
              }
            }
          },
          "482": {
            "description": "ResourceAlreadyExistsException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ResourceAlreadyExistsException"
                }
              }
            }
          },
          "483": {
            "description": "ThrottlingException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ThrottlingException" }
              }
            }
          },
          "484": {
            "description": "ResourceLimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ResourceLimitExceededException"
                }
              }
            }
          },
          "485": {
            "description": "InternalServiceErrorException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServiceErrorException"
                }
              }
            }
          },
          "486": {
            "description": "TooManyTagsException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TooManyTagsException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": [
                  "ClientRequestToken",
                  "Name",
                  "Framework",
                  "FrameworkVersion",
                  "VotingPolicy",
                  "MemberConfiguration"
                ],
                "properties": {
                  "ClientRequestToken": {
                    "description": "A unique, case-sensitive identifier that you provide to ensure the idempotency of the operation. An idempotent operation completes no more than one time. This identifier is required only if you make a service request directly using an HTTP client. It is generated automatically if you use an AWS SDK or the AWS CLI.",
                    "type": "string",
                    "minLength": 1,
                    "maxLength": 64
                  },
                  "Name": {
                    "description": "The name of the network.",
                    "type": "string",
                    "pattern": ".*\\S.*",
                    "minLength": 1,
                    "maxLength": 64
                  },
                  "Description": {
                    "description": "An optional description for the network.",
                    "type": "string",
                    "maxLength": 128
                  },
                  "Framework": {
                    "description": "The blockchain framework that the network uses.",
                    "type": "string",
                    "enum": ["HYPERLEDGER_FABRIC", "ETHEREUM"]
                  },
                  "FrameworkVersion": {
                    "description": "The version of the blockchain framework that the network uses.",
                    "type": "string",
                    "minLength": 1,
                    "maxLength": 8
                  },
                  "FrameworkConfiguration": {
                    "description": " Configuration properties relevant to the network for the blockchain framework that the network uses. ",
                    "type": "object",
                    "properties": {
                      "Fabric": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/NetworkFabricConfiguration"
                          },
                          {
                            "description": " Hyperledger Fabric configuration properties for a Managed Blockchain network that uses Hyperledger Fabric. "
                          }
                        ]
                      }
                    }
                  },
                  "VotingPolicy": {
                    "description": " The voting rules for the network to decide if a proposal is accepted ",
                    "type": "object",
                    "properties": {
                      "ApprovalThresholdPolicy": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/ApprovalThresholdPolicy"
                          },
                          {
                            "description": "Defines the rules for the network for voting on proposals, such as the percentage of <code>YES</code> votes required for the proposal to be approved and the duration of the proposal. The policy applies to all proposals and is specified when the network is created."
                          }
                        ]
                      }
                    }
                  },
                  "MemberConfiguration": {
                    "description": "Configuration properties of the member.",
                    "type": "object",
                    "properties": {
                      "Name": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/NetworkMemberNameString"
                          },
                          { "description": "The name of the member." }
                        ]
                      },
                      "Description": {
                        "allOf": [
                          { "$ref": "#/components/schemas/DescriptionString" },
                          {
                            "description": "An optional description of the member."
                          }
                        ]
                      },
                      "FrameworkConfiguration": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/MemberFrameworkConfiguration"
                          },
                          {
                            "description": "Configuration properties of the blockchain framework relevant to the member."
                          }
                        ]
                      },
                      "LogPublishingConfiguration": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/MemberLogPublishingConfiguration"
                          },
                          { "description": "<p/>" }
                        ]
                      },
                      "Tags": {
                        "allOf": [
                          { "$ref": "#/components/schemas/InputTagMap" },
                          {
                            "description": "<p>Tags assigned to the member. Tags consist of a key and optional value. For more information about tags, see <a href=\"https://docs.aws.amazon.com/managed-blockchain/latest/hyperledger-fabric-dev/tagging-resources.html\">Tagging Resources</a> in the <i>Amazon Managed Blockchain Hyperledger Fabric Developer Guide</i>.</p> <p>When specifying tags during creation, you can specify multiple key-value pairs in a single request, with an overall maximum of 50 tags added to each resource.</p>"
                          }
                        ]
                      },
                      "KmsKeyArn": {
                        "allOf": [
                          { "$ref": "#/components/schemas/ArnString" },
                          {
                            "description": "<p>The Amazon Resource Name (ARN) of the customer managed key in AWS Key Management Service (AWS KMS) to use for encryption at rest in the member. This parameter is inherited by any nodes that this member creates.</p> <p>Use one of the following options to specify this parameter:</p> <ul> <li> <p> <b>Undefined or empty string</b> - The member uses an AWS owned KMS key for encryption by default.</p> </li> <li> <p> <b>A valid symmetric customer managed KMS key</b> - The member uses the specified key for encryption.</p> <p>Amazon Managed Blockchain doesn't support asymmetric keys. For more information, see <a href=\"https://docs.aws.amazon.com/kms/latest/developerguide/symmetric-asymmetric.html\">Using symmetric and asymmetric keys</a> in the <i>AWS Key Management Service Developer Guide</i>.</p> <p>The following is an example of a KMS key ARN: <code>arn:aws:kms:us-east-2:111122223333:key/1234abcd-12ab-34cd-56ef-1234567890ab</code> </p> </li> </ul>"
                          }
                        ]
                      }
                    }
                  },
                  "Tags": {
                    "description": "<p>Tags to assign to the network. Each tag consists of a key and optional value.</p> <p>When specifying tags during creation, you can specify multiple key-value pairs in a single request, with an overall maximum of 50 tags added to each resource.</p> <p>For more information about tags, see <a href=\"https://docs.aws.amazon.com/managed-blockchain/latest/ethereum-dev/tagging-resources.html\">Tagging Resources</a> in the <i>Amazon Managed Blockchain Ethereum Developer Guide</i>, or <a href=\"https://docs.aws.amazon.com/managed-blockchain/latest/hyperledger-fabric-dev/tagging-resources.html\">Tagging Resources</a> in the <i>Amazon Managed Blockchain Hyperledger Fabric Developer Guide</i>.</p>",
                    "type": "object",
                    "minProperties": 0,
                    "maxProperties": 50,
                    "additionalProperties": {
                      "$ref": "#/components/schemas/TagValue"
                    }
                  }
                }
              }
            }
          }
        },
        "summary": "Create Network"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "get": {
        "operationId": "ListNetworks",
        "description": "Returns information about the networks in which the current AWS account has members.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ListNetworksOutput" }
              }
            }
          },
          "480": {
            "description": "InvalidRequestException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidRequestException"
                }
              }
            }
          },
          "481": {
            "description": "AccessDeniedException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/AccessDeniedException"
                }
              }
            }
          },
          "482": {
            "description": "ThrottlingException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ThrottlingException" }
              }
            }
          },
          "483": {
            "description": "InternalServiceErrorException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServiceErrorException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "name",
            "in": "query",
            "required": false,
            "description": "The name of the network.",
            "schema": { "type": "string" }
          },
          {
            "name": "framework",
