{
"openapi": "3.0.0",
"info": {
"description": "This is an initial API specification for requesting data for performing audits",
"title": "Audit Data Open Api Project (AICPA)",
"version": "1.0.0"
},
"servers": [
{
"url": "/AuditDataOpenAPI/v1.0"
}
],
"security": [
{
"basicAuth": []
},
{
"implicitAuth": [
"read"
]
},
{
"bearerAuth": []
},
{
"apiKeyAuth": [
"read"
]
},
{
"openIdAuth": [
"read"
]
}
],
"tags": [
{
"description": "Used across modules.",
"name": "Entities"
},
{
"description": "Used to request General Ledger Information",
"name": "General Ledger"
},
{
"description": "Used to request Accounts Receivable Information",
"name": "Accounts Receivable"
},
{
"description": "Used to request Accounts Payable Information",
"name": "Accounts Payable"
},
{
"description": "Used to request Inventory Information",
"name": "Inventory"
}
],
"paths": {
"/Entities": {
"get": {
"description": "Allows requesting the reporting entities available from the API.\n",
"operationId": "GetEntities",
"parameters": [
{
"description": "Entity Identifier.  UID of the entity. Can be defined by the implementer of the API does not imply that a standard identifier is used. If no entity id identifier is provided and the requested system has multiple entities the API implementer determines entity to return.",
"in": "query",
"name": "entityID",
"required": false,
"schema": {
"type": "string"
}
}
],
"responses": {
"200": {
"content": {
"application/json": {
"schema": {
"$ref": "#/components/schemas/Entities"
}
}
},
"description": "_entities_ successfully accepted"
},
"400": {
"content": {
"applications/json": {
"example": {
"code": "BadRequest",
"ErrorDescription": "Parameter field invalid."
},
"schema": {
"$ref": "#/components/schemas/ErrorResult"
}
}
},
"description": "Malformed request or missing fields. Check that Content-Type matches the format/encoding, and that all required fields are spelled correctly, have correct types, and valid values. \n"
}
},
"tags": [
"Entities"
]
}
},
"/Entities/{entityId}/Periods": {
"get": {
"description": "Allows requesting the reporting periods of the company. If no parameters are passed all the available entities available to the user in the system are returned.  The end point also allows the user to pass an entity identifier to return the name of the entity.\n",
"operationId": "GetPeriods",
"parameters": [
{
"description": "Returns all reporting periods with a start date equal or greater than the start date.",
"in": "query",
"name": "startDate",
"required": false,
"schema": {
"format": "date",
"type": "string"
}
},
{
"description": "Returns all reporting periods with an end date equal or less than the end date.",
"in": "query",
"name": "endDate",
"required": false,
"schema": {
"format": "date",
"type": "string"
}
},
{
"description": "Entity Identifier.  UID of the entity. Can be defined by the implementer of the API does not imply that a standard identifier is used. If no entity id identifier is provided and the requested system has multiple entities the API implementer determines entity to return.",
"in": "path",
"name": "entityId",
"required": true,
"schema": {
"type": "string"
}
}
],
"responses": {
"200": {
"content": {
"application/json": {
"schema": {
"$ref": "#/components/schemas/Periods"
}
}
},
"description": "_periods_ successfully accepted"
},
"400": {
"content": {
"applications/json": {
"example": {
"code": "BadRequest",
"ErrorDescription": "Parameter field invalid."
},
"schema": {
"$ref": "#/components/schemas/ErrorResult"
}
}
},
"description": "Malformed request or missing fields. Check that Content-Type matches the format/encoding, and that all required fields are spelled correctly, have correct types, and valid values. \n"
}
},
"tags": [
"Entities"
]
}
},
