                        "$ref": "#/components/schemas/MimeHeaderValue",
                        "description": "MIME [RFC4021]"
                    },
                    "Content-Disposition": {
                        "$ref": "#/components/schemas/MimeHeaderValue",
                        "description": "MIME [RFC4021]"
                    },
                    "Content-Duration": {
                        "$ref": "#/components/schemas/MimeHeaderValue",
                        "description": "MIME [RFC4021]"
                    },
                    "Content-ID": {
                        "$ref": "#/components/schemas/MimeHeaderValue",
                        "description": "MIME [RFC4021]"
                    },
                    "Content-Language": {
                        "$ref": "#/components/schemas/MimeHeaderValue",
                        "description": "MIME [RFC4021]"
                    },
                    "Content-Location": {
                        "$ref": "#/components/schemas/MimeHeaderValue",
                        "description": "MIME [RFC4021]"
                    },
